import React from "react";
import Auxiliary from "../../../../util/Auxiliary";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import { connect } from "react-redux";
import IntlMessages from "../../../../util/IntlMessages";
import CardBox from "../../../../components/CardBox/index";
import MenuItem from "@material-ui/core/MenuItem";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import baseUrl from "../../../../config/config";
import axios from "axios";
import Radio from "@material-ui/core/Radio";

class ShowQuestion extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      QuestionItems: [],
      level: "",
      subject: "",
    };
  }

  componentWillMount() {
    var level = this.props.levels.filter(
      (element) => element.id == this.props.questionItem.level_id
    );
    var subject = this.props.profSubjectList.filter(
      (element) => element.id == this.props.questionItem.subject_id
    );
    this.setState({
      level: level[0],
      subject: subject[0],
    });
  }

  render() {
    console.log("this.props.listChoices ", this.props.listChoices);
    return (
      <Auxiliary>
        <Modal isOpen={this.props.openShowQuestion}>
          <ModalHeader
            toggle={this.props.cancelModal}
            className="modal-box-header bg-primary text-white"
          >
            {this.props.questionItem.question_subject}
          </ModalHeader>
          <br />
          <ModalBody>
            <form autoComplete="off">
              <div className="row">
                <CardBox
                  heading={
                    <IntlMessages id="component.etablishments.info.general" />
                  }
                  styleName="col-lg-12 text-primary"
                >
                  <div className="row">
                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          name="niveau"
                          id="niveau"
                          label={<IntlMessages id="components.note.niveau" />}
                          value={this.state.level.name}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          name="subject_id"
                          id="subject_id"
                          label={<IntlMessages id="components.note.subject" />}
                          value={
                            this.props.settings.languageId == "tunisia"
                              ? this.state.subject.name_AR
                              : this.props.settings.languageId == "french"
                              ? this.state.subject.name_FR
                              : this.state.subject.name_EN
                          }
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>

                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          name="topic"
                          id="topic"
                          label={
                            <IntlMessages id="level.test.question.topic" />
                          }
                          value={this.props.questionItem.question_subject}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>
                  </div>
                </CardBox>

                <CardBox
                  heading={<IntlMessages id="level.test.question.content" />}
                  styleName="col-lg-12 text-primary"
                >
                  <div className="row">
                    <div className="col-md-9">
                      <div className="form-group">
                        <TextField
                          required
                          name="question_description"
                          id="question_description"
                          label={<IntlMessages id="Add.level.test.question" />}
                          value={this.props.questionItem.question_desc}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>

                    <div className="d-flex flex-column col-md-12">
                      {this.props.listChoices.map((element, i) => {
                        return (
                          <div key={i}>
                            <RadioGroup
                              aria-label="anchorOriginVertical"
                              name="anchorOriginVertical"
                            >
                              <div className="d-flex flex-row  col-md-12">
                                <FormControlLabel
                                  control={
                                    <Radio
                                      required
                                      checked={element.propasal_status == true}
                                    />
                                  }
                                  style={{
                                    marginTop: "4%",
                                  }}
                                />
                                <div className="col-md-9">
                                  <TextField
                                    required
                                    name="suite"
                                    id="suite"
                                    value={element.proposal}
                                    margin="normal"
                                    fullWidth
                                  />
                                </div>
                                <div
                                  className="col-md-1"
                                  style={{
                                    marginTop: "4%",
                                  }}
                                ></div>
                              </div>
                            </RadioGroup>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </CardBox>
              </div>

              <div className="col-md-12 text-left ">
                <br />
                <br />
                <Button
                  variant="contained"
                  className="jr-btn bg-indigo text-white "
                  onClick={this.props.modify}
                >
                  {<IntlMessages id="button.modify" />}
                </Button>
                <Button
                  variant="contained"
                  className="jr-btn bg-grey text-white "
                  onClick={this.props.cancelModal}
                >
                  {<IntlMessages id="level.test.Close" />}
                </Button>
              </div>
            </form>
          </ModalBody>
        </Modal>
      </Auxiliary>
    );
  }
}

function mapStateToProps(state) {
  return {
    userProfile: state.auth.userProfile,
    settings: state.settings.locale,
  };
}

export default connect(
  mapStateToProps,
  {}
)(ShowQuestion);
