const roleRbac = {
  Admin: 'add-admin',
  Professor: 'add-prof',
  Parent: 'add-parent',
  Student: 'add-student',
  'Vie scolaire': 'add-school-life',
  Director: 'add-direction-membre',
};
module.exports = {
  roleIdAdmin: 2,
  roleIdProfessor: 3,
  isEmail(value) {
    if (value.length > 0)
      return /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@(([[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
        value
      );
    else return true;
  },

  isPhonenumber(str) {
    if (str.length > 1) return /^[2-9]\d{7}$/.test(str);
    else return true;
  },
  isZipCode(zipCode) {
    if (zipCode.length > 0) return /^[1-9]\d{3}$/.test(zipCode);
    else return true;
  },

  isCIN(cin) {
    if (cin.length > 0) return /^[0-9]\d{7}$/.test(cin);
    else return true;
  },
  isNotEmpty(value) {
    if (value.length > 0) return true;
    else return false;
  },
  rolePermitted(roleLabel, userPermission) {
    let permission = false;
    // console.log(roleLabel, 'rooooooole');
    // console.log(roleRbac[roleLabel], 'roleRbac');
    if (userPermission != undefined) {
      permission =
        userPermission.findIndex((element) => element.permission.rbac == roleRbac[roleLabel]) > -1;
    }

    return permission;
  },
};
