import React, { Component } from 'react';
import IconButton from '@material-ui/core/IconButton';
import axios from 'axios';
import IntlMessages from '../../../../../util/IntlMessages';
import baseUrl from '../../../../../config/config';
import _ from 'lodash';
import { connect } from 'react-redux';
import moment from 'moment';
import Button from '@material-ui/core/Button';
import Switch from '@material-ui/core/Switch';
import { roleIdSuperAdmin } from '../../../../../config/config';
import { roleIdAdmin } from '../../../../../config/config';
import { roleIdProfessor } from '../../../../../config/config';
import { roleIdDirector } from '../../../../../config/config';
import { Col } from 'reactstrap';
import { Link, withRouter, NavLink, Route } from 'react-router-dom';
import Can from '../../../../../can';
import { RoleContext } from '../../../../../Context';

class HomeworkItem extends Component {
  render() {
    // const { anchorEl, menuState } = this.state;

    const statusButtonStyle = '#3F51B5';

    return (
      <div className="card package bg-white shadow">
        <div
          className="package-header  lighten-1 text-white"
          style={{ backgroundColor: 'green', height: '150px' }}
        >
          <h3 className="letter-spacing-base text-uppercase" style={{ margin: '0 auto' }}>
            {' '}
            <strong>exercice maths</strong>
          </h3>
        </div>

        <div class="d-flex flex-wrap flex-column bd-highlight ">
          <div class="p-1 bd-highlight">
            <div class="d-flex flex-wrap flex-row bd-highlight justify-content-between">
              <div class="p-1 bd-highlight">
                <div class="d-flex flex-wrap flex-column bd-highlight " style={{ color: 'green' }}>
                  <div class=" bd-highlight">
                    <strong>Maths</strong>
                  </div>
                  <div class=" bd-highlight">7 ème 2</div>
                </div>
              </div>
              <div class="p-1 bd-highlight">
                <div class="d-flex flex-wrap flex-column bd-highlight ">
                  <div class=" bd-highlight">
                    <Button
                      style={{
                        backgroundColor: 'green',
                        color: 'white',
                        width: '80px',
                        height: '20px',
                      }}
                      target="_blank"
                    >
                      <span style={{ fontSize: '12px' }}>Programmé</span>
                    </Button>
                  </div>
                  <div class=" bd-highlight">23/01/2021</div>
                </div>
              </div>
            </div>
          </div>
          <div class="p-1 bd-highlight">
            <div class="d-flex flex-wrap flex-row bd-highlight justify-content-between  ">
              <div class="pr-2 bd-highlight">Sciences</div>
            </div>
          </div>
          <div class="p-1 bd-highlight">
            <div class="d-flex flex-wrap flex-column bd-highlight ">
              <div class=" bd-highlight text-primary">Les intervalles</div>
              <div class=" bd-highlight text-primary" style={{ fontSize: '10px' }}>
                Voir les détails ...
              </div>
            </div>
          </div>
          <div>


          {!this.props.archived ? (
                <div class="d-flex flex-wrap flex-row  justify-content-around ">
                <div>
                  <Button
                    style={{
                      backgroundColor: 'white',
                      color: '#7C7C7C',
                    }}
                  >
                    <span style={{ fontSize: '9px', color: '#7C7C7C' }}>
                      {/* <IntlMessages id="button.modify" /> */}
                      Télécharger |
                    </span>
                  </Button>
                </div>
                <div>
                  <Button
                    style={{
                      backgroundColor: 'white',
                      color: '#7C7C7C',
                      width: '30px',
                    }}
                  >
                    <span style={{ fontSize: '9px', color: '#7C7C7C' }}>
                      <IntlMessages id="button.modify" /> |
                    </span>
                  </Button>
                </div>

                <div
                  className=" "
                  style={{
                    width: '18px',
                    height: '18px',
                  }}
                >
                  <IconButton size="small" className="icon-btn">
                    <i className="zmdi zmdi-eye" style={{ color: '#A3A3A3' }} />
                  </IconButton>
                </div>

                <div
                  className=" "
                  style={{
                    width: '18px',
                    height: '18px',
                  }}
                >
                  <IconButton size="small" className="icon-btn">
                    <i
                      className="zmdi zmdi-delete"
                      style={{ color: '#A3A3A3' }}
                      onClick={(e) => {
                        this.props.handleDelete(3);
                      }}
                    />
                  </IconButton>
                </div>
              </div>
              ) : (
                ""
              )}


          </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    userProfile: state.auth.userProfile,
  };
}

export default connect(mapStateToProps)(HomeworkItem);
