import React from "react";
import Classlist from "./ClassList.jsx";
import AddClasse from "./AddClass";
import {
  getClasses,
  getClassesByEstablishmentId
} from "../../../../../actions/classeAction";
import { connect } from "react-redux";
import { getEstablishment } from "../../../../../actions/establishmentAction";
import { getData } from "../../../../../actions/professorAction";
import { getSections } from "../../../../../actions/sectionAction";
import { getLevels } from "../../../../../actions/classLevelAction";
import { getStudents } from "../../../../../actions/studentAction";
import { getAllUsers } from "../../../../../actions/stuppUserAction";
import { getSubject } from "../../../../../actions/subjectAction";
import { getProfiles } from "../../../../../actions/profileAction";
import { getAllUsersByEstablishmentId } from "../../../../../actions/stuppUserAction";

import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import CardBox from "../../../../../components/CardBox/index";
import Box from "@material-ui/core/Box";

import Input from "@material-ui/icons/Input";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import ContainerHeader from "../../../../../components/ContainerHeader/index";
import IntlMessages from "../../../../../util/IntlMessages";
import { UncontrolledAlert } from "reactstrap";
import { getCountriesByEstablishmentId } from "../../../../../actions/countriesAction";
const roleIdAdmin = 2;
class Classe extends React.Component {
  constructor() {
    super();
    this.state = {
      modal: false,
      classItem: "",
      classes: [],
      roleIdSuperAdmin: 1,
      addClassModal: false,
    };
    this.cancelModal = this.cancelModal.bind(this);
    this.editCLass = this.editCLass.bind(this);
    this.addClass = this.addClass.bind(this);
  }
  addClass() {
    this.setState({ addClassModal: true });
  }

  componentDidMount() {
    const roleId = this.props.userProfile.role_id;
    const establishmentId = this.props.userProfile.establishment_id;
    this.props.getCountriesByEstablishmentId(establishmentId);
    if (roleId === roleIdAdmin || roleId ===7) {
      this.props.getClassesByEstablishmentId(this.props.userProfile.establishment_id);
      this.props.getEstablishment();
      this.props.getData();
      this.props.getSections();
      this.props.getLevels();
      this.props.getStudents();
      this.props.getAllUsersByEstablishmentId(
        this.props.userProfile.establishment_id
      );
      this.props.getSubject();
      this.props.getProfiles();
    } else {
      this.props.getClasses();
      this.props.getEstablishment();
      this.props.getData();
      this.props.getSections();
      this.props.getLevels();
      this.props.getStudents();
      this.props.getAllUsers();
      this.props.getSubject();
      this.props.getProfiles();
    }
  }

  cancelModal() {
    this.setState({ modal: false, addClassModal: false });
  }

  editCLass(element) {
    this.setState({
      modal: true,
      classItem: element
    });
  }

  render() {
    return (
      <div className="app-wrapper">
        <ContainerHeader
          match={this.props.match}
          title={<IntlMessages id="sidebar.classes" />}
        />
        {this.props.successStatus  ? (
          <UncontrolledAlert className="alert-addon-card bg-success bg-success text-white shadow-lg">
            <span className="icon-addon alert-addon">
              <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
            </span>
            <span className="d-inline-block">
              {" "}
              {this.props.message}{" "}
            </span>
          </UncontrolledAlert>
        ) : (
            ""
          )}
        {this.props.errorStatus ? (
          <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
            <span className="icon-addon alert-addon">
              <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
            </span>
            <span className="d-inline-block">
              {" "}{this.props.message}{" "}
            </span>
          </UncontrolledAlert>
        ) : (
            ""
          )}
        <div className="col-md-12 text-right " style={{ marginBottom: 20 }}>
          <Fab
            size="small"
            color="primary"
            aria-label="Add"
            onClick={this.addClass}
          >
            <AddIcon />
          </Fab>
          &nbsp;&nbsp;&nbsp;
          <Fab size="small" color="primary">
            <Input />
          </Fab>
        </div>
        {this.state.addClassModal ? (
          <AddClasse
            students={this.props.students}
            classSections={this.props.classSections}
            classLevels={this.props.classLevels}
            establishments={this.props.establishments}
            professors={this.props.professors}
            classes={this.props.classes}
            subjects={this.props.subjects}
            cancelModal={this.cancelModal}
            stuppUsers={this.props.stuppUsers}
            profiles={this.props.profiles}
            userProfile={this.props.userProfile}
            country={this.props.country}
          />
        ) : (
            ""
          )}
        {this.props.classes.length !== 0 &&
          this.props.userProfile.role_id !== undefined ? (
            <Classlist
              students={this.props.students}
              classSections={this.props.classSections}
              classLevels={this.props.classLevels}
              editCLass={this.editCLass}
              classes={this.props.classes}
              profiles={this.props.profiles}
              subjects={this.props.subjects}
              professors={this.props.professors}
              establishments={this.props.establishments}
              stuppUsers={this.props.stuppUsers}
              userProfile={this.props.userProfile}
              country={this.props.country}
              language={this.props.language.locale}
            />
          ) : (
            <CardBox styleName="col-lg-12">
              <div className="table-responsive-material bg-dark d-flex justify-content-center">
                <Table className="default-table table-unbordered table table-sm table-hover">
                  <TableHead className="th-border-b">
                    <TableRow>
                      {this.props.userProfile.role_id ===
                        this.state.roleIdSuperAdmin ? (
                          <TableCell>
                            {
                              <IntlMessages id="components.student.formadd.establishment" />
                            }
                          </TableCell>
                        ) : (
                          ""
                        )}
                      <TableCell>{<IntlMessages id="room.name" />}</TableCell>
                      <TableCell align="right">
                        {<IntlMessages id="room.description" />}
                      </TableCell>
                      <TableCell></TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {/* <Box
                    component="div"
                    mt={5}
                    textOverflow="clip"
                    overflow="hidden"
                    bgcolor="background.paper"
                  > */}

                    {/* </Box> */}
                  </TableBody>
                </Table>
              </div>
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-around",
                  marginTop: "25px"
                }}
              >
                <b>
                  {" "}
                  <IntlMessages id="class.empty" />
                </b>
              </div>
            </CardBox>
          )}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    classes: state.classes,
    professors: state.professor.remoteProfessors,
    establishments: state.establishment.remoteEstablishments,
    classLevels: state.ClassLevels.remoteLevels,
    classSections: state.classSections.remoteSections,
    students: state.student.remoteStudents,
    stuppUsers: state.stuppUsers.remoteStuppUsers,
    subjects: state.subject.remoteSubjects,
    profiles: state.profiles.remoteProfiles,
    userProfile: state.auth.userProfile,
    successStatus: state.alert.success,
    errorStatus: state.alert.error,
    message: state.alert.message,
    country: state.countries.remoteCountries,
    language: state.settings.locale,
  };
};

export default connect(
  mapStateToProps,
  {
    getAllUsersByEstablishmentId,
    getProfiles,
    getClassesByEstablishmentId,
    getSubject,
    getAllUsers,
    getLevels,
    getSections,
    getClasses,
    getEstablishment,
    getStudents,
    getData,
    getCountriesByEstablishmentId
  }
)(Classe);
