import React, { Component } from "react";
import IconButton from "@material-ui/core/IconButton";
import axios from "axios";
import IntlMessages from "../../../../../util/IntlMessages";
import baseUrl from "../../../../../config/config";
import _ from "lodash";
import { connect } from "react-redux";
import moment from "moment";
import Button from "@material-ui/core/Button";
import Switch from "@material-ui/core/Switch";
import { roleIdSuperAdmin } from "../../../../../config/config";
import { roleIdAdmin } from "../../../../../config/config";
import { roleIdProfessor } from "../../../../../config/config";
import { roleIdDirector } from "../../../../../config/config";
import { Col } from "reactstrap";
import { Link, withRouter, NavLink, Route } from "react-router-dom";
import Can from "../../../../../can";
import { RoleContext } from "../../../../../Context";

class HomeworkItem extends Component {
  render() {
    const { homeworkItem } = this.props;
    let sys, start, end;
    if (homeworkItem.homework.correction_date !== null) {
      sys = Date.parse(new Date()) / 60000;
      start =
        Date.parse(
          homeworkItem.homework.publication_date.slice(0, 10) +
            " " +
            moment(homeworkItem.homework.publication_time).format("HH:mm")
        ) / 60000;
      end =
        Date.parse(
          homeworkItem.homework.correction_date.slice(0, 10) +
            " " +
            moment(homeworkItem.homework.correction_date).format("HH:mm")
        ) / 60000;
    }

    return (
      <div className="card package bg-white shadow">
        <div
          className="package-header  lighten-1 text-white"
          style={{
            backgroundColor: `${homeworkItem.homework.subject.color}`,
            height: "150px",
          }}
        >
          <h3
            className="letter-spacing-base text-uppercase"
            style={{ margin: "0 auto" }}
          >
            {" "}
            <strong>
              {homeworkItem.homework.homework_type === "serie" ? (
                <IntlMessages id="toDo.series.exercise" />
              ) : homeworkItem.homework.homework_type === "book" ? (
                <IntlMessages id="toDo.book.exercise" />
              ) : (
                <IntlMessages id="toDo.other.exercise" />
              )}
            </strong>
          </h3>
        </div>

        <div class="d-flex flex-wrap flex-column bd-highlight ">
          <div class="p-1 bd-highlight">
            <div class="d-flex flex-wrap flex-row bd-highlight justify-content-between">
              <div class="p-1 bd-highlight">
                <div
                  class="d-flex flex-wrap flex-column bd-highlight "
                  style={{ color: "green" }}
                >
                  <div class=" bd-highlight">
                    <strong>{homeworkItem.homework.subject.name}</strong>
                  </div>
                  <div class=" bd-highlight"> {homeworkItem.className}</div>
                </div>
              </div>
              <div class="p-1 bd-highlight">
                <div class="d-flex flex-wrap flex-column bd-highlight ">
                  <div class=" bd-highlight">
                    {/* <Button
                      style={{
                        backgroundColor: "green",
                        color: "white",
                        width: "80px",
                        height: "20px",
                      }}
                      target="_blank"
                    >
                      <span style={{ fontSize: "12px" }}>Programmé</span>
                    </Button> */}

                    <div
                      className={` badge  ${
                        start > sys
                          ? "bg-info"
                          : end > sys
                          ? "text-white bg-amber" 
                          : "text-white bg-success"
                      }`}
                      style={{ height: "20px", width: "80px", float: "left" }}
                    >
                      {start > sys ? (
                        <IntlMessages id="status.classe.virtual.programmé" />
                      ) : end > sys ? (
                        <IntlMessages id="status.classe.virtual.progrés" />
                      ) : (
                        <IntlMessages id="status.classe.virtual.términé" />
                      )}
                    </div>
                  </div>
                  <div class=" bd-highlight">
                    {moment(homeworkItem.homework.publication_date).format("L")}{" "}
                  </div>
                </div>
              </div>
            </div>
          </div>
          {/* <div class="p-1 bd-highlight">
            <div class="d-flex flex-wrap flex-row bd-highlight justify-content-between  ">
              <div class="pr-2 bd-highlight">Sciences</div>
            </div>
          </div> */}
          <div class="p-1 bd-highlight">
            <div class="d-flex flex-wrap flex-column bd-highlight ">
              <div class=" bd-highlight text-primary">
                {homeworkItem.homework.title}
              </div>
              <div
                class=" bd-highlight text-primary"
                style={{ fontSize: "10px" }}
              >
                <IntlMessages id="homework.detail" />
              </div>
            </div>
          </div>
          <div>
            {!this.props.archived ? (
              <div class="d-flex flex-wrap flex-row  justify-content-around ">
                <div>
                  <Button
                    style={{
                      backgroundColor: "white",
                      color: "#7C7C7C",
                    }}
                  >
                    <span style={{ fontSize: "9px", color: "#7C7C7C" }}>
                      <IntlMessages id="message.attach.download" />|
                    </span>
                  </Button>
                </div>
                <div>
                  <Button
                    style={{
                      backgroundColor: "white",
                      color: "#7C7C7C",
                      width: "30px",
                    }}
                  >
                    <span style={{ fontSize: "9px", color: "#7C7C7C" }}>
                      <IntlMessages id="button.modify" /> |
                    </span>
                  </Button>
                </div>

                <div
                  className=" "
                  style={{
                    width: "18px",
                    height: "18px",
                  }}
                >
                  <IconButton size="small" className="icon-btn">
                    <i className="zmdi zmdi-eye" style={{ color: "#A3A3A3" }} />
                  </IconButton>
                </div>

                <div
                  className=" "
                  style={{
                    width: "18px",
                    height: "18px",
                  }}
                >
                  <IconButton size="small" className="icon-btn">
                    <i
                      className="zmdi zmdi-delete"
                      style={{ color: "#A3A3A3" }}
                      onClick={(e) => {
                        this.props.handleDelete(3);
                      }}
                    />
                  </IconButton>
                </div>
              </div>
            ) : (
              ""
            )}
          </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    userProfile: state.auth.userProfile,
  };
}

export default connect(mapStateToProps)(HomeworkItem);
