import { callService } from '../_services/call.service';
import _ from 'lodash';
import { GET_PERMISSION_SETTING } from '../constants/ActionTypes';
 var listPermissions = [
  {
    id: 1,
    Action_Fr: 'Ajouter un admin',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 2, 3, 5],
