  {
    id: 5,
    Action_Fr: 'Ajouter un membre de direction ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 2, 3, 4],
  },
  {
    id: 6,
    Action_Fr: 'Ajouter un devoir de maison  ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5, 6],
  },
  {
    id: 7,
    Action_Fr: 'Modifier un devoir de maison ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5],
  },
  {
    id: 8,
    Action_Fr: 'Supprimer un devoir de maison ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5],
  },
  {
    id: 9,
    Action_Fr: 'Consulter les devoirs de maisons',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5],
  },
];
export const getPermissionSetting = (idEducationType) => {
  console.log(idEducationType, 'getPermissionSetting');
  return (dispatch) => {
    dispatch({ type: GET_PERMISSION_SETTING, payload: listPermissions });
    let apiEndpoint = `/etab_role_permissions?access_token=${
      localStorage.token
    }&filter[where][fk_id_etab]=${6}&filter[include][permission]`;
    callService.get(apiEndpoint).then((response) => {
      if (response) {
        let listPermissionsuniq =_.uniqBy(response.data, 'fk_id_permission');

        console.log(response.data, 'response response response');
      }
    });
  };
};
