import React from "react";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import IntlMessages from "../../../../../util/IntlMessages";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Auxiliary from "../../../../../util/Auxiliary";
import { connect } from "react-redux";
import moment from "moment";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/moment";

class EditSchoolSession extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nameSchoolSession: "",
      start_date: "",
      end_date: "",
      id: null,
    };
  }
  componentDidMount() {
    this.setState({
      nameSchoolSession: this.props.schoolSession.name,
      start_date: this.props.values.item.start_date,
      end_date: this.props.values.item.end_date,
      id: this.props.schoolSession.id,
    });
  }
  render() {
    return (
      <Auxiliary>
        <Modal
          isOpen={this.props.values.isOpen}
          toggle={this.props.handleToggle}
        >
          <ModalHeader className="modal-box-header bg-primary text-white">
            {<IntlMessages id="modal.modif.module.school.session" />}
          </ModalHeader>
          <ModalBody>
            <form className="row" onSubmit={this.props.handleSubmit}>
              <div className="col-sm-6">
                <TextField
                  required
                  variant="outlined"
                  id="nameSchoolSession"
                  label={<IntlMessages id="schollSession.formadd.name" />}
                  value={this.props.values.nameSchoolSession}
                  onChange={this.props.handleChange("nameSchoolSession")}
                  margin="normal"
                  fullWidth
                />
              </div>

              <div className="col-sm-12">
                <h4>
                  <font color="red">*</font>{" "}
                  {<IntlMessages id="component.required_fields" />}
                </h4>
              </div>
              <div className="col-md-12 text-left ">
                <br />
                <br />
                <Button
                  variant="contained"
                  className="jr-btn bg-indigo text-white "
                  type="submit"
                >
                  {
                    <IntlMessages id="components.establishments.formModify.buttonModify" />
                  }
                </Button>
                <Button
                  variant="contained"
                  className="jr-btn bg-grey text-white "
                  onClick={this.props.handleAnnule}
                >
                  {
                    <IntlMessages id="components.establishments.formadd.buttonCancel" />
                  }
                </Button>
              </div>
            </form>
          </ModalBody>
        </Modal>
      </Auxiliary>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};

export default connect(
  mapStateToProps,
  {}
)(EditSchoolSession);
