import { roleIdDirector } from "../../../config/config";
import _ from "lodash";

class VirtualClassList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      professorIdFilter: "",
      studentClassIdFilter: "",
      id: "",
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: true,
      class_object: "",
      level_id: "",
      section_id: "",
      professor_id: "",
      class_id: "",
      subject_id: "",
      class_url: "",
      class_password: "",
      class_date: "",
      selectedStartTime: new Date(),
      selectedEndTime: new Date(),
      startTime: "",
      endTime: "",
      studentsectionByLevels: [],
      Disable_studentsection: true,
      levelsbyestablishment: [],
      studentClassesByLevelSectionID: [],
      subjectList: [],
      establishmentProfessor: [],
      professorClassesAdd: [],
      profSubjects: [],
    };
    this.addClassShowModal = this.addClassShowModal.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleChangeDate = this.handleChangeDate.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleStartTimeChange = this.handleStartTimeChange.bind(this);
    this.handleEndTimeChange = this.handleEndTimeChange.bind(this);
    this.editVirtualClass = this.editVirtualClass.bind(this);
    this.handleChangestudentSection = this.handleChangestudentSection.bind(
      this
    );
    this.handleChangestudentLevel = this.handleChangestudentLevel.bind(this);
    this.handleChangeClass = this.handleChangeClass.bind(this);
    this.handleChangeSubject = this.handleChangeSubject.bind(this);
    this.handleChangeProfessor = this.handleChangeProfessor.bind(this);
  }

  editVirtualClass = () => {
    var data = {};
    data.id = this.state.id;
    data.object = this.state.class_object;
    data.class_id = this.state.class_id;
    data.subject_id = this.state.subject_id;
    data.class_url = this.state.class_url;
    data.password = this.state.class_password;
    data.date_virtual_class = this.state.class_date;
    data.start_time_class = this.state.startTime;
    data.end_time_class = this.state.endTime;
    data.professor_id = this.state.professor_id;
    data.status = true;
    data.establishment_id = this.props.userProfile.establishment_id;
    data.virtual_class_status = "programée";

    this.props.editClassVirtual(data);
    this.setState({
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: false,
      class_object: "",
      level_id: "",
      section_id: "",
      professor_id: "",
      class_id: "",
      subject_id: "",
      class_url: "",
      class_password: "",
      class_date: "",
      selectedStartTime: new Date(),
      selectedEndTime: new Date(),
      startTime: "",
      endTime: "",
