      editIsopen: true,
      id: ClassObjectEdit.id,
      class_object: ClassObjectEdit.object,
      class_id: ClassObjectEdit.class_id,
      subject_id: ClassObjectEdit.subject_id,
      class_url: ClassObjectEdit.class_url,
      class_password: ClassObjectEdit.password,
      class_date: ClassObjectEdit.date_virtual_class,
      startTime: ClassObjectEdit.start_time_class,
      endTime: ClassObjectEdit.end_time_class,
      professor_id: ClassObjectEdit.professor_id,
      status: ClassObjectEdit.status,
      establishment_id: ClassObjectEdit.establishment_id,
      virtual_class_status: ClassObjectEdit.virtual_class_status,
      level_id: ClassObjectEdit.level_id,
      section_id: ClassObjectEdit.section_id,
