    let apiEndpoint1 =
      `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][and][0][class_id]=` +
      ClassObjectEdit.class_id +
      `&filter[where][and][1][professor_id]=` +
      ClassObjectEdit.professor_id +
      `&filter[include][subject]`;
    axios.get(apiEndpoint1).then((res) => {
      let profSubjects = _.map(
        res.data.filter((element) => element.status),
        "subject"
      );
      this.setState({ profSubjects });
    });

