      this.setState({ Disable_studentsection: false });
      var studentSectionbyLevel = getSectionFromLevel(
        this.props.classSections,
        ClassObjectEdit.level_id
      );
      this.setState({ studentsectionByLevels: studentSectionbyLevel });
    } else {
      this.setState({
        Disable_studentsection: true,
        section_id: 0,
      });
      const levelID = ClassObjectEdit.level_id;
      var studentfiltredClasses = this.props.classes.filter(
        (classe) => classe.level_id === levelID && classe.status
      );
      var professorClassesAdd = this.props.professorClassesList.filter(
        (classe) => classe.level_id === levelID && classe.status
      );
      this.setState({
        studentClassesByLevelSectionID: studentfiltredClasses,
        professorClassesAdd,
      });
    }

    const levelID = ClassObjectEdit.level_id;
    const sectionID = ClassObjectEdit.section_id;
    var studentfiltredClasses = this.props.classes.filter(
      (classe) =>
        classe.level_id === levelID &&
        classe.section_id === sectionID &&
        classe.status
    );
    var professorClassesAdd = this.props.professorClassesList.filter(
      (classe) =>
        classe.level_id === levelID &&
        classe.section_id === sectionID &&
        classe.status
    );
    this.setState({
      studentClassesByLevelSectionID: studentfiltredClasses,
      professorClassesAdd,
    });

    let apiEndpoint =
      `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][and][0][subject_id]=` +
      ClassObjectEdit.subject_id +
      `&filter[where][and][1][class_id]=` +
      ClassObjectEdit.class_id +
      `&filter[include][professor][profile][user]`;

    axios.get(apiEndpoint).then((res) => {
      this.setState({ establishmentProfessor: res.data });
    });
  };
  handleCancel = () => {
    this.setState({
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: false,
    });
  };

  handleChangeSubject = (name) => (event) => {
    this.setState({ [name]: event.target.value, professor_id: "" });

    let apiEndpoint =
      `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][and][0][subject_id]=` +
      event.target.value +
      `&filter[where][and][1][class_id]=` +
      this.state.class_id +
      `&filter[include][professor][profile][user]`;

    axios.get(apiEndpoint).then((res) => {
      var establishmentProfessor = res.data.filter((element) => element.status);
      this.setState({ establishmentProfessor });
    });
  };

  handleChangestudentSection = (name) => (event) => {
    this.setState({ [name]: event.target.value });
    const levelID = this.state.level_id;
    const sectionID = event.target.value;
    var studentfiltredClasses = this.props.classes.filter(
      (classe) =>
        classe.level_id === levelID &&
        classe.section_id === sectionID &&
        classe.status
    );
    var professorClassesAdd = this.props.professorClassesList.filter(
      (classe) =>
        classe.level_id === levelID &&
        classe.section_id === sectionID &&
        classe.status
    );
    this.setState({
      studentClassesByLevelSectionID: studentfiltredClasses,
      professorClassesAdd,
    });
  };
  handleChangestudentLevel = (name) => (event) => {
    this.setState({
      section_id: 0,
      class_id: 0,
      studentClassesByLevelSectionID: [],
      professorClassesAdd: [],
      profSubjects: [],
      establishmentProfessor: [],
    });
    if (event.target.value > 10) {
      this.setState({ [name]: event.target.value });
      this.setState({ Disable_studentsection: false });
      var studentSectionbyLevel = getSectionFromLevel(
        this.props.classSections,
        event.target.value
      );
      this.setState({ studentsectionByLevels: studentSectionbyLevel });
    } else {
      this.setState({
        level_id: event.target.value,
        Disable_studentsection: true,
        section_id: 0,
      });
      const levelID = event.target.value;
      var studentfiltredClasses = this.props.classes.filter(
        (classe) => classe.level_id === levelID && classe.status
      );
      var professorClassesAdd = this.props.professorClassesList.filter(
        (classe) => classe.level_id === levelID && classe.status
      );
      this.setState({
        studentClassesByLevelSectionID: studentfiltredClasses,
        professorClassesAdd,
      });
    }
  };

  handleChangeClass = (name) => (event) => {
    this.setState({
      subject_id: "",
      profSubjects: [],
      establishmentProfessor: [],
      professor_id: "",
    });
    if (this.props.userProfile.role_id === roleIdAdmin) {
      this.setState({
        establishment_id: this.props.userProfile.establishment_id,
      });
    } else {
      let apiEndpoint1 =
        `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][and][0][class_id]=` +
        event.target.value +
        `&filter[where][and][1][professor_id]=` +
        this.state.professorIdFilter +
        `&filter[include][subject]`;
      axios.get(apiEndpoint1).then((res) => {
        let profSubjects = _.map(
          res.data.filter((element) => element.status),
          "subject"
        );
        this.setState({ profSubjects });
      });
    }
    this.setState({ [name]: event.target.value });
  };

  componentWillMount() {
    //  this.props.getClassesVirtual();
    this.props.getSections();
    // this.props.getLevels();
    //this.props.getLevelsVirtualClass();

    this.props.getClassesByEstablishmentId(
      this.props.userProfile.establishment_id
    );

    axios
      .get(
        `${baseUrl.baseUrl}/establishments/` +
          this.props.userProfile.establishment_id +
          `?access_token=${localStorage.token}`
      )
      .then((res) => {
        var levelsbyestablishment = getLevelListFromEstabType(
          this.props.classLevels,
          res.data.estab_type_id
        );
        this.setState({
          estabType: res.data.estab_type_id,
          levelsbyestablishment: levelsbyestablishment,
        });

        axios
          .get(`${baseUrl.baseUrl}/subjects?access_token=${localStorage.token}`)
          .then((res) => {
            const subjectList = res.data;
            this.setState({ subjectList });
          });
      });

    if (this.props.userProfile.role_id === 3) {
      axios
        .get(
          `${baseUrl.baseUrl}/profiles/` +
            this.props.userProfile.id +
            `/professors?access_token=${localStorage.token}`
        )
        .then((res) => {
          this.setState({
            professorIdFilter: res.data[0].id,
          });
        });
    }
    if (this.props.userProfile.role_id === 5) {
      axios
        .get(
          `${baseUrl.baseUrl}/students/findOne?access_token=?access_token=${localStorage.token}&filter={"where":{"profile_id":` +
            this.props.userProfile.id +
            `}}`
        )
        .then((res) => {
          this.setState({
            studentClassIdFilter: res.data.class_id,
          });
        });
    }
  }

  render() {
    var VirtualClassListByRole = [];
    var VirtualClassListFinal = [];

    if (this.props.userProfile.role_id === roleIdProfessor) {
      if (
        this.props.professor_class_id == 0 &&
        this.props.selectedFilterDateProfessor === null
      ) {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) => element.professor_id === this.state.professorIdFilter
        );
      } else if (
        this.props.professor_class_id != "" &&
        this.props.selectedFilterDateProfessor === null
      ) {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) =>
            element.professor_id === this.state.professorIdFilter &&
            element.class_id === this.props.professor_class_id
        );
      } else if (
        this.props.professor_class_id == 0 &&
        this.props.selectedFilterDateProfessor !== null
      ) {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) =>
            element.professor_id === this.state.professorIdFilter &&
            element.date_virtual_class.slice(0, 10) ===
              this.props.selectedFilterDateProfessor
        );
      } else {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) =>
            element.professor_id === this.state.professorIdFilter &&
            element.date_virtual_class.slice(0, 10) ===
              this.props.selectedFilterDateProfessor &&
            element.class_id === this.props.professor_class_id
        );
      }
    } else if (
      this.props.userProfile.role_id === roleIdStudent ||
      this.props.userProfile.role_id === roleIdParent
    ) {
      VirtualClassListByRole = this.props.classVirtual.filter(
        (element) =>
          element.class_id === this.state.studentClassIdFilter &&
          element.subject_id === this.props.student_subject_id
      );

      if (
        this.props.student_subject_id === "" &&
        this.props.selectedFilterDateStudent === null
      ) {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) => element.class_id === this.state.studentClassIdFilter
        );
      } else if (
        this.props.student_subject_id != "" &&
        this.props.selectedFilterDateStudent == null
      ) {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) =>
            element.class_id === this.state.studentClassIdFilter &&
            element.subject_id === this.props.student_subject_id
        );
      } else if (
        this.props.student_subject_id === "" &&
        this.props.selectedFilterDateStudent !== null
      ) {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) =>
            element.class_id === this.state.studentClassIdFilter &&
            element.date_virtual_class.slice(0, 10) ===
              this.props.selectedFilterDateStudent
        );
      } else {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) =>
            element.class_id === this.state.studentClassIdFilter &&
            element.date_virtual_class.slice(0, 10) ===
              this.props.selectedFilterDateStudent &&
            element.subject_id === this.props.student_subject_id
        );
      }
    } else {
      if (this.props.adminClassId == "") {
        VirtualClassListByRole = this.props.classVirtual;
      } else {
        VirtualClassListByRole = this.props.classVirtual.filter(
          (element) => element.class_id === this.props.adminClassId
        );
      }
    }
    VirtualClassListFinal = VirtualClassListByRole.filter((element) => {
      const sys = Date.parse(new Date()) / 60000;
      const start =
        Date.parse(
          element.date_virtual_class.slice(0, 10) +
            " " +
            element.start_time_class
        ) / 60000;
      const end =
        Date.parse(
          element.date_virtual_class.slice(0, 10) + " " + element.end_time_class
        ) / 60000;

      if (this.props.statutId === 0 && start > sys && end > sys) {
        return element;
      } else if (this.props.statutId === 1 && start < sys && end > sys) {
        return element;
      } else if (this.props.statutId === 2 && start < sys && end < sys) {
        return element;
      }
    });

    return (
      <div>
        <div className="row animated slideInUpTiny animation-duration-3">
          {VirtualClassListFinal.map((element, index) => {
            return (
              <VitualClassListItems
                key={index}
                classVirtualElement={element}
                subjectList={this.props.subjectList}
                editClassShowModal={this.editClassShowModal}
              />
            );
          })}
        </div>
        {this.state.editIsopen === true ? (
          <EditVitualClass
            classVirtual={VirtualClassListByRole}
            values={this.state}
            editIsopen={this.state.editIsopen}
            handleCancel={this.handleCancel}
            handleChangeDate={this.handleChangeDate}
            handleChange={this.handleChange}
            handleStartTimeChange={this.handleStartTimeChange}
            handleEndTimeChange={this.handleEndTimeChange}
            editVirtualClass={this.editVirtualClass}
            handleChangestudentSection={this.handleChangestudentSection}
            handleChangestudentLevel={this.handleChangestudentLevel}
            handleChangeClass={this.handleChangeClass}
            handleChangeSubject={this.handleChangeSubject}
            handleChangeProfessor={this.handleChangeProfessor}
            levelsEdit={this.props.levelsEdit}
            professorClassesList={this.props.professorClassesList}
          />
        ) : (
          ""
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    userProfile: state.auth.userProfile,
    classLevels: state.ClassLevels.remoteLevels,
    classSections: state.classSections.remoteSections,
    classes: state.classes,
    classVirtual: state.classVirtualReducer.remoteClassVirtual,
  };
}

export default connect(
  mapStateToProps,
  {
    editClassVirtual,
    getSections,
    getLevels,
    getLevelsVirtualClass,
    getClassesByEstablishmentId,
    getClassesVirtual,
  }
)(VirtualClassList);
