import React from 'react';

class GestionParametreCard extends React.Component {
  state = {
    isHide: false,
    children: '',
    chartProperties: {
      prize: '',
      title: '',
      bgColor: '',
      styleName: '',
      desc: '',
      percent: '',
      icon: '',
    },
  };

  componentDidMount() {
    this.setState((previousState) => ({
      chartProperties: this.props.chartProperties,
      children: this.props.children,
    }));
  }

  handleToggle() {
    this.setState((previousState) => ({
      isHide: !previousState.isHide,
    }));
  }

  render() {
    const { chartProperties, isHide, children } = this.state;
    const { prize, title, styleName, bgColor, icon } = chartProperties;
    return (
      <div className="jr-card jr-card-full">
        <div
          className={
            isHide === true
              ? `jr-fillchart bg-${bgColor}
jr-fillchart-nocontent`
              : `jr-fillchart bg-${bgColor}
jr-overlay-fillchart`
          }
        >
          <div className="card-title mb-3">{title}</div>
          {children}
          <div className="jr-fillchart-content">
            <div className="card-title mb-4">{title}</div>

            <i className={`${icon} zmdi-hc-3x`} style={{ float: 'right' }} />

            <h2
              className="mb-2 jr-fs-xl
jr-font-weight-medium"
            >
              {prize}
            </h2>

            <p className="mb-0 jr-fs-sm">
              <span
                className={`jr-font-weight-medium jr-fs-md
jr-chart-${styleName}`}
              ></span>
            </p>
          </div>
          <div
            className="jr-fillchart-btn-close"
            onClick={this.handleToggle.bind(this)}
          >
            <i className="zmdi zmdi-close" />
          </div>
          {/* <div
            className="jr-fillchart-btn"
            onClick={this.handleToggle.bind(this)}
          >
            <i className={`zmdi zmdi-equalizer jr-fs-lg`} />
          </div> */}
        </div>
      </div>
    );
  }
}

export default GestionParametreCard;
