import { classService } from "../_services/class.service";
import {
  ADD_SCHOOL_SESSION,
  SHOW_SUCCESS_MESSAGE,
  HIDE_SUCCESS_MESSAGE,
  SHOW_ERROR_MESSAGE,
  HIDE_ERROR_MESSAGE,
  GET_SCHOOL_SESSION,
  EDIT_SCHOOL_SESSION,
  DELETE_SCHOOL_SESSION,
  ARCHIVED_GET_SCHOOL_SESSION,
} from "../constants/ActionTypes";
export function addSchoolSession(data) {
  return (dispatch) => {
    let apiEndpoint = `/school_sessions?access_token=${localStorage.token}`;
    classService.post(apiEndpoint, data).then((response) => {
      if (response) {
        dispatch({ type: ADD_SCHOOL_SESSION, payload: response.data });
        dispatch({
          type: SHOW_SUCCESS_MESSAGE,
          payload: "La création est effectuée avec succès",
        });
        setTimeout(() => {
          dispatch({ type: HIDE_SUCCESS_MESSAGE });
        }, 4000);
      } else {
        dispatch({
          type: SHOW_ERROR_MESSAGE,
          payload:
            "Une erreur est survenue lors de la création merci d'essayer à nouveau",
        });
        setTimeout(() => {
          dispatch({ type: HIDE_ERROR_MESSAGE });
        }, 4000);
      }
    });
  };
}

export function editSchoolSession(data) {
  return (dispatch) => {
    let apiEndpoint = `/school_sessions/${data.id}?access_token=${localStorage.token}`;
    classService.patch(apiEndpoint, { name: data.name }).then((response) => {
      if (response) {
        dispatch({ type: EDIT_SCHOOL_SESSION, payload: response.data });
      }
    });
  };
}

export function getSchoolSession(establishementId, schoolYearId) {
  return (dispatch) => {
    let apiEndpoint = `/school_sessions?access_token=${localStorage.token}&filter[where][and][0][fk_id_establishment]=${establishementId}&filter[where][and][1][fk_id_school_year]=${schoolYearId}`;
    classService.get(apiEndpoint).then((response) => {
      if (response) {
        const list = response.data;
        const SchoolSessionList = list.filter((element) => element.status);
        const ArchivedSchoolSessionListList = list.filter(
          (element) => element.status == false
        );
        dispatch({ type: GET_SCHOOL_SESSION, payload: SchoolSessionList });
        dispatch({
          type: ARCHIVED_GET_SCHOOL_SESSION,
          payload: ArchivedSchoolSessionListList,
        });
      }
    });
  };
}

export function deleteSchoolSession(itemId) {
  return (dispatch) => {
    let apiEndpoint =
      `/school_sessions/` +
      itemId.id +
      `?access_token=${localStorage.token}&filter[where][and][0][fk_id_establishment]=${localStorage.establishment_id}&filter[where][and][1][fk_id_school_year]=${localStorage.school_year_id}`;
    classService
      .patch(apiEndpoint, {
        status: false,
      })
      .then((response) => {
        dispatch({ type: DELETE_SCHOOL_SESSION, payload: response.data });
      })
      .catch(function(error) {});
  };
}
