import React from "react";
import IntlMessages from "../../../../../util/IntlMessages";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import Auxiliary from "../../../../../util/Auxiliary";
import TextField from "@material-ui/core/TextField";
import CalendarTodayIcon from "@material-ui/icons/CalendarToday";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Button from "@material-ui/core/Button";
import { Form, FormGroup, Row, Col } from "reactstrap";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import {
  roleIdSuperAdmin,
  roleIdAdmin,
  roleIdProfessor,
  roleIdStudent,
  roleIdDirector,
  roleIdParent,
  roleIdSupervisor,
} from "../../../../../config/config";
import PictureAsPdfIcon from "@material-ui/icons/PictureAsPdf";
import Typography from "@material-ui/core/Typography";
import _ from 'lodash';

export class ModalDetailsUser extends React.Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
     let userItem = this.props.userItem;

    return (
      <Auxiliary>
        <Modal isOpen={this.props.openDetails}>
          <ModalHeader className="modal-box-header bg-primary text-white">
          <IntlMessages id="user.details" />
             <IconButton className="text-white">
              <CloseIcon onClick={this.props.handleCancelDetails} />
            </IconButton>
          </ModalHeader>
          <ModalBody style={{ paddingLeft: "5%", paddingRight: "5%" }}>
            <form>
              <div class="d-flex flex-row col-md-12 col-lg-12 col-sm-12">
                <div class="form-group col-md-6 col-lg-3 col-sm-12">
                  <label for="gender">
                    {" "}
                    <IntlMessages id="user.gender" />
                  </label>
                  <input
                    disabled={true}
                    type="gender"
                    class="form-control"
                    id="gender"
                    value={userItem.gender}
                  />
                </div>
                <div class="form-group col-md-6 col-lg-3 col-sm-12">
                  <label for="birthdaydate">
                    <IntlMessages id="user.birthday.date" />
                  </label>
                  <input
                    disabled={true}
                    type="date"
                    class="form-control"
                    id="birthdaydate"
                    value={userItem.dateOfBirth}
                  />
                </div>
                <div class="form-group col-md-6 col-lg-6 col-sm-12">
                  <label for="birthdayplace">
                    <IntlMessages id="user.birthday.place" />
                  </label>
                  <input
                    disabled={true}
                    type="text"
                    class="form-control"
                    id="birthdayplace"
                    value={userItem.placeOfBirth}
                  />
                </div>

              </div>
              <div class="d-flex flex-row col-md-12 col-lg-12 col-sm-12">
              <div class="form-group col-md-6 col-lg-3 col-sm-12">
                  <label for="nationality">
                    <IntlMessages id="user.nationality" />
                  </label>
                  <input
                    disabled={true}
                    type="text"
                    class="form-control"
                    id="nationality"
                    value={userItem.nationality}
                  />
                </div>
                {userItem.roleId === roleIdStudent ? (
                  <div class="form-group col-md-6 col-lg-3 col-sm-12">
                    <label for="identifiant">
                      {<IntlMessages id="user.identifiant" />}
                    </label>
                    <input
                      disabled={true}
                      type="text"
                      class="form-control"
                      id="identifiant"
                      value={userItem.cin}
                    />
                  </div>
                ) : (
                  <div class="form-group col-md-6 col-lg-4 col-sm-12">
                    <label for="cin">{<IntlMessages id="user.cin" />}</label>
                    <input
                      disabled={true}
                      type="text"
                      class="form-control"
                      id="cin"
                      value={userItem.cin}
                    />
                  </div>
                )}
                <div class="form-group col-md-6 col-lg-5 col-sm-12">
                  <label for="addresspostal">
                    {<IntlMessages id="user.address.postal" />}
                  </label>
                  <input
                    disabled={true}
                    type="text"
                    class="form-control"
                    id="addresspostal"
                    value={userItem.cin}
                    />
                </div>
              </div>
              <div class="d-flex flex-row col-md-12 col-lg-12 col-sm-12">
                <div class="form-group col-md-6 col-lg-3 col-sm-12">
                  <label for="zipcode">
                    {<IntlMessages id="zip.code.user" />}
                  </label>
                  <input
                    disabled={true}
                    type="text"
                    class="form-control"
                    id="zipcode"
                    value={userItem.zipCode}
                  />
                </div>
                <div class="form-group col-md-6 col-lg-3 col-sm-12">
                  <label for="country">
                    {<IntlMessages id="country.user" />}
                  </label>
                  <input
                    disabled={true}
                    type="text"
                    class="form-control"
                    id="country"
                    value={userItem.country}
                  />
                </div>
              </div>
              <div class="d-flex flex-column col-md-12 col-lg-12 col-sm-12">
                <div class="col-md-12 col-lg-12 col-sm-12">
                  <label for="inforamtionsuser">
                    {<IntlMessages id="inforamtions.user" />}
                  </label>
                </div>
                <div class="col-md-12 col-lg-12 col-sm-12 p-3">
                  <textarea
                    disabled={true}
                    className="container"
                    id="UsefulInformation"
                    name="UsefulInformation"
                    rows="3"
                    value={""}
                    style={{
                      borderRadius: "20px",
                      marginTop: "10px",
                      width: "100%",
                    }}
                  ></textarea>
                </div>
              </div>
              <div class="d-flex flex-column col-md-12 col-lg-12 col-sm-12">
                <div class="col-md-12 col-lg-12 col-sm-12">
                  <label for="inforamtionsuser">
                    {<IntlMessages id="user.adminstratif.papiers" />}{" "}
                  </label>
                </div>
                <div class="col-md-12 col-lg-12 col-sm-12 d-flex  flex-row p-3">
                  {!_.isEmpty(userItem.paperFiles) && (userItem.paperFiles).map((element) => {
                    return (
                      <div class="d-flex flex-column">
                        <div class="p-2">
                          {" "}
                          <PictureAsPdfIcon
                            style={{
                              fontSize: "35",
                            }}
                          />
                        </div>
                        <div class="p-2">
                          {" "}
                          <Typography
                            variant="h6"
                            style={{
                              color: "#3F51B5",
                              fontWeight: "normal",
