import { classService } from '../_services/class.service';
import {
  SHOW_SUCCESS_MESSAGE,
  HIDE_SUCCESS_MESSAGE,
  SHOW_ERROR_MESSAGE,
  HIDE_ERROR_MESSAGE,
  GET_GROUPS,
  ADD_GROUPS,
  EDIT_GROUPS,
  DELETE_GROUPS,
  ARCHIVED_GET_GROUPS,
} from '../constants/ActionTypes';

export function addGroup(data, dataClass) {
  return (dispatch) => {
    console.log('data avant post ', data, dataClass);
    let apiEndpoint = `/groups?access_token=${localStorage.token}`;
    classService.post(apiEndpoint, data).then((response) => {
      if (response) {
        console.log('response.data ', response.data);
        dispatch({ type: ADD_GROUPS, payload: response.data });
        dispatch({
          type: SHOW_SUCCESS_MESSAGE,
          payload: 'La création est effectuée avec succès',
        });
        setTimeout(() => {
          dispatch({ type: HIDE_SUCCESS_MESSAGE });
        }, 4000);
      } else {
        dispatch({
          type: SHOW_ERROR_MESSAGE,
          payload: "Une erreur est survenue lors de la création merci d'essayer à nouveau",
        });
        setTimeout(() => {
          dispatch({ type: HIDE_ERROR_MESSAGE });
        }, 4000);
      }
    });
  };
}

export function editGroup(data) {
  return (dispatch) => {
    let apiEndpoint = `/group_v4/${data.id}?access_token=${localStorage.token}`;
    classService.patch(apiEndpoint, data).then((response) => {
      if (response) {
        dispatch({ type: EDIT_GROUPS, payload: response.data });
      }
    });
  };
}

export function getGroup(establishementId, schoolYearId) {
  return (dispatch) => {
    let apiEndpoint = `/groups?access_token=${localStorage.token}&filter[where][fk_id_school_year]=${schoolYearId}&filter[include][educationType]`;
    classService.get(apiEndpoint).then((response) => {
      if (response) {
        let groupListByEstab = [];
        response.data.forEach((element) => {
          if (element.educationType.fk_id_establishment == establishementId) {
            groupListByEstab.push(element);
          }
        });
        const list = groupListByEstab;
        const LevelList = list.filter((element) => element.status);
        const ArchivedGroupList = list.filter((element) => element.status == false);
        dispatch({ type: GET_GROUPS, payload: LevelList });
        dispatch({ type: ARCHIVED_GET_GROUPS, payload: ArchivedGroupList });
      }
    });
  };
}

export function deleteGroup(itemId) {
  return (dispatch) => {
    let apiEndpoint =
      `/group_v4/` +
      itemId.id +
      `?access_token=${localStorage.token}&filter[where][and][0][fk_id_establishment]=${localStorage.establishment_id}&filter[where][and][1][fk_id_school_year]=${localStorage.school_year_id}`;
    classService
      .patch(apiEndpoint, {
        status: false,
      })
      .then((response) => {
        dispatch({ type: DELETE_GROUPS, payload: response.data });
      })
      .catch(function(error) {});
  };
}
