import React from "react";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import IntlMessages from "../../../../../util/IntlMessages";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Auxiliary from "../../../../../util/Auxiliary";
import { connect } from 'react-redux';

class EditSubjectModules extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      namesubjectModule:'',
      id: null
    };
  }
  componentDidMount() {
 this.setState({namesubjectModule: this.props.moduleSubject.name, id : this.props.moduleSubject.id})
  }
  render() {
    return (
      <Auxiliary>
        <Modal
          isOpen={this.props.values.isOpen}
          toggle={this.props.handleToggle}
        >
          <ModalHeader className="modal-box-header bg-primary text-white">
            {<IntlMessages id="modal.modif.module.subjects" />}
          </ModalHeader>
          <ModalBody>
            <form className="row" onSubmit={this.props.handleSubmit}>
              <div className="col-sm-6">
                <TextField
                  required
                  variant="outlined"
                  id="namesubjectModule"
                  label={<IntlMessages id="subjectModule.formadd.name" />}
                  value={this.props.values.namesubjectModule}
                  onChange={this.props.handleChange("namesubjectModule")}
                  margin="normal"
                  fullWidth
                />
              </div>

              <div className="col-sm-12">
                <h4>
                  <font color="red">*</font>{" "}
                  {<IntlMessages id="component.required_fields" />}
                </h4>
              </div>
              <div className="col-md-12 text-left ">
                <br />
                <br />
                <Button
                  variant="contained"
                  className="jr-btn bg-indigo text-white "
                  type="submit"
                >
                  {
                    <IntlMessages id="components.establishments.formModify.buttonModify" />
                  }
                </Button>
                <Button
                  variant="contained"
                  className="jr-btn bg-grey text-white "
                  onClick={this.props.handleAnnule}
                >
                  {
                    <IntlMessages id="components.establishments.formadd.buttonCancel" />
                  }
                </Button>
              </div>
            </form>
          </ModalBody>
        </Modal>
      </Auxiliary>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};

export default connect(
  mapStateToProps,
  {  }
)(EditSubjectModules);
