import React from 'react';
import ContainerHeader from '../../../../../components/ContainerHeader/index';
import IntlMessages from '../../../../../util/IntlMessages';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import AddVirtualClass from './AddVirtualClass';
import VirtualClassList from './VirtualClassList';
import { addClassVirtual } from '../../../../../actions/VirtualClassAction';
import { getClassesVirtual } from '../../../../../actions/VirtualClassAction';
import { getClassVirtualByEstablishmentId } from '../../../../../actions/VirtualClassAction';

import { connect } from 'react-redux';
import { getSectionFromLevel } from '../../../../../actions/sectionAction';
import { getLevelListFromEstabType } from '../../../../../actions/classLevelAction';
import axios from 'axios';
import baseUrl from '../../../../../config/config';

import { getSections } from '../../../../../actions/sectionAction';
import { getLevels } from '../../../../../actions/classLevelAction';
import { getClassesByEstablishmentId } from '../../../../../actions/classeAction';

class VirtualClasses extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: true,
      class_object: '',
      level_id: '',
      section_id: '',
      professor_id: '',
      class_id: '',
      subject_id: '',
      class_url: '',
      class_password: '',
      class_date: '',
      selectedStartTime: new Date(),
      selectedEndTime: new Date(),
      startTime: '',
      endTime: '',
      studentsectionByLevels: [],
      Disable_studentsection: true,
      levelsbyestablishment: [],
      studentClassesByLevelSectionID: [],
      subjectList: [],
      establishmentProfessor: [],
    };
    this.addClassShowModal = this.addClassShowModal.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleChangeDate = this.handleChangeDate.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleStartTimeChange = this.handleStartTimeChange.bind(this);
    this.handleEndTimeChange = this.handleEndTimeChange.bind(this);
    this.addVirtualClass = this.addVirtualClass.bind(this);
    this.handleChangestudentSection = this.handleChangestudentSection.bind(
      this
    );
    this.handleChangestudentLevel = this.handleChangestudentLevel.bind(this);
    this.handleChangeClass = this.handleChangeClass.bind(this);
    this.handleChangeSubject = this.handleChangeSubject.bind(this);
    this.handleChangeProfessor = this.handleChangeProfessor.bind(this);
  }

  addVirtualClass = () => {
    var data = {};
    data.object = this.state.class_object;
    data.class_id = this.state.class_id;
    data.subject_id = this.state.subject_id;
    data.class_url = this.state.class_url;
    data.password = this.state.class_password;
    data.date_virtual_class = this.state.class_date;
    data.start_time_class = this.state.startTime;
    data.end_time_class = this.state.endTime;
    data.professor_id = this.state.professor_id;
    data.status = true;
    data.establishment_id = this.props.userProfile.establishment_id;
    data.virtual_class_status = 'programée';

    this.props.addClassVirtual(data);
    this.setState({
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: false,
      class_object: '',
      level_id: '',
      section_id: '',
      professor_id: '',
      class_id: '',
      subject_id: '',
      class_url: '',
      class_password: '',
      class_date: '',
      selectedStartTime: new Date(),
      selectedEndTime: new Date(),
      startTime: '',
      endTime: '',
      studentsectionByLevels: [],
      Disable_studentsection: true,
      levelsbyestablishment: [],
      studentClassesByLevelSectionID: [],
      subjectList: [],
      establishmentProfessor: [],
    });
  };
  handleChange = (name) => (event) => {
    this.setState({ [name]: event.target.value });
  };
  handleChangeProfessor = name => event => {
    this.setState({ professor_id: event.target.value });
  };

  handleChangeDate = (date) => {
    this.setState({ class_date: date });
  };
  handleStartTimeChange = (time) => {
    this.setState({ selectedStartTime: time, startTime: time.format('LT') });
  };

  handleEndTimeChange = (time) => {
    this.setState({ selectedEndTime: time, endTime: time.format('LT') });
  };

  addClassShowModal = () => {
    this.setState({ addIsopen: true });
  };

  editClassShowModal = () => {
    this.setState({ editIsopen: true });
  };
  handleCancel = () => {
    this.setState({
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: false,
    });
  };

  handleChangeSubject = (name) => (event) => {
    this.setState({ [name]: event.target.value });

    let apiEndpoint =
      `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][subject_id]=` +
      event.target.value +
      `&filter[include][professor][profile][user]`;
    axios.get(apiEndpoint).then((res) => {
      this.setState({ establishmentProfessor: res.data });
    });
  };

  handleChangestudentSection = (name) => (event) => {
    this.setState({ [name]: event.target.value });
    const levelID = this.state.level_id;
    const sectionID = event.target.value;
    var studentfiltredClasses = this.props.classes.filter(
      (classe) =>
        classe.level_id === levelID &&
        classe.section_id === sectionID &&
        classe.status
    );
    this.setState({ studentClassesByLevelSectionID: studentfiltredClasses });
  };
  handleChangestudentLevel = (name) => (event) => {
    this.setState({ section_id: 0, class_id: 0 });
    if (event.target.value > 10) {
      this.setState({ [name]: event.target.value });
      this.setState({ Disable_studentsection: false });
      var studentSectionbyLevel = getSectionFromLevel(
        this.props.classSections,
        event.target.value
      );
      this.setState({ studentsectionByLevels: studentSectionbyLevel });
    } else {
      this.setState({
        level_id: event.target.value,
        Disable_studentsection: true,
        section_id: 0,
      });
      const levelID = event.target.value;
      var studentfiltredClasses = this.props.classes.filter(
        (classe) => classe.level_id === levelID && classe.status
      );
      this.setState({ studentClassesByLevelSectionID: studentfiltredClasses });
    }
  };

  handleChangeClass = (name) => (event) => {
    if (this.props.userProfile.role_id === 2) {
      this.setState({
        establishment_id: this.props.userProfile.establishment_id,
      });
    }
    this.setState({ [name]: event.target.value });
  };

  componentWillMount() {
if(this.props.userProfile.role_id===1){
  this.props.getClassesVirtual();


}
else{
  this.props.getClassVirtualByEstablishmentId(this.props.userProfile.establishment_id);

}


    this.props.getSections();
    this.props.getLevels();
    this.props.getClassesByEstablishmentId(
      this.props.userProfile.establishment_id
    );

    axios
      .get(
        `${baseUrl.baseUrl}/establishments/` +
          this.props.userProfile.establishment_id +
          `?access_token=${localStorage.token}`
      )
      .then((res) => {
        var levelsbyestablishment = getLevelListFromEstabType(
          this.props.classLevels,
          res.data.estab_type_id
        );
        this.setState({
          estabType: res.data.estab_type_id,
          levelsbyestablishment: levelsbyestablishment,
        });

        axios
          .get(`${baseUrl.baseUrl}/subjects?access_token=${localStorage.token}`)
          .then((res) => {
            const subjectList = res.data;
            this.setState({ subjectList });
          });
      });
  }
  render() {
    return (
      <div className="app-wrapper">
        <ContainerHeader
          match={this.props.match}
          title={<IntlMessages id="sidebar.components.virtual_classes" />}
        />
        <br />
        {
          (this.props.userProfile.role_id===1 || this.props.userProfile.role_id===2 || this.props.userProfile.role_id===7) ?
          <div className="col-md-12 text-right " style={{ marginBottom: 20 }}>
          <Fab
            size="small"
            color="primary"
            aria-label="Add"
            onClick={this.addClassShowModal}
          >
            <AddIcon />
          </Fab>
        </div> :""
        }

        <div className="col-md-12 text-right ">
          <VirtualClassList
            classVirtual={this.props.classVirtual}
            subjectList={this.state.subjectList}
            editClassShowModal={this.editClassShowModal}
          />
        </div>

        {this.state.addIsopen === true ? (
          <AddVirtualClass
            values={this.state}
            addIsopen={this.state.addIsopen}
            handleCancel={this.handleCancel}
            handleChangeDate={this.handleChangeDate}
            handleChange={this.handleChange}
            handleStartTimeChange={this.handleStartTimeChange}
            handleEndTimeChange={this.handleEndTimeChange}
            addVirtualClass={this.addVirtualClass}
            handleChangestudentSection={this.handleChangestudentSection}
            handleChangestudentLevel={this.handleChangestudentLevel}
            handleChangeClass={this.handleChangeClass}
            handleChangeSubject={this.handleChangeSubject}
            handleChangeProfessor={this.handleChangeProfessor}
          />
        ) : (
          ''
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    userProfile: state.auth.userProfile,
    classLevels: state.ClassLevels.remoteLevels,
    classSections: state.classSections.remoteSections,
    classes: state.classes,
    classVirtual: state.classVirtualReducer.remoteClassVirtual,
  };
}

export default connect(
  mapStateToProps,
  {
    addClassVirtual,
    getSections,
    getLevels,
    getClassesByEstablishmentId,
    getClassesVirtual,
    getClassVirtualByEstablishmentId
  }
)(VirtualClasses);
