import React from 'react';
import asyncComponent from '../../../util/asyncComponent';
import { Redirect, Route, Switch } from 'react-router-dom';
import Can from '../../../can';
import { RoleContext } from "../../../Context";

import Reporting from "./Reporting/Reporting";
import SettingsManagement from "./SettingsManagement/SettingsManagement";
import FinancialManagement from "./financialManagement/FinancialManagement"
import EstablishmentManagement from "./EstablishmentManagement/EstablishmentManagement"


const Superadmin = ({ match, estabModule }) => (

    <div className="app-wrapper">
        <Switch>
            <Redirect exact from={`${match.url}/`} to={`${match.url}/service`} />
            <Route path={`${match.url}/settings_management`} render={() =>
                <RoleContext.Consumer>
                    {({ role }) => (
                        < Can
                            role={role}
                            perform="module-nav-access"
                            data={{
                                mod: 'superadmin',
                                moduleList: estabModule
                            }}
                            yes={() => (
                                < Can
                                    role={role}
                                    perform={`module-nav-settings_management`}
                                    yes={() => (
                                        <SettingsManagement match={match} />
                                    )}
                                    no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                                />
                            )}
                            no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
                        />
                    )}
                </RoleContext.Consumer>
            } />

            <Route path={`${match.url}/establishment_management`} render={() =>
                <RoleContext.Consumer>
                    {({ role }) => (
                        < Can
                            role={role}
                            perform="module-nav-access"
                            data={{
                                mod: 'superadmin',
                                moduleList: estabModule
                            }}
                            yes={() => (
                                < Can
                                    role={role}
                                    perform={`module-nav-establishment_management`}
                                    yes={() => (
                                        < EstablishmentManagement match={match} />
                                    )}
                                    no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                                />
                            )}
                            no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
                        />
                    )}
                </RoleContext.Consumer>
            } />
            <Route path={`${match.url}/reporting`} render={() =>
                <RoleContext.Consumer>
                    {({ role }) => (
                        < Can
                            role={role}
                            perform="module-nav-access"
                            data={{
                                mod: 'superadmin',
                                moduleList: estabModule
                            }}
                            yes={() => (
                                < Can
                                    role={role}
                                    perform={`module-nav-reporting`}
                                    yes={() => (
                                        < Reporting match={match} />
                                    )}
                                    no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                                />
                            )}
                            no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
                        />
                    )}
                </RoleContext.Consumer>
            } />
             <Route path={`${match.url}/financial_management_superadmin`} render={() =>
                <RoleContext.Consumer>
                    {({ role }) => (
                        < Can
                            role={role}
                            perform="module-nav-access"
                            data={{
                                mod: 'superadmin',
                                moduleList: estabModule
                            }}
                            yes={() => (
                                < Can
                                    role={role}
                                    perform={`module-nav-financial_management_superadmin`}
                                    yes={() => (
                                        <FinancialManagement match={match} />
                                    )}
                                    no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                                />
                            )}
                            no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
                        />
                    )}
                </RoleContext.Consumer>
            } />
        </Switch>
    </div>
)





export default Superadmin;
