import React, { Component } from 'react';
import CardBox from '../../../../../components/CardBox/index';
import Select from 'react-select';
import IntlMessages from '../../../../../util/IntlMessages';
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import PermissionListItem from './PermissionListItem';
import TextField from '@material-ui/core/TextField';

export class PermissionList extends Component {
  render() {
    return (
      <div className="col-md-12 col-lg-12 col-sm-12 d-flex flex-column bd-highlight mb-3 ">
        <CardBox styleName="col-md-12 col-lg-12 col-sm-12">
          {/* <h1 style={{ color: '#0000CD', fontFamily: 'Roboto' }}>
            {' '}
            <IntlMessages id="sidebar.components.permissionSetting" />
          </h1>
          <TextField
            id="classLevel"
            name="classLevel"
            select
            // value={this.state.filterLevelId}
            // onChange={this.handleChangeFilter('filterLevelId')}
            SelectProps={{}}
            label={<IntlMessages id={`components.note.niveau`} />}
            InputProps={{ disableUnderline: true }}
            margin="normal"
            fullWidth
          ></TextField> */}
          <div className="d-flex p-2 bd-highlight ">
            <div className="d-flex flex-row bd-highlight mb-9 ">
              <div className="p-2 bd-highlight ml-4">
                <h1 style={{ color: '#0000CD', fontFamily: 'Roboto' }}>
                  {' '}
                  <IntlMessages id="setting.call.register" />
                </h1>
              </div>

              <TextField
                id="classLevel"
                name="classLevel"
                select
                // value={this.state.filterLevelId}
                // onChange={this.handleChangeFilter('filterLevelId')}
                SelectProps={{}}
                label={<IntlMessages id={`components.note.niveau`} />}
                InputProps={{ disableUnderline: true }}
                margin="normal"
                fullWidth
              ></TextField>
            </div>
          </div>
        </CardBox>
        <CardBox styleName="col-md-12 col-lg-12 col-sm-12">
          <div className="table-responsive-material">
            <Table>
              <TableHead className="th-border-b">
                <TableRow>
                  <TableCell> Action</TableCell>

                  <TableCell>Admin</TableCell>
                  <TableCell>Direction</TableCell>
                  <TableCell>Vie scolaire</TableCell>
                  <TableCell>Professeur</TableCell>
                  <TableCell>Élève</TableCell>
                  <TableCell>Parent</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {[1, 2, 3, 5, 5, 5, 5, 55, 5, 5, 5, 55, 5, 5].map((cours) => {
                  return <PermissionListItem />;
                })}
              </TableBody>
            </Table>
          </div>
        </CardBox>
        <div className="d-flex flex-row-reverse bd-highlight col-md-6 col-lg-6 col-sm-12">
          <div className=" bd-highlight ">
            <Button
              variant="contained"
              className="bg-secondary text-white "
              style={{
                borderBottomLeftRadius: '10px',
                borderBottomRightRadius: '10px',
                borderTopLeftRadius: '10px',
                borderTopRightRadius: '10px',
                width: '100%',
                height: '80%',
                textTransform: 'capitalize',
              }}
            >
              {true ? (
                <IntlMessages id="superadmin.apply.button" />
              ) : (
                <IntlMessages id="button.modify" />
              )}
            </Button>
          </div>
          <div className=" bd-highlight mr-2">
            <Button
              variant="contained"
              style={{
                borderBottomLeftRadius: '10px',
                borderBottomRightRadius: '10px',
                borderTopLeftRadius: '10px',
                borderTopRightRadius: '10px',
                width: '100%',
                height: '80%',
                textTransform: 'capitalize',
              }}
              className=" bg-grey text-white "
            >
              <IntlMessages id="components.classes.formadd.buttonCancel" />
            </Button>
          </div>
        </div>
      </div>
    );
  }
}

export default PermissionList;
