import React from "react";
import IntlMessages from "../../../../../util/IntlMessages";
import CardBox from "../../../../../components/CardBox/index";
import Can from "../../../../../can";
import { RoleContext } from "../../../../../Context";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import { connect } from "react-redux";
import InputLabel from "@material-ui/core/InputLabel";

class AddSchoolSession extends React.Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div>
        <RoleContext.Consumer>
          {({ role }) => (
            <Can
              role={role}
              perform="add-service"
              yes={() => (
                <div>
                  <form autoComplete="off" onSubmit={this.props.handleSubmit}>
                    <div className="d-flex justify-content-start align-items-center">
                      <h1>
                        <b>
                          <IntlMessages id="new.School.Session" />
                        </b>
                      </h1>
                      &nbsp;&nbsp;&nbsp;
                      <Fab
                        size="small"
                        color="primary"
                        aria-label="Add"
                        onClick={this.props.openAddModal}
                      >
                        {this.state.isOpen ? <RemoveSharpIcon /> : <AddIcon />}
                      </Fab>
                    </div>
                    <br />
                    {this.props.values.isOpen ? (
                      <>
                        {" "}
                        <CardBox styleName=" text-black  ">
                          <div className="d-flex flex-column  ">
                            <div className="d-flex flex-wrap  align-items-center ">
                              <div className="col-md-2 ">
                                <InputLabel htmlFor="newAllocation">
                                  <IntlMessages id="sidebar.components.schoolSession" />{" "}
                                  :
                                </InputLabel>
                              </div>

                              <div className="col-md-4   ">
                                <TextField
                                  required
                                  variant="outlined"
                                  id="nameSchoolSession"
                                  label={
                                    <IntlMessages id="schollSession.formadd.name" />
                                  }
                                  value={this.props.values.nameSchoolSession}
                                  onChange={this.props.handleChange(
                                    "nameSchoolSession"
                                  )}
                                  margin="normal"
                                  fullWidth
                                />
                              </div>
                              <div className="col-md-4   ">
                                <TextField
                                  required
                                  id="start_date"
                                  type="date"
                                  value={this.props.values.start_date}
                                  onChange={this.props.handleChange(
                                    "start_date"
                                  )}
                                  helperText={
                                    <IntlMessages id="components.class.formadd.startdate" />
                                  }
                                  margin="normal"
                                  fullWidth
                                />
                              </div>
                              <div className="col-md-2   "></div>

                              <div className="col-md-2   "></div>

                              <div className="col-md-4   ">
                                <TextField
                                  required
                                  id="end_date"
                                  type="date"
                                  value={this.props.values.end_date}
                                  onChange={this.props.handleChange("end_date")}
                                  helperText={
                                    <IntlMessages id="components.class.formadd.enddate" />
                                  }
                                  margin="normal"
                                  fullWidth
                                />
                              </div>
                            </div>
                          </div>
                        </CardBox>
                        <div className="d-flex flex-wrap justify-content-end ">
                          <Button
                            variant="contained"
                            onClick={this.props.openAddModal}
                            style={{
                              borderBottomLeftRadius: "16px",
                              borderBottomRightRadius: "16px",
                              borderTopLeftRadius: "16px",
                              borderTopRightRadius: "16px",
                              width: "10%",
                              height: "20%",
                            }}
                          >
                            {
                              <IntlMessages id="components.establishments.formadd.buttonCancel" />
                            }
                          </Button>
                          &nbsp;&nbsp;
                          <Button
                            variant="contained"
                            className="bg-grey text-white "
                            style={{
                              borderBottomLeftRadius: "16px",
                              borderBottomRightRadius: "16px",
                              borderTopLeftRadius: "16px",
                              borderTopRightRadius: "16px",
                              width: "10%",
                              height: "6%",
                            }}
                            onClick={this.props.handleArchive}
                          >
                            {<IntlMessages id="level.button.archive" />}
                          </Button>
                          &nbsp;&nbsp;
                          <Button
                            variant="contained"
                            style={{
                              borderBottomLeftRadius: "16px",
                              borderBottomRightRadius: "16px",
                              borderTopLeftRadius: "16px",
                              borderTopRightRadius: "16px",
                              width: "10%",
                              height: "6%",
                            }}
                            className=" bg-indigo text-white "
                            type="submit"
                          >
                            <IntlMessages id="service.button.publish" />
                          </Button>
                        </div>{" "}
                      </>
                    ) : (
                      ""
                    )}
                  </form>
                </div>
              )}
            />
          )}
        </RoleContext.Consumer>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
  };
};

export default connect(mapStateToProps)(AddSchoolSession);
