import React from "react";
import IntlMessages from "../../../../../util/IntlMessages";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import GetAppIcon from "@material-ui/icons/GetApp";
import { connect } from "react-redux";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/moment";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "react-select";
import AddBox from "@material-ui/icons/AddBox";
import Typography from "@material-ui/core/Typography";
import WcIcon from "@material-ui/icons/Wc";
import { Radio } from "@material-ui/core";
import CheckCircleIcon from "@material-ui/icons/CheckCircle";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
import PrintIcon from "@material-ui/icons/Print";
import PictureAsPdfIcon from "@material-ui/icons/PictureAsPdf";
import { getAllRole } from "../../../../../actions/usersAction";
import PhotoIcon from "@material-ui/icons/Photo";
import {
  roleIdSuperAdmin,
  roleIdAdmin,
  roleIdProfessor,
  roleIdStudent,
  roleIdDirector,
  roleIdParent,
  roleIdSupervisor,
} from "../../../../../config/config";
import {isEmail,isPhonenumber,isZipCode,isCIN,isValidphoneNumber} from "../../../../../constants/validationFunctions";
import MuiPhoneNumber from "material-ui-phone-number";
import { parsePhoneNumberFromString } from "libphonenumber-js";
import moment from "moment";

const fonctionList = [
  { label: "Agent d'entretien", id: 1, value: 1 },
  { label: "Infirmière", id: 2, value: 2 },
  { label: "Cuisinier(e)", id: 3, value: 3 },
  { label: "Gardien", id: 4, value: 4 },
  { label: "surveillant", id: 5, value: 5 },
  { label: "surveillant général", id: 6, value: 6 },
];
class AddUsers extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  isValidphoneNumber = (number) => {
    const tel = parsePhoneNumberFromString(number);
    let res = false;
    if (tel) {
      res = tel.isValid();
    }
    return res;
  };
  render() {
    const { values } = this.props;
    return (
      <div className="col-lg-12 col-md-12 col-sm-12 d-flex flex-wrap align-items-start">
        <div className="col-lg-12 col-md-12 col-sm-12 d-flex flex-row flex-wrap  justify-content-between align-items-center">
          <div className="d-flex flex-row ">
            <Fab
              size="small"
              color="primary"
              aria-label="Add"
              onClick={this.props.openAddModal}
            >
              {this.props.values.isOpen ? <RemoveSharpIcon /> : <AddIcon />}
            </Fab>
            &nbsp;&nbsp;&nbsp;
            <Typography
              variant="h6"
              style={{
                color: "black",
                fontWeight: "blod",
                fontFamily: "Roboto",
                fontSize: "25px",
              }}
            >
              <IntlMessages id="add.new.user" />
            </Typography>
          </div>
          &nbsp;&nbsp;&nbsp;
          <div className="d-flex flex-row">
            <Fab
              size="small"
              color="secondary"
              aria-label="Add"
              // onClick={this.props.openAddModal}
            >
              <GetAppIcon />
            </Fab>
            &nbsp;&nbsp;&nbsp;
            <Typography
              variant="h6"
              style={{
                color: "black",
                fontWeight: "blod",
                fontFamily: "Roboto",
                fontSize: "25px",
              }}
            >
              <IntlMessages id="upload.file.user" />
            </Typography>
          </div>
          &nbsp;&nbsp;&nbsp;
          <div className="d-flex flex-row ">
            <Fab
              size="small"
              aria-label="Add"
              style={{ backgroundColor: "#ffbb33", color: "#ffffff" }}
              onClick={this.props.openArchive}
            >
              <DeleteOutlineIcon />
            </Fab>
            &nbsp;&nbsp;&nbsp;
            <Typography
              variant="h6"
              style={{
                color: "black",
                fontWeight: "blod",
                fontFamily: "Roboto",
                fontSize: "25px",
              }}
            >
              <IntlMessages id="service.button.archives" />
            </Typography>
          </div>
          <div className="d-flex flex-row justify-content-between">
            &nbsp;&nbsp;&nbsp;
            <PrintIcon
              style={{
                fontSize: "35",
              }}
              color="inherit"
            />
            &nbsp;&nbsp;&nbsp;
            <PictureAsPdfIcon
              style={{
                fontSize: "35",
              }}
              color="inherit"
            />
          </div>
        </div>

        <form
          className="d-flex  flex-wrap col-lg-12 col-md-12 col-sm-12 p-4"
          autoComplete="off"
          onSubmit={this.props.handleSubmit}
        >
          {this.props.values.isOpen ? (
            <>
              <div className=" d-flex col-lg-12 col-md-12 col-sm-12 flex-row flex-wrap justify-content-around align-items-center">
                <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                  <InputLabel
                    htmlFor="nomSelect"
                    style={{
                      fontFamily: "Roboto",
                      fontSize: "18px",
                    }}
                    required
                  >
                    {<IntlMessages id="role.user" />}
                  </InputLabel>
                  <Select
                    required
                    options={this.props.values.listRoles}
                    onChange={this.props.handleChangeRole}
                    id="role"
                    name="role"
                    styles={{
                      control: (base) => ({
                        ...base,
                        "&:hover": { borderColor: "gray" }, // border style on hover
                        border: "1px solid lightgray", // default border color
                        boxShadow: "none", // no box-shadow
                        borderTopStyle: "none",
                        borderRightStyle: "none",
                        borderLeftStyle: "none",
                        borderRadius: " none",
                      }),
                    }}
                  />{" "}
                </div>
                <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                  <InputLabel
                    htmlFor="nomSelect"
                    style={{
                      fontFamily: "Roboto",
                      fontSize: "18px",
                    }}
                    required
                  >
                    {<IntlMessages id="school.year.user" />}
                  </InputLabel>
                  <Select
                    options={this.props.values.listSchoolYears}
                    onChange={this.props.handleChangeSchoolYear}
                    id="role"
                    name="role"
                    styles={{
                      control: (base) => ({
                        ...base,
                        "&:hover": { borderColor: "gray" }, // border style on hover
                        border: "1px solid lightgray", // default border color
                        boxShadow: "none", // no box-shadow
                        borderTopStyle: "none",
                        borderRightStyle: "none",
                        borderLeftStyle: "none",
                        borderRadius: " none",
                      }),
                    }}
                  />{" "}
                </div>
                {this.props.userProfile.role_id === roleIdSuperAdmin ? (
                  <>
                    <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                      <InputLabel
                        htmlFor="nomSelect"
                        style={{
                          fontFamily: "Roboto",
                          fontSize: "18px",
                        }}
                        required
                      >
                        {<IntlMessages id="user.establishment" />}
                      </InputLabel>
                      <Select
                        options={this.props.values.establishmentsList}
                        onChange={this.props.handleChangeEstablishments}
                        id="role"
                        name="role"
                        styles={{
                          control: (base) => ({
                            ...base,
                            "&:hover": { borderColor: "gray" }, // border style on hover
                            border: "1px solid lightgray", // default border color
                            boxShadow: "none", // no box-shadow
                            borderTopStyle: "none",
                            borderRightStyle: "none",
                            borderLeftStyle: "none",
                            borderRadius: " none",
                          }),
                        }}
                      />{" "}
                    </div>
                  </>
                ) : (
                  ""
                )}
                {this.props.values.roleId === roleIdStudent ? (
                  <>
                    <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                      <InputLabel
                        htmlFor="nomSelect"
                        style={{
                          fontFamily: "Roboto",
                          fontSize: "18px",
                        }}
                      >
                        {<IntlMessages id="parent.couplage.user" />}
                      </InputLabel>
                      <Select
                        // options={this.props.values.parentList}
                        // onChange={this.props.handleChangeParent}
                        id="role"
                        name="role"
                        styles={{
                          control: (base) => ({
                            ...base,
                            "&:hover": { borderColor: "gray" }, // border style on hover
                            border: "1px solid lightgray", // default border color
                            boxShadow: "none", // no box-shadow
                            borderTopStyle: "none",
                            borderRightStyle: "none",
                            borderLeftStyle: "none",
                            borderRadius: " none",
                          }),
                        }}
                      />{" "}
                    </div>
                    <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                      <InputLabel
                        htmlFor="nomSelect"
                        style={{
                          fontFamily: "Roboto",
                          fontSize: "18px",
                        }}
                        required
                      >
                        {<IntlMessages id="classe.couplage.user" />}
                      </InputLabel>
                      <Select
                        options={this.props.values.classForStudent}
                        onChange={this.props.handleChangeStudentClass}
                        id="nomSelect"
                        styles={{
                          control: (base) => ({
                            ...base,
                            "&:hover": { borderColor: "gray" }, // border style on hover
                            border: "1px solid lightgray", // default border color
                            boxShadow: "none", // no box-shadow
                            borderTopStyle: "none",
                            borderRightStyle: "none",
                            borderLeftStyle: "none",
                            borderRadius: " none",
                          }),
                        }}
                      />{" "}
                    </div>
                  </>
                ) : (
                  ""
                )}
                {this.props.values.roleId === roleIdProfessor ? (
                  <>
                    <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                      <InputLabel
                        htmlFor="nomSelect"
                        style={{
                          fontFamily: "Roboto",
                          fontSize: "18px",
                        }}
                        required
                      >
                        {<IntlMessages id="prof.couplage.class" />}
                      </InputLabel>
                      <Select
                        options={this.props.values.classRoomList}
                        onChange={this.props.handleChangeClassRoom}
                        id="nomSelect"
                        styles={{
                          control: (base) => ({
                            ...base,
                            "&:hover": { borderColor: "gray" }, // border style on hover
                            border: "1px solid lightgray", // default border color
                            boxShadow: "none", // no box-shadow
                            borderTopStyle: "none",
                            borderRightStyle: "none",
                            borderLeftStyle: "none",
                            borderRadius: " none",
                          }),
                        }}
                      />{" "}
                    </div>
                    <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                      <InputLabel
                        htmlFor="nomSelect"
                        style={{
                          fontFamily: "Roboto",
                          fontSize: "18px",
                        }}
                        required
                      >
                        {<IntlMessages id="prof.couplage.subjects" />}
                      </InputLabel>
                      <Select
                        options={this.props.values.subjectsList}
                        onChange={this.props.handleChangeSubject}
                        value={this.props.values.subjectItem}
                        id="nomSelect"
                        styles={{
                          control: (base) => ({
                            ...base,
                            "&:hover": { borderColor: "gray" }, // border style on hover
                            border: "1px solid lightgray", // default border color
                            boxShadow: "none", // no box-shadow
                            borderTopStyle: "none",
                            borderRightStyle: "none",
                            borderLeftStyle: "none",
                            borderRadius: " none",
                          }),
                        }}
                      />{" "}
                    </div>
                  </>
                ) : (
                  ""
                )}
                {this.props.values.roleId === roleIdParent ? (
                  <>
                    <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                      <InputLabel
                        htmlFor="nomSelect"
                        style={{
                          fontFamily: "Roboto",
                          fontSize: "18px",
                        }}
                      >
                        {<IntlMessages id="parent.couplage.student" />}
                      </InputLabel>
                      <Select
                        // options={this.props.values.subjectsList}
                        // onChange={this.props.handleChangeStudent}
                        id="studentIdAssignement"
                        name="studentIdAssignement"
                        styles={{
                          control: (base) => ({
                            ...base,
                            "&:hover": { borderColor: "gray" }, // border style on hover
                            border: "1px solid lightgray", // default border color
                            boxShadow: "none", // no box-shadow
                            borderTopStyle: "none",
                            borderRightStyle: "none",
                            borderLeftStyle: "none",
                            borderRadius: " none",
                          }),
                        }}
                      />{" "}
                    </div>
                  </>
                ) : (
                  ""
                )}
                {this.props.values.roleId === roleIdSupervisor ? (
                  <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                    <InputLabel
                      htmlFor="nomSelect"
                      style={{
                        fontFamily: "Roboto",
                        fontSize: "18px",
                      }}
                    >
                      {<IntlMessages id="vie.scolaire.fonction" />}
                    </InputLabel>
                    <Select
                      options={fonctionList}
                      onChange={this.props.handleChangeFunctions}
                      id="function"
                      name="function"
                      styles={{
                        control: (base) => ({
                          ...base,
                          "&:hover": { borderColor: "gray" }, // border style on hover
                          border: "1px solid lightgray", // default border color
                          boxShadow: "none", // no box-shadow
                          borderTopStyle: "none",
                          borderRightStyle: "none",
                          borderLeftStyle: "none",
                          borderRadius: " none",
                        }),
                      }}
                    />{" "}
                  </div>
                ) : (
                  ""
                )}
                <hr
                  style={{
                    width: "100%",
                    margin: "auto",
                    marginTop: "40px",
                    marginBottom: "10px",
                    border: "1px dashed #979A9A",
                    paddingLeft: "-100%",
                  }}
                />
              </div>

              <div className="d-flex col-lg-12 col-md-12 col-sm-12 flex-row flex-wrap justify-content-around align-items-center pt-3">
                <div className="col-md-6 col-lg-2 col-sm-12 p-1">
                  <InputLabel
                    style={{
                      fontFamily: "Roboto",
                      fontSize: "18px",
                      marginTop: "-2%",
                    }}
                    required
                  >
                    {<IntlMessages id="user.name" />}
                  </InputLabel>
                  <TextField
                    required
                    // error={values.nameError}
                    id="userName"
                    name="userName"
                    value={values.userName || ""}
                    onChange={this.props.handleChange("userName")}
                    style={{
                      marginTop: "3%",
                    }}
                    fullWidth
                    SelectProps={{
                      native: true,
                    }}
                  />
                  {/* <FormHelperText
                    error={values.nameError}
                    >
                      {values.nameError ? 'Nom de support de cours déja existe' : ''}
                    </FormHelperText> */}
                </div>
                <div className="col-md-6 col-lg-2 col-sm-12 p-1">
                  <InputLabel
                    style={{
                      fontFamily: "Roboto",
                      fontSize: "18px",
                      marginTop: "-2%",
                    }}
                    required
                  >
                    {<IntlMessages id="user.last.name" />}
                  </InputLabel>
                  <TextField
                    required
                    // error={values.nameError}
                    id="userLastName"
                    name="userLastName"
                    value={values.userLastName || ""}
                    onChange={this.props.handleChange("userLastName")}
                    style={{
                      marginTop: "3%",
                    }}
                    fullWidth
                    SelectProps={{
                      native: true,
                    }}
                  />
                  {/* <FormHelperText
                    error={values.nameError}
                    >
                      {values.nameError ? 'Nom de support de cours déja existe' : ''}
                    </FormHelperText> */}
                </div>
                <div className="col-md-6 col-lg-2 col-sm-12 p-0 d-flex justify-content-center">
                  <div className="col-md-2 p-1 d-flex justify-content-center align-items-end ">
                    <Radio
                      checked={values.userGender == "Male"}
                      onChange={this.props.handleChange("userGender")}
                      value="Male"
                      color="primary"
                      name="radio-button-demo"
                      inputProps={{ "aria-label": "D" }}
                    />
                    <WcIcon color="primary" style={{ fontSize: 60 }} />
                    <Radio
                      checked={values.userGender == "Female"}
                      onChange={this.props.handleChange("userGender")}
                      value="Female"
                      color="primary"
                      name="radio-button-demo"
                      inputProps={{ "aria-label": "D" }}
                    />
                  </div>
                </div>
                <div className="col-md-6 col-lg-2 col-sm-12 p-2">
                  <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <KeyboardDatePicker
                      label={
                        <InputLabel
                          style={{
                            backgroundColor: "white",
                            fontFamily: "Roboto",
                            fontSize: "30px",
                            marginTop: "-16px",
                            width: "300px",
                          }}
                          required
                        >
