            <NavLink className="prepend-icon" to="/app/home">
              <span className="nav-text" ><IntlMessages id="sidebar.home" /></span>
            </NavLink>
          </li>
          <li className="nav-item">
            <MenuCollapseBoxItem
              pathName={'super_administration'}
