import React, { Component } from 'react';
import CardBox from '../../../../../components/CardBox/index';
import Select from 'react-select';
import IntlMessages from '../../../../../util/IntlMessages';
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import PermissionListItem from './PermissionListItem';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import ResetPermission from './ResetPermission';

export class PermissionList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listPermissions: [],
      listPermissionsModified: [],
      modalReset: false,
    };

    this.addPermission = this.addPermission.bind(this);
    this.deletePermission = this.deletePermission.bind(this);
    this.resetPermission = this.resetPermission.bind(this);
    this.applyPermission = this.applyPermission.bind(this);
  }
  componentDidMount() {
    this.setState({ listPermissions: this.props.listPermissions });
  }
  componentDidUpdate(prevProps) {
    if (prevProps.listPermissions !== this.props.listPermissions) {
      this.setState({ listPermissions: this.props.listPermissions });
    }
  }
  applyPermission() {
    let listPermissionsModified = this.state.listPermissions.filter(
      (permission) => permission.modified
    );
    console.log(listPermissionsModified, 'listPermissionsModified');
    this.setState({ listPermissionsModified });
  }
  resetPermission() {
    this.setState({ listPermissions: this.props.listPermissions, listPermissionsModified: [] });
  }
  deletePermission(idPermission, idRole) {
    let newListPermissions = this.state.listPermissions.map((permission) => {
      let newPermission =
        permission.id === parseInt(idPermission)
          ? {
              ...permission,
              role: permission.role.filter((role) => role != idRole),
              modified: true,
            }
          : permission;
      return newPermission;
    });
    this.setState({
      listPermissions: newListPermissions,
    });
  }
  addPermission(idPermission, idRole) {
    let newListPermissions = this.state.listPermissions.map((permission) => {
      let newPermission =
        permission.id === parseInt(idPermission)
          ? {
              ...permission,
              role: permission.role.concat([idRole]),
              modified: true,
            }
          : permission;
      return newPermission;
    });
    this.setState({
      listPermissions: newListPermissions,
    });
  }
  render() {
    const { values } = this.props;
    // console.log(values, 'valuuues');
    console.log(this.state.listPermissions, 'stateeeee');
    console.log(this.state.listPermissionsModified, 'listPermissionsModified');

    return (
      <div className="col-md-12 col-lg-12 col-sm-12 d-flex flex-column bd-highlight mb-3 ">
        <ResetPermission
          modal={this.state.modalReset}
          handleCancelNotif={this.resetPermission}
          handleSubmitNotif={this.handleSubmitNotif}
        ></ResetPermission>
        <CardBox styleName="col-md-12 col-lg-12 col-sm-12">
          <div className="d-flex flex-row bd-highlight  ">
            <div className="p-3 col-md-6 col-lg-6 col-sm-6  mt-4">
              <h1 style={{ color: '#0000CD', fontFamily: 'Roboto' }}>
                {' '}
                <IntlMessages id="sidebar.components.permissionSetting" />
              </h1>
            </div>
            <div className="p-2 col-md-2 col-lg-2 col-sm-2    ">
              <TextField
                id="moduleId"
                name="moduleId"
                select
                value={values.moduleId}
                onChange={this.props.handleChange('moduleId')}
                SelectProps={{}}
                label={<IntlMessages id={`sidebar.modules`} />}
                InputProps={{ disableUnderline: true }}
                margin="normal"
                fullWidth
              >
                <MenuItem key={0} value={0}>
                  <IntlMessages id={`userStuppDisplay.all`} />
                </MenuItem>
                {this.props.estabModule.map((element, index) => (
                  <MenuItem key={index} value={element.module.id}>
                    {element.module.name}
                    {/* <IntlMessages id={`sidebar.components.${element.module.path}`} /> */}
                  </MenuItem>
                ))}
              </TextField>
            </div>
            <div className="p-2 col-md-2 col-lg-2 col-sm-2">
              <TextField
                id="subModuleId"
                name="subModuleId"
                select
                value={values.subModuleId}
                onChange={this.props.handleChange('subModuleId')}
                SelectProps={{}}
                label={<IntlMessages id={`sidebar.modules`} />}
                InputProps={{ disableUnderline: true }}
                margin="normal"
                fullWidth
              >
                {values.subModuleList.map((subModule) => (
                  <MenuItem key={subModule.id} value={subModule.id}>
                    {subModule.name}
                    {/* <IntlMessages id={`sidebar.components.${subModule.name}`} /> */}
                  </MenuItem>
                ))}
              </TextField>
            </div>
          </div>
        </CardBox>
        <CardBox styleName="col-md-12 col-lg-12 col-sm-12">
          <div className="table-responsive-material">
            <Table>
              <TableHead className="th-border-b">
                <TableRow>
                  <TableCell>
                    {' '}
                    <IntlMessages id={`action.type.of.education`} />
                  </TableCell>

                  <TableCell>
                    <IntlMessages id={`role.admin`} />
                  </TableCell>
                  <TableCell>
                    {' '}
                    <IntlMessages id={`component.etablishments.info.director`} />
                  </TableCell>
                  <TableCell>
                    {' '}
                    <IntlMessages id={`role.Supervisor`} />
                  </TableCell>
                  <TableCell>
                    {' '}
                    <IntlMessages id={`toDo.professor`} />
                  </TableCell>
                  <TableCell>
                    {' '}
                    <IntlMessages id={`userStuppDisplay.Student`} />
                  </TableCell>
                  <TableCell>
                    {' '}
                    <IntlMessages id={`userStuppDisplay.Parent`} />
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {this.state.listPermissions.map((element, index) => {
                  return (
                    <PermissionListItem
                      key={index}
                      permission={element}
                      addPermission={this.addPermission}
                      deletePermission={this.deletePermission}
                    />
                  );
                })}
              </TableBody>
            </Table>
          </div>
        </CardBox>
        <div className="d-flex flex-row-reverse bd-highlight col-md-6 col-lg-6 col-sm-12">
          <div className=" bd-highlight ">
            <Button
              variant="contained"
              className="bg-secondary text-white "
              style={{
                borderBottomLeftRadius: '10px',
                borderBottomRightRadius: '10px',
                borderTopLeftRadius: '10px',
                borderTopRightRadius: '10px',
                width: '100%',
                height: '80%',
                textTransform: 'capitalize',
              }}
              onClick={this.applyPermission}
            >
              {true ? (
                <IntlMessages id="superadmin.apply.button" />
              ) : (
                <IntlMessages id="button.modify" />
              )}
            </Button>
          </div>
          <div className=" bd-highlight mr-2">
            <Button
              variant="contained"
              style={{
                borderBottomLeftRadius: '10px',
                borderBottomRightRadius: '10px',
                borderTopLeftRadius: '10px',
                borderTopRightRadius: '10px',
                width: '100%',
                height: '80%',
                textTransform: 'capitalize',
              }}
              className=" bg-grey text-white "
              onClick={() => this.setState({ modalReset: true })}
            >
              <IntlMessages id="components.classes.formadd.buttonCancel" />
            </Button>
          </div>
        </div>
      </div>
    );
  }
}

export default PermissionList;
