import React, { Component } from "react";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import IntlMessages from "../../../../../util/IntlMessages";
import PlayForWorkIcon from "@material-ui/icons/PlayForWork";
import imageStudent from "../../../../../app/routes/Administration/routes/StuppUser/imageUser/supAdmin.png";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import axios from "axios";
import baseUrl from "../../../../../config/config";
import _ from "lodash";
import jsPDF from "jspdf";
import { Link } from "react-router-dom";
import "jspdf-autotable";
import { url } from "./photo";
import EditAllocationService from "./EditAllocationService";
import DeleteAllcation from "./DeleteAllocation";
import { connect } from "react-redux";
import { getAllocationServiceByEstablishment } from "../../../../../actions/AllocationServiceAction";
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
  textColorPrimary: {
    color: "#FFFFFF",
  }
})

class ServiceItemList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      value: 0,
      uri: "",
      openEdit: false,
      allocationItem: {},
      openDelete: false,
    };
    this.handleEdit = this.handleEdit.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
  }

  handleDelete() {
    this.setState({ openDelete: true });
  }

  handleCancel() {
    this.setState({ openEdit: false });
    this.props.dispatch(
      getAllocationServiceByEstablishment(
        this.props.userProfile.establishment_id
      )
    );
  }

  handleEdit() {
    this.setState({ openEdit: true, allocationItem: this.props.item });
  }

  handleChange = (event, value) => {
    this.setState({ value });
  };
  handleDeleteClose = () => {
    this.setState({ openDelete: false });
  };

  render() {
    const { classes } = this.props;
    const { value, openEdit } = this.state;
    let {
      name,
      surname,
      name_ar,
      surname_ar,
      level,
      services,
    } = this.props.item;

    return (
      <div className=" row col-lg-12 col-md-12 pt-2 d-flex justify-content-center alignement-items-center">
        <div
          className="d-flex justify-content-center"
          style={{ marginTop: "7px" }}
        >
          {this.state.uri ? (
            <Avatar
              align="left"
              className="size-50"
              alt="..."
              src={this.state.uri}
            />
          ) : (
            <Avatar
              align="left"
              className="size-50"
              alt="..."
              src="https://pngimage.net/wp-content/uploads/2018/05/admin-avatar-png-1.png"
            />
          )}
        </div>


        <div className="d-flex flex-row col-lg-8 col-md-8 justify-content-start align-items-center " >

          <AppBar
            position="relative"
            style={{
              borderTopRightRadius: "80px",
              borderBottomRightRadius: "80px",
              borderTopLeftRadius: "80px",
              borderBottomLeftRadius: "80px",
              border: "1px solid #abada8",
              height: "100%",
              backgroundColor: "#4a3ea3",

            }}
          >
            <Tabs
              value={false}
              onChange={this.handleChange}
              variant="scrollable"
              scrollButtons="on"
              textColor="primary"
            >
              <Tab
                style={{ width: "45%", textColor: "#FFFFFF", marginTop: "10px" }}
                icon={

                  <div style={{ textAlign: "left" }} className={classes.textColorPrimary} >
                    {this.props.languageId == "tunisia" ? (
                      <h3>
                        {name_ar} {surname_ar}{" "}
                      </h3>
                    ) : (
                        <h3 >
                          {surname} {name}
                        </h3>
                      )}
                  </div>
                }
              />
              <Tab
                style={{ backgroundColor: "#FFFFFF" }}
                icon={
                  <div >
                    <h3 style={{ marginBottom: "0px", fontSize: "15px" }}>{level.name_FR}</h3>
                  </div>
                }
              />
              <Tab
                style={{ backgroundColor: "#FFFFFF", minWidth: "10px" }}
                icon={
                  <div
                    style={{
                      borderWidth: "thin",
                      borderStyle: "solid",
                      borderColor: "#979A9A",
                      height: "30px",
                    }}

                  ></div>
                }
              />
              <Tab
                style={{ backgroundColor: "#FFFFFF", minWidth: "290px" }}
                icon={
                  <div >
                    {services.map((element, index) => {
                      return (
                        <i
                          key={index}
                          className={`${element.service_v2.path_img_service}`}
                        ></i>
                      );
                    })}
                  </div>
                }
              />

              <Tab
                style={{ backgroundColor: "#FFFFFF", minWidth: "10px" }}
                icon={
                  <div
                    className="d-flex"
                    style={{
                      borderWidth: "thin",
                      borderStyle: "solid",
                      borderColor: "#979A9A",
                      height: "30px",
                    }}
                  ></div>
                }
              />

              <Tab
                style={{ backgroundColor: "#FFFFFF" }}
                icon={
                  <div>
                    <i
                      className="zmdi zmdi-edit zmdi-hc-2x"
                      style={{
                        color: "text-grey",
                        marginRight: "5px",
                        marginLeft: "5px",
                      }}
                      onClick={this.handleEdit}
                    />
                    <i
                      className="zmdi zmdi-eye zmdi-hc-2x"
                      style={{
                        color: "text-grey",
                        marginRight: "5px",
                        marginLeft: "5px",
                      }}
                    />

                    <i
                      className="zmdi zmdi-delete zmdi-hc-2x"
                      style={{
                        color: "text-grey",
                        marginRight: "5px",
                        marginLeft: "5px",
                      }}
                      onClick={this.handleDelete}
                    />
                  </div>
                }
              />
            </Tabs>
          </AppBar>
        </div>
        {this.state.openEdit ? (
          <EditAllocationService
            openEdit={this.state.openEdit}
            cancel={this.handleCancel}
            services={this.props.services}
            allocationItem={this.state.allocationItem}
          />
        ) : (
            ""
          )}
        {this.state.openDelete ? (
          <DeleteAllcation
            cancelModalDelete={this.handleDeleteClose.bind(this)}
            item={this.props.item}
          />
        ) : (
            ""
          )}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,

    services: state.service.servicesV2,
  };
};

export default withStyles(styles)(connect(mapStateToProps)(ServiceItemList));
