import React from 'react';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import IntlMessages from '../../../../../util/IntlMessages';
import StepContent from '@material-ui/core/StepContent';
import Paper from '@material-ui/core/Paper';
import MenuItem from '@material-ui/core/MenuItem';
import Input from '@material-ui/core/Input';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';
import { connect } from "react-redux";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Chip from '@material-ui/core/Chip';
import Avatar from '@material-ui/core/Avatar';
import { getLevelListFromEstabType } from "../../../../../actions/classLevelAction";
import { getSectionFromLevel } from "../../../../../actions/sectionAction";
import { subjectsByLevelBySection } from "../../../../../actions/subjectAction";
import { getEstablishment } from "../../../../../actions/establishmentAction";
import _ from 'lodash';
import { List } from '@material-ui/core';
import FormControl from '@material-ui/core/FormControl';
import Auxiliary from "../../../../../util/Auxiliary";
import moment from 'moment';
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import { updateClass } from "../../../../../actions/classeAction";
import { classService } from "../../../../../_services";
import CircularFab from './CircularFab'
import FormHelperText from '@material-ui/core/FormHelperText';


const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const roleIdSuperAdmin = 1;
function jsUpperCaseFirst(string) {
  return string.charAt(0).toUpperCase() + string.slice(1);
};
class EditClass extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      activeStep: 0,
      steps: [<IntlMessages id="component.classe.info.general" />, <IntlMessages id="component.chips.list.professors" />,
      <IntlMessages id="component.students.list" />],
      professorListBySubjectId: [],
      students: [],
      affectedStudentsIds: [],
      affectedStudentsIds: [],
      professors: [],
      professorsData: [],
      firstListProfileId: [],
      name: '',
      level_id: 0,
      section_id: 0,
      establishment: '',
      capacity: '',
      start_date: '',
      end_date: '',
      description: '',
      classNumber: '',
      id: '',
      students_number: '',
      finalListOfProfessor: [],
      subjectListByEstablishmentId: [],
      studentsListByEstablishment: [],
      professorsFiltredByEstablishment: [],
      fetchStudentByEstablishmentId: [],
      allStudentsList: [],
      previewVisible: true,
      estabType: 0,
      sectionDisabled: false,
      ar_name: '',
      studentsNumberError:false
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeStudents = this.handleChangeStudents.bind(this);
    this.handleChangeProfessors = this.handleChangeProfessors.bind(this);
    this.handleChangeLevel = this.handleChangeLevel.bind(this);
    this.handleRequestDelete = this.handleRequestDelete.bind(this);
    this.handleRequestDeleteProfessor = this.handleRequestDeleteProfessor.bind(this);
    this.handleChangeEstablishment = this.handleChangeEstablishment.bind(this)
  };

  handleCancel(e) {
    this.props.cancelModal()
  };
  handleRequestDelete = data => () => {
    const chipData = [...this.state.students];
    const chipToDelete = chipData.indexOf(data);
    chipData.splice(chipToDelete, 1);
    this.setState({ students: chipData });
  };

  handleRequestDeleteProfessor = data => () => {
    const chipData = [...this.state.professors];
    const chipToDelete = chipData.indexOf(data);
    chipData.splice(chipToDelete, 1);
    this.setState({ professors: chipData });
  };

  handleNext = () => {
    const { activeStep } = this.state;
    this.setState({
      activeStep: activeStep + 1,
    });
  };

  handleBack = () => {
    const { activeStep } = this.state;
    this.setState({
      activeStep: activeStep - 1,
    });
  };

  handleReset = () => {
    this.setState({
      activeStep: 0,
    });
  };

  handleChangeStudents = event => {
    this.setState({
      students: event.target.value,
      affectedStudentsIds: event.target.value
    });
  };

  handleChangeProfessors = event => {
    let obj = JSON.parse(event.target.value);
    let oldArray = this.state.professors.filter(item => item.subject_id !== obj.subject_id)
    if (!(_.isEmpty(obj))) {
      this.setState({ professors: [...oldArray, obj] });
    }
  };

  handleChangeLevel = event => {
    (event.target.value > 10) ? this.setState({ level_id: event.target.value, sectionDisabled: false }) : this.setState({ level_id: event.target.value, sectionDisabled: true });

  };

  Validation_Steps() {
    if (this.state.activeStep === 0) {
      if ( parseInt(this.state.capacity) > parseInt(this.state.students_number) )
      return true;
      else return false;     
    }else{
      return true;
    }
  }

  handleChange = name => event => {
    if (name === 'students_number') {
      let studentsNumberError = false;
      studentsNumberError = parseInt(this.state.capacity) < parseInt(event.target.value.trim())
      this.setState({ studentsNumberError })
    }
    this.setState({ [name]: event.target.value });
  };

  handleChangeEstablishment = name => event => {
    this.setState({ [name]: event.target.value });
    let estabItem = this.props.establishments.find(({ id }) => (id === event.target.value))
    this.setState({
      estabType: estabItem.estab_type_id
    });
    let apiEndpoint = `/students/fetchStudentByEstablishmentId/` + event.target.value + `?access_token=${localStorage.token}`;
    classService.get(apiEndpoint)

      .then(res => {
        this.setState({ fetchStudentByEstablishmentId: res.data.students })
      }).catch(error => {
        console.log(error);
      });

    let apiEndpoint2 = `/professors/fetchProfessorByEstablishmentId/` + event.target.value + `?access_token=${localStorage.token}`;
    classService.get(apiEndpoint2)

      .then(res => {
        this.setState({ professorsFiltredByEstablishment: res.data.professors })
      }).catch(error => {
        console.log(error);
      });


  }

  componentDidMount() {
    const classId = this.props.classItem.id;
    const classLevel = this.state.level_id
    let studentList = this.props.students.filter(element => element.class_id === classId)
    let studentsId = _.map(studentList, 'id');
    this.setState({ students: studentsId })
    let apiEndpoint = `/classes_professors/getProfessorByClassId/` + classId + `?access_token=${localStorage.token}`;
    classService.get(apiEndpoint)
      .then(response => {
        let result = response.data.professor;
        let professorId = _.map(result, 'id');
        this.setState({ professorsData: result, firstListProfileId: professorId })
      }).catch(error => { console.log(error); });
    let apiEndpoint2 = `/students/fetchStudentByEstablishmentId/` + this.props.classItem.establishment_id + `?access_token=${localStorage.token}`;
    classService.get(apiEndpoint2)
      .then(res => {
        let studentList2 = res.data.students.filter(element => element.class_id === classId)
        this.setState({
          allStudentsList: res.data.students
        })
        let IDs = _.map(studentList2, 'id');
        var noAffectedStudentList = res.data.students.filter(student => student.class_id === null && student.level_id === this.props.classItem.level_id)
        var noAffectedStudentListID = _.map(noAffectedStudentList, 'id');
        var AffectedStudentList = studentList2.filter(student => student.class_id !== null)
        var AffectedStudentListID = _.map(AffectedStudentList, 'id');
        this.setState({ affectedStudentsIds: AffectedStudentListID });
        this.setState({ fetchStudentByEstablishmentId: noAffectedStudentList })
      }).catch(error => { console.log(error); });

    let apiEndpoint3 = `/professors/fetchProfessorByEstablishmentId/` + this.props.classItem.establishment_id + `?access_token=${localStorage.token}`;
    classService.get(apiEndpoint3)
      .then(res => {
        this.setState({ professorsFiltredByEstablishment: res.data.professors })
      }).catch(error => { console.log(error); });



    let startDate = moment(this.props.classItem.start_date).format('MM-DD-YYYY');
    let endDate = moment(this.props.classItem.end_date).format('MM-DD-YYYY');
    startDate = new Date(startDate).toISOString().substr(0, 10);
    endDate = new Date(endDate).toISOString().substr(0, 10);
    let estabItem = this.props.establishments.find(({ id }) => (id === this.props.classItem.establishment_id))
    this.setState({
      id: this.props.classItem.id,
      name: this.props.classItem.name,
      level_id: this.props.classItem.level_id,
      section_id: this.props.classItem.section_id,
      establishment: this.props.classItem.establishment_id,
      estabType: estabItem.estab_type_id,
      capacity: this.props.classItem.capacity,
      start_date: startDate,
      end_date: endDate,
      description: this.props.classItem.description,
      students_number: this.props.classItem.students_number,
      ar_name: this.props.classItem.ar_name
    })
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      id: this.state.id,
      name: this.state.name,
      level_id: this.state.level_id,
      section_id: this.state.section_id,
      capacity: this.state.capacity,
      students_number: this.state.students_number,
      description: this.state.description,
      start_date: this.state.start_date,
      end_date: this.state.end_date,
      professor_id: this.state.professors,
      student_id: this.state.students,
      establishment_id: this.state.establishment,
      last_professor_id: this.state.firstListProfileId,
      ar_name: this.state.ar_name
    }
    this.props.updateClass(data);
    this.props.cancelModal();
  };
  getStepContent(step) {
    switch (step) {
      case 0:
        return <div>
          <div className="row">
            {this.props.userProfile.role_id === roleIdSuperAdmin ? <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  name='establishment'
                  id="establishment"
                  select
                  label={<IntlMessages id="components.student.formadd.establishment" />}
                  value={this.state.establishment}
                  onChange={this.handleChangeEstablishment('establishment')}
                  onClick={this.getEstabType}
                  SelectProps={{}}
                  margin="normal"
                  fullWidth >
                  {this.props.establishments.map(establishment => (
                    <MenuItem key={establishment.id} value={establishment.id}>
                      {establishment.name}
                    </MenuItem>
                  ))}
                </TextField>
              </div>
            </div> : ''}
            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  id="level_id"
                  name="level_id"
                  select
                  label={<IntlMessages id="components.note.niveau" />}
                  value={this.state.level_id}
                  onChange={this.handleChangeLevel}
                  SelectProps={{}}
                  margin="normal"
                  fullWidth >
                  {getLevelListFromEstabType(this.props.classLevels, this.state.estabType).map(option => (
                    <MenuItem key={option.id} value={option.id}>
                      {option.name}
                    </MenuItem>
                  ))}
                </TextField>
              </div>
            </div>
            <div className="col-md-6">
              <div >
                <TextField
                  disabled={this.state.sectionDisabled}
                  id="section_id"
                  name="section_id"
                  select
                  label={<IntlMessages id="components.class.level.input.label.section" />}
                  value={this.state.section_id}
                  onChange={this.handleChange('section_id')}
                  SelectProps={{}}
                  margin="normal"
                  fullWidth >
                  {getSectionFromLevel(this.props.classSections, this.state.level_id).map(option => (
                    <MenuItem key={option.id} value={option.id}>
                      {option.name}
                    </MenuItem>
                  ))}
                </TextField>
              </div>
            </div>
            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  name='name'
                  id="name"
                  value={this.state.name}
                  onChange={this.handleChange('name')}
                  label={<IntlMessages id="components.class.formadd.name" />}
                  margin="normal"
                  fullWidth
                />
              </div>
            </div>
            {_.head(this.props.country).locale === 'ar' ? <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  name='ar_name'
                  id="ar_name"
                  label={<IntlMessages id="components.establishments.formadd.ar_name" />}
                  onChange={this.handleChange('ar_name')}
                  margin="normal"
                  fullWidth
                  value={this.state.ar_name}
                />
              </div>
            </div> : ''}



            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  name='capacity'
                  id="capacity"
                  value={this.state.capacity}
                  onChange={this.handleChange('capacity')}
                  label={<IntlMessages id="components.class.formadd.capacity" />}
                  type="number"
                  margin="normal"
                  fullWidth
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  name='students_number'
                  id="students_number"
                  value={this.state.students_number}
                  onChange={this.handleChange('students_number')}
                  label={<IntlMessages id="components.establishments.formadd.number_students" />}
                  type="number"
                  margin="normal"
                  fullWidth
                />
                  <FormHelperText error={this.state.studentsNumberError}>
                    {this.state.studentsNumberError
                      ? <IntlMessages id="message.error.number.class" />
                      : ''}
                  </FormHelperText>
              </div>
            </div>

            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  name='description'
                  id="description"
                  value={this.state.description}
                  onChange={this.handleChange('description')}
                  label={<IntlMessages id="room.description" />}
                  margin="normal"
                  fullWidth
                />
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  id="start_date"
                  type="date"
                  value={this.state.start_date}
                  onChange={this.handleChange('start_date')}
                  helperText={<IntlMessages id="components.class.formadd.startdate" />}
                  margin="normal"
                  fullWidth
                />
              </div>
            </div>
            <div className="col-md-6">
              <div className="form-group">
                <TextField
                  required
                  id="end_date"
                  type="date"
                  value={this.state.end_date}
                  onChange={this.handleChange('end_date')}
                  helperText={<IntlMessages id="components.class.formadd.enddate" />}
                  margin="normal"
                  fullWidth
                />
              </div>
            </div>

          </div>
        </div >;
      case 1:
        return <div className="row">
          {(() => {
            if (this.props.subjects) {
              let listSubjectsFiltered = subjectsByLevelBySection(this.props.subjects, this.state.level_id, this.state.section_id)
              if (listSubjectsFiltered.length) {
                return (
                  <Table className="default-table table-unbordered table table-sm table-hover">
                    <TableBody>
                      {listSubjectsFiltered.map((element, index) => {
                        let professorId = `professor-${index}`;
                        return (
                          <TableRow key={index}>
                            <TableCell>
                              <label htmlFor="pet-select"> {index + 1}- {<IntlMessages id="components.note.subject" />} : {element.name} </label>
                            </TableCell>
                            <TableCell>
                              {this.state.professorsData.map(professor => {
                                let element2;
                                if (professor.subject_id == element.id) {
                                  element2 = professor.professor.profile.user.surname.toUpperCase() + " " + jsUpperCaseFirst(professor.professor.profile.user.name)
                                }
                                return (<label htmlFor="pet-select" > {element2}</label>)

                              })
                              }

                            </TableCell>

                            <TableCell>
                              <select
                                name={professorId}
                                className="professor"
                                onChange={this.handleChangeProfessors}
                              >
                                <option >Professeur </option>
                                {this.state.professorsFiltredByEstablishment.map(professor => {
                                  let correctProf = false;
                                  let data = { professor_id: professor.id, subject_id: element.id }
                                  if (!(_.isEmpty(professor.profSubjects))) {
                                    professor.profSubjects.map(subjectItem => {
                                      if (subjectItem.subject_id == element.id) {
                                        correctProf = true
                                      }
                                    })
                                  }
                                  return correctProf ? (
                                    <option
                                      key={professor.id}
                                      value={JSON.stringify(data)}
                                    >
                                      {professor.profile.user_id + ": " + professor.profile.user.surname.toUpperCase() + " " + jsUpperCaseFirst(professor.profile.user.name)}
                                    </option>) : "";
                                })}
                              </select>
                            </TableCell>
                          </TableRow>
                        )
                      })
                      }
                    </TableBody>
                  </Table>
                )
              } else {
                return (<div>{<IntlMessages id="no.subjects.for.level" />}</div>)
              }
            }
          })()}
        </div>;
      case 2:
        return (<div>
          {(() => {
            if (this.props.stuppUsers.length !== 0) {
              return (<div>
                <CircularFab classItem={this.props.classItem} />
                <div className="col-lg-3 col-sm-6 col-12">
                  <FormControl className="w-100 mb-2">
                    <label htmlFor="pet-select">{<IntlMessages id="sidebar.eleve" />}</label>
                    <Select
                      multiple
                      name="students"
                      value={this.state.students}
                      onChange={this.handleChangeStudents}
                      input={<Input id="name-multiple" />}
                    >
                      {this.state.fetchStudentByEstablishmentId.map((student) => (
                        <MenuItem
                          key={student.id}
                          value={student.id}
                        >
                          {student.id + " " + student.profile.user.surname.toUpperCase() + " " + jsUpperCaseFirst(student.profile.user.name)}

                        </MenuItem>
                      ))}
                    </Select>

                  </FormControl>
                </div>

                <div className="manage-margin d-flex flex-wrap">
                  {this.state.affectedStudentsIds.map((data, index) => {
                    return (
                      <List key={index}>
                        <Chip
                          key={index + data}
                          avatar={<Avatar src='data:image/png;base64,' />}
                          label={this.state.allStudentsList.map(student => {
                            if (student.id === data) {
                              return (
                                student.id + " " + student.profile.user.surname.toUpperCase() + " " + jsUpperCaseFirst(student.profile.user.name)
                              );
                            } else { return null }
                          })}
                          onDelete={this.handleRequestDelete(data)}
                        />
                      </List>
                    );
                  })}
                </div>
              </div>)
            } else {
              return (
                <CircularFab classItem={this.props.classItem} />
              )
            }
          })()}
          <br /><br /><br />
        </div>

        )
      default:
        return 'Unknown step';
    }
  }

  handleToggle() {
    this.props.cancelModal();
  };

  render() {
    const { activeStep, steps } = this.state;
    return (
      <div className="container">
        <Auxiliary>
          <Modal isOpen={this.state.previewVisible} >
            <ModalHeader toggle={this.handleToggle.bind(this)} className="modal-box-header bg-primary text-white" >{<IntlMessages id="sidebar.classes" />}</ModalHeader>
            <ModalBody>
              <form className="row" noValidate autoComplete="off" onSubmit={this.handleSubmit}>
                <div className="w-100">
                  <Stepper activeStep={activeStep} orientation="vertical">
                    {steps.map((label, index) => {
                      return (
                        <Step key={label}>
                          <StepLabel>{label}</StepLabel>
                          <StepContent className="pb-3">
                            <Typography component={'span'} variant={'body2'}>
                              {this.getStepContent(index)}
                            </Typography>
                            <div className="mt-2">
                              <div>
                                <Button
                                  disabled={activeStep === 0}
                                  onClick={this.handleBack}
                                  className="jr-btn"
                                >
                                  {<IntlMessages id="components.class.level.button.back" />}
                                </Button>
                                <Button
                                  variant="contained"
                                  color="primary"
                                  onClick={this.handleNext}
                                  disabled={this.Validation_Steps() ? false : true}
                                  className="jr-btn"
                                >
                                  {activeStep === steps.length - 1 ? <IntlMessages id="components.class.level.button.terminer" /> : <IntlMessages id="components.class.level.button.next" />}
                                </Button>
                              </div>
                            </div>
                          </StepContent>
                        </Step>
                      );
                    })}
                  </Stepper>
                  {activeStep === steps.length && (
                    <Paper square elevation={0} className="p-2">
                      <Button onClick={this.handleReset} className="jr-btn">
                        {<IntlMessages id="button.reset" />}
                      </Button>
                      <Button variant="contained" color="primary" onClick={this.handleSubmit} className="jr-btn">
                        {<IntlMessages id="note.button.save" />}
                      </Button>

                    </Paper>
                  )}
                </div>
              </form>
            </ModalBody>
          </Modal>
        </Auxiliary>
      </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    establishments: state.establishment.remoteEstablishments
  };
}

export default connect(mapStateToProps, { getEstablishment, updateClass })(EditClass);
