import React, { Component } from "react";
import IntlMessages from "../../../util/IntlMessages";
import Auxiliary from "../../../util/Auxiliary";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import TextField from "@material-ui/core/TextField";
import CardBox from "../../../components/CardBox/index";
import Button from "@material-ui/core/Button";
import MenuItem from "@material-ui/core/MenuItem";
import { UncontrolledAlert } from "reactstrap";
import { roleIdProfessor } from "../../../config/config";

import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/moment";
import moment from "moment";
import { TimePicker } from "@material-ui/pickers";
import { connect } from "react-redux";
import { getSections } from "../../../actions/sectionAction";
import { getLevels } from "../../../actions/classLevelAction";
import { getClassesByEstablishmentId } from "../../../actions/classeAction";
import { getLevelListFromEstabType } from "../../../actions/classLevelAction";
import axios from "axios";
import baseUrl from "../../../config/config";
import { subjectsByLevelBySection } from "../../../actions/subjectAction";
import _ from "lodash";

class AddVirtualClass extends Component {
  constructor(props) {
    super(props);

    this.state = {
      alerte: false,
      alerteDate: false,
      alerteHeure: false,
      estabType: "",
      levelsbyestablishment: [],
      profLevels: [],
    };
    this.checkDate = this.checkDate.bind(this);
  }
  componentWillMount() {
    if (this.props.userProfile.role_id === roleIdProfessor) {
      let profLevels = _.map(
        this.props.values.professorClassesList,
        "level_id"
      );
      this.setState({
        profLevels,
      });
    }
  }

  checkDate = (date, startTime, endTime, classId) => {
    var format = "hh:mm:ss";
    var timeend = moment(endTime, format);
    var timestart = moment(startTime, format);
    const result = this.props.classVirtual.filter(
      (item) =>
        moment(item.date_virtual_class).format("LL") ==
          moment(date).format("LL") &&
        classId == item.class_id &&
        (moment(item.start_time_class, format).isBetween(timestart, timeend) ||
          moment(item.end_time_class, format).isBetween(timestart, timeend) ||
          timestart.isBetween(
            moment(item.start_time_class, format),
            moment(item.end_time_class, format)
          ) ||
          (item.start_time_class == startTime &&
            item.end_time_class == endTime) ||
          timeend.isBetween(
            moment(item.start_time_class, format),
            moment(item.end_time_class, format)
          ))
    );
    return result;
  };
  render() {
    const {
      classVirtual,
      values,
      handleChange,
      handleChangeDate,
      handleChangeProfessor,
      handleEndTimeChange,
      handleChangeSubject,
      handleStartTimeChange,
      handleChangestudentLevel,
      handleChangestudentSection,
      handleChangeClass,
    } = this.props;

    var sameClassDate = this.checkDate(
      values.class_date,
      values.startTime,
      values.endTime,
      values.class_id
    );
    var format = "hh:mm:ss";
    var timeend = moment(values.endTime, format);
    var timestart = moment(values.startTime, format);
    var disabled = values.professor_id == "";
    var heureCheck = timeend - timestart <= 0;

    return (
      <Auxiliary>
        <Modal isOpen={this.props.addIsopen}>
          <ModalHeader
            toggle={this.props.handleCancel}
            className="modal-box-header bg-primary text-white"
          >
            {<IntlMessages id="new.class.virtual" />}
          </ModalHeader>
          <br />
          <ModalBody>
            <form
              autoComplete="off"
              onSubmit={(event) => {
                event.preventDefault();

                if (disabled) {
                  this.setState({ alerte: true });
                  setTimeout(() => {
                    this.setState({ alerte: false });
                  }, 3000);
                } else if (heureCheck) {
                  this.setState({ alerteHeure: true });
                  setTimeout(() => {
                    this.setState({ alerteHeure: false });
                  }, 3000);
                } else {
                  if (sameClassDate.length == 0) {
                    this.props.addVirtualClass();
                  } else {
                    this.setState({ alerteDate: true });
                    setTimeout(() => {
                      this.setState({ alerteDate: false });
                    }, 3000);
                  }
                }
              }}
            >
              <div className="row">
                <CardBox
                  heading={
                    <IntlMessages id="component.etablishments.info.general" />
                  }
                  styleName="col-lg-12 text-primary"
                >
                  <div className="row">
                    <div className="col-md-12">
                      <div className="form-group">
                        <TextField
                          required
                          name="class_object"
                          id="class_object"
                          variant="outlined"
                          label={<IntlMessages id="subject.message" />}
                          onChange={handleChange("class_object")}
                          value={values.class_object}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          id="level_id"
                          name="level_id"
                          variant="outlined"
                          required
                          select
                          value={values.level_id}
                          defaultValue=" "
                          onChange={handleChangestudentLevel("level_id")}
                          SelectProps={{}}
                          helperText={
                            <IntlMessages id="components.note.niveau" />
                          }
                          margin="normal"
                          fullWidth
                        >
                          {values.levelsbyestablishment.map((level) => {
                            if (
                              this.props.userProfile.role_id === roleIdProfessor
                            ) {
                              if (
                                this.state.profLevels.indexOf(level.id) >= 0
                              ) {
                                return (
                                  <MenuItem key={level.id} value={level.id}>
                                    {level.name}
                                  </MenuItem>
                                );
                              }
                            } else {
                              return (
                                <MenuItem key={level.id} value={level.id}>
                                  {level.name}
                                </MenuItem>
                              );
                            }
                          })}
                        </TextField>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          id="section_id"
                          required
                          name="section_id"
                          variant="outlined"
                          select
                          value={values.section_id}
                          onChange={handleChangestudentSection("section_id")}
                          disabled={values.Disable_studentsection}
                          SelectProps={{}}
                          helperText={
                            <IntlMessages id="components.class.level.input.label.section" />
                          }
                          margin="normal"
                          fullWidth
                        >
                          {values.studentsectionByLevels.map((section) => (
                            <MenuItem key={section.id} value={section.id}>
                              {section.name}
                            </MenuItem>
                          ))}
                        </TextField>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          id="class_id"
                          name="class_id"
                          variant="outlined"
                          select
                          required
                          value={values.class_id}
                          onChange={handleChangeClass("class_id")}
                          SelectProps={{}}
                          helperText={<IntlMessages id="ticket.name.class" />}
                          margin="normal"
                          fullWidth
                        >
                          {this.props.userProfile.role_id === roleIdProfessor
                            ? values.professorClassesAdd.map((classe) => (
                                <MenuItem key={classe.id} value={classe.id}>
                                  {classe.name}
                                </MenuItem>
                              ))
                            : values.studentClassesByLevelSectionID.map(
                                (classe) => (
                                  <MenuItem key={classe.id} value={classe.id}>
                                    {classe.name}
                                  </MenuItem>
                                )
                              )}
                        </TextField>
                      </div>
                    </div>

                    <div className="col-md-6">
                      <div className="form-group">
                        <TextField
                          id="subject_id"
                          name="subject_id"
                          variant="outlined"
                          select
                          required
                          value={values.subject_id}
                          onChange={handleChangeSubject("subject_id")}
                          SelectProps={{}}
                          helperText={
                            <IntlMessages id="components.note.subject" />
                          }
                          margin="normal"
                          fullWidth
                        >
                          {this.props.userProfile.role_id === roleIdProfessor
                            ? values.profSubjects.map((subject) => (
                                <MenuItem key={subject.id} value={subject.id}>
                                  {this.props.settings.languageId == "tunisia"
                                    ? subject.name_AR
                                    : this.props.settings.languageId == "french"
                                    ? subject.name_FR
                                    : subject.name_EN}
                                </MenuItem>
                              ))
                            : subjectsByLevelBySection(
                                values.subjectList,
                                values.level_id,
                                values.section_id
                              ).map((subject) => (
                                <MenuItem key={subject.id} value={subject.id}>
                                  {this.props.settings.languageId == "tunisia"
                                    ? subject.name_AR
                                    : this.props.settings.languageId == "french"
                                    ? subject.name_FR
                                    : subject.name_EN}
                                </MenuItem>
                              ))}
                        </TextField>
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="form-group">
                        <TextField
                          id="professor_id"
                          name="professor_id"
                          variant="outlined"
                          select
                          required
                          value={values.professor_id}
                          onChange={handleChangeProfessor("professor_id")}
                          SelectProps={{}}
                          helperText={<IntlMessages id="toDo.professor" />}
                          margin="normal"
                          fullWidth
                        >
                          {values.establishmentProfessor.map((option) => (
                            <MenuItem
                              key={option.professor.id}
                              value={option.professor.id}
                            >
                              {this.props.settings.languageId == "tunisia"
                                ? option.professor.profile.user.surname_ar +
                                  " " +
                                  option.professor.profile.user.name_ar
                                : option.professor.profile.user.name +
                                  " " +
                                  option.professor.profile.user.surname}
                            </MenuItem>
                          ))}
                        </TextField>
                      </div>
                    </div>
                  </div>
                </CardBox>
                <CardBox
                  heading={
                    <IntlMessages id="component.connexion.information" />
                  }
                  styleName="col-lg-12 text-primary"
                >
                  <div className="row">
                    <div className="col-md-7">
                      <div className="form-group">
                        <h5 style={{ color: "#000" }}>
                          {" "}
                          <IntlMessages id="class.extern.link" /> &#x27A5;
                        </h5>
                      </div>
                    </div>
                    <div className="col-md-5">
                      <div className="form-group">
                        <a
                          href="https://jetsi.adn-expertise.fr/"
                          target="_blank"
                        >
                          <IntlMessages id="clic.here" />
                        </a>
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="form-group">
                        <TextField
                          variant="outlined"
                          required
                          name="class_url"
                          id="class_url"
                          label={
                            <IntlMessages id="components.virtual.class.url" />
                          }
                          onChange={handleChange("class_url")}
                          value={values.class_url}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="form-group">
                        <TextField
                          variant="outlined"
                          name="class_password"
                          id="class_password"
                          multiline
                          label={<IntlMessages id="appModule.password" />}
                          onChange={handleChange("class_password")}
                          value={values.class_password}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>
                  </div>
                </CardBox>

                <CardBox
                  heading={<IntlMessages id="horaires.informations" />}
                  styleName="col-lg-12 text-primary"
                >
                  <div className="row">
                    <div className="col-md-4">
                      <div className="form-group">
                        <MuiPickersUtilsProvider utils={DateFnsUtils}>
                          <KeyboardDatePicker
                            required
                            inputVariant="outlined"
                            label="                                           "
                            fullWidth
                            id="class_date"
                            name="class_date"
                            value={values.class_date}
                            onChange={handleChangeDate}
                            format="MM-DD-YYYY"
                            autoOk
                            minDate={new Date()}
                          />
                        </MuiPickersUtilsProvider>
                      </div>
                    </div>

                    <div className="col-md-4">
                      <div className="form-group">
                        <div key="datetime_default" className="picker">
                          <TimePicker
                            required
                            label={<IntlMessages id="start.hour.class" />}
                            fullWidth
                            inputVariant="outlined"
                            value={values.selectedStartTime}
                            showTabs={false}
                            onChange={handleStartTimeChange}
                            ampm={false}
                            leftArrowIcon={
                              <i className="zmdi zmdi-arrow-back" />
                            }
                            rightArrowIcon={
                              <i className="zmdi zmdi-arrow-forward" />
                            }
                          />
                        </div>
                      </div>
                    </div>
                    <div className="col-md-4">
                      <div className="form-group">
                        <div key="datetime_default" className="picker">
                          <TimePicker
                            label={<IntlMessages id="end.hour.class" />}
                            fullWidth
                            required
                            inputVariant="outlined"
                            value={values.selectedEndTime}
                            ampm={false}
                            showTabs={false}
                            onChange={handleEndTimeChange}
                            leftArrowIcon={
                              <i className="zmdi zmdi-arrow-back" />
                            }
                            rightArrowIcon={
                              <i className="zmdi zmdi-arrow-forward" />
                            }
                          />
                        </div>

                        {values.selectedEndTime <= values.selectedStartTime ? (
                          <IntlMessages id="end.hour.check" />
                        ) : (
                          ""
                        )}
                      </div>
                    </div>
                  </div>
                </CardBox>
              </div>
              {this.state.alerte ? (
                <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
                  <span className="icon-addon alert-addon">
                    <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                  </span>
                  <span className="d-inline-block">
                    <IntlMessages id="alerte.ajout.classVirtual" />
                  </span>
                </UncontrolledAlert>
              ) : (
                ""
              )}
              {this.state.alerteDate ? (
                <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
                  <span className="icon-addon alert-addon">
                    <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                  </span>
                  <span className="d-inline-block">
                    <IntlMessages id="alerte.date" />
                  </span>
                </UncontrolledAlert>
              ) : (
                ""
              )}
              {this.state.alerteHeure ? (
                <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
                  <span className="icon-addon alert-addon">
                    <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                  </span>
                  <span className="d-inline-block">
                    <IntlMessages id="end.hour.check" />
                  </span>
                </UncontrolledAlert>
              ) : (
                ""
              )}

              <div className="col-md-12 text-left ">
                <br />
