import React, { Component } from 'react';
import { PermissionList } from './PermissionList';
import { getEducationType } from '../../../../../actions/estabTypeAction';
import { connect } from 'react-redux';
import { getPermissionSetting } from '../../../../../actions/PermissionAction';
import { UncontrolledAlert } from 'reactstrap';
import _ from 'lodash';
import SweetAlert from 'react-bootstrap-sweetalert';
import IntlMessages from '../../../../../util/IntlMessages';
var listPermissions = [
  {
    id: 1,
    Action_Fr: 'Ajouter un admin',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 2, 3, 5],
  },
  {
    id: 2,
    Action_Fr: 'Modifier un admin ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 3, 5],
  },
  {
    id: 3,
    Action_Fr: 'Supprimer un admin  ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 3, 5, 6],
  },
  {
    id: 4,
    Action_Fr: 'Consulter des admin',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 2, 3, 7],
  },
  {
    id: 5,
    Action_Fr: 'Ajouter un membre de direction ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 3,
    role: [1, 2, 3, 4],
  },
  {
    id: 6,
    Action_Fr: 'Ajouter un devoir de maison  ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5, 6],
  },
  {
    id: 7,
    Action_Fr: 'Modifier un devoir de maison ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5],
  },
  {
    id: 8,
    Action_Fr: 'Supprimer un devoir de maison ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5],
  },
  {
    id: 9,
    Action_Fr: 'Consulter les devoirs de maisons',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 4,
    role: [1, 2, 3, 5],
  },
];
export class PermissionSetting extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount() {
    this.props.dispatch(
      getPermissionSetting(4)
    );
  }

  render() {
    console.log(this.props.permissionSetting, 'permissionSetting');
    return (
      <div>
        {this.props.successStatus ? (
          <UncontrolledAlert className="alert-addon-card bg-success bg-success text-white shadow-lg">
            <span className="icon-addon alert-addon">
              <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
            </span>
            <span className="d-inline-block"> {this.props.message} </span>
          </UncontrolledAlert>
        ) : (
          ''
        )}
        {this.props.errorStatus ? (
          <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
            <span className="icon-addon alert-addon">
              <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
            </span>
            <span className="d-inline-block"> {this.props.message} </span>
          </UncontrolledAlert>
        ) : (
          ''
        )}
        <PermissionList listPermissions={listPermissions} estabModule={this.props.estabModule} />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
    successStatus: state.alert.success,
    errorStatus: state.alert.error,
    message: state.alert.message,
    estabModule: state.establishment.estabModule,
    settings: state.settings.locale,
    permissionSetting: state.PermissionReducer.permissionSetting,
  };
};

export default connect(mapStateToProps)(PermissionSetting);
