import React, { Component } from 'react';
import Auxiliary from '../../../util/Auxiliary';
import { Modal, ModalHeader } from 'reactstrap';
import IntlMessages from '../../../util/IntlMessages';
import Chip from '@material-ui/core/Chip';
import { connect } from 'react-redux';

class ShowVirtualClass extends Component {
  render() {
    const sys = Date.parse(new Date()) / 60000;
    const start =
      Date.parse(
        this.props.ClassVirtualObject.date_virtual_class.slice(0, 10) +
          ' ' +
          this.props.ClassVirtualObject.start_time_class
      ) / 60000;
    const end =
      Date.parse(
        this.props.ClassVirtualObject.date_virtual_class.slice(0, 10) +
          ' ' +
          this.props.ClassVirtualObject.end_time_class
      ) / 60000;

    const duration_h = Math.floor((end - start) / 60);
    const duration_min = (end - start) % 60;
    var options = {
      weekday: 'long',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    };
    const statusClass =
      start > sys ? (
        <IntlMessages id="status.classe.virtual.programmé" />
      ) : end > sys ? (
        <IntlMessages id="status.classe.virtual.progrés" />
      ) : (
        <IntlMessages id="status.classe.virtual.términé" />
      );
    const statusStyle =
      start > sys
        ? 'text-white bg-success'
        : end > sys
        ? 'bg-amber'
        : 'text-white bg-danger';

    const date_Class = this.props.ClassVirtualObject.date_virtual_class;

    const date_Class_EN = new Date(date_Class).toLocaleDateString(
      'en-US',
      options
    );
    const date_Class_FR = new Date(date_Class).toLocaleDateString(
      'fr-FR',
      options
    );
    const date_Class_AR = new Date(date_Class).toLocaleDateString(
      'ar-TN',
      options
    );

    return (
      <Auxiliary>
        <Modal
          className="modal-box modal-box-mail"
          isOpen={true}
          style={{ zIndex: 2600 }}
        >
          <ModalHeader
            className="modal-box-header bg-primary text-white"
            toggle={this.props.cancelModal}
            align="right"
          ></ModalHeader>
          <div className="module-detail mail-detail">
            <div className="jr-card text-center">
              <div className={`jr-card-header-color bg-gradient-primary`}>
                <div className="jr-card-header-top">
                  <div>
                    <h3 className="mb-0 text-white">
                      {this.props.settings.languageId == 'tunisia'
                        ? date_Class_AR + '   على الساعة  '
                        : this.props.settings.languageId == 'french'
                        ? date_Class_FR + '  À '
                        : date_Class_EN + '  at '}
                      {this.props.ClassVirtualObject.start_time_class}
                    </h3>
                  </div>
                </div>

                <img
                  className="rounded-circle size-90 avatar-shadow mb-3"
                  src="https://pngimage.net/wp-content/uploads/2018/05/admin-avatar-png-1.png"
                  alt="Team Member"
                />

                <div className="jr-card-hd-content">
                  <h4 className="mb-0 text-white">
                    {this.props.settings.languageId == 'tunisia'
                      ? this.props.ClassVirtualObject.ProfessorNameAR
                      : this.props.ClassVirtualObject.ProfessorName}
                  </h4>
                </div>
              </div>
              <div className="jr-card-body pt-2">
                <p className="message">
                  <font size="+2"> {this.props.ClassVirtualObject.object}</font>
                </p>

                <div>
                  <div>
                    <strong>
                      <IntlMessages id="ticket.name.class" /> :{' '}
                      {this.props.ClassVirtualObject.ClassName}
                    </strong>
                  </div>
                  <div>
                    <strong>
                      <IntlMessages id="components.note.subject" /> :{' '}
                      {this.props.settings.languageId == 'tunisia'
                        ? this.props.ClassVirtualObject.SubjectName_AR
                        : this.props.settings.languageId == 'french'
                        ? this.props.ClassVirtualObject.SubjectName_FR
                        : this.props.ClassVirtualObject.SubjectName_EN}
                    </strong>
                  </div>
                  <div>
                    <strong>
                      <IntlMessages id="components.virtual.class.url" /> :
                      <a
                        href={this.props.ClassVirtualObject.class_url}
                        target="_blank"
                      >
                        <IntlMessages id="clic.here" />
                      </a>
                    </strong>
                  </div>
                  <div>
                    <strong>
                      <IntlMessages id="appModule.password" /> :{' '}
                      {this.props.ClassVirtualObject.password}
                    </strong>
                  </div>
                  <div>
                    <strong>
                      <IntlMessages id="class.virtual.duration" /> :{' '}
                      {duration_h} <IntlMessages id="duration.heure" />{' '}
                      {duration_min} <IntlMessages id="duration.min" />
                    </strong>
                  </div>
                  <div>
                    <Chip
                      variant="outlined"
                      size="small"
                      label={statusClass}
                      className={` badge text-uppercase ${statusStyle}`}
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </Modal>
      </Auxiliary>
    );
  }
}

function mapStateToProps(state) {
  return {
    settings: state.settings.locale,
  };
}
export default connect(
  mapStateToProps,
  {}
)(ShowVirtualClass);
