import React from "react";
import IntlMessages from "../../../../../util/IntlMessages";
import { connect } from "react-redux";
import IconWithTextCard from "../ServiceAllocation/ServiceAllocationComp/IconWithTextCard";
import TextField from "@material-ui/core/TextField";
import { classService } from "../../../../../_services/class.service";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import ListServiceAllocation from "./ListServiceAllocation";
import AddAllocationService from "./AddAllocationService";
import MenuItem from "@material-ui/core/MenuItem";
import InputLabel from "@material-ui/core/InputLabel";
import { getServiceV2 } from "../../../../../actions/ServiceAction";
import { getLevelByEstablishmentId } from "../../../../../actions/classLevelAction";
import { getClassesByEstablishmentId } from "../../../../../actions/classeAction";
import { getSubjectsByEstablishmentId } from "../../../../../actions/subjectAction";
import { getAllocationServiceByEstablishment } from "../../../../../actions/AllocationServiceAction";
import _ from "lodash";
import { UncontrolledAlert } from "reactstrap";

const idHighSchool = 2;

class ServiceAllocation extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      studentList: [],
      classesList: [],
      classLevel: 0,
      sections: [],
      schoolYearsV2: [],
      allocationServicesList: [],
      sectionStatus: false,
      idSection: "",
      idClasse: "",
      idSchoolYearsV2: "",
    };
    this.openAddModal = this.openAddModal.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleChangeLevel = this.handleChangeLevel.bind(this);
    this.handleChangeClass = this.handleChangeClass.bind(this);
    this.handleChangeSection = this.handleChangeSection.bind(this);
  }

  handleChange = (name) => (event) => {
    let allocationServerFiltred = this.props.allocationService.filter(
      (element) =>
        element.services[0].service_v2.fk_id_schol_year_v2 ===
        event.target.value
    );
    this.setState({
      [name]: event.target.value,
      allocationServicesList: allocationServerFiltred,
    });
  };

  handleChangeSection = (name) => (event) => {
    let allocationServerFiltred = this.props.allocationService.filter(
      (element) => element.Class.section_id === event.target.value
    );
    this.setState({
      [name]: event.target.value,
      allocationServicesList: allocationServerFiltred,
    });
  };

  handleChangeClass = (name) => (event) => {
    let allocationServerFiltred = this.props.allocationService.filter(
      (element) => element.Class.id === event.target.value
    );
    this.setState({
      [name]: event.target.value,
      allocationServicesList: allocationServerFiltred,
    });
  };

  handleChangeLevel = (name) => (event) => {
    this.setState({ [name]: event.target.value });
    let allocationServerFiltred = this.props.allocationService.filter(
      (element) => element.level.id === event.target.value
    );
    let classesListFiltred = this.props.classes.filter(
      (element) => element.level_id === event.target.value
    );
    this.setState({
      allocationServicesList: allocationServerFiltred,
      classesList: classesListFiltred,
    });
    let apiEndpoint = `/section_by_levels?access_token=${localStorage.token}&filter[where][level_id]=${event.target.value}`;
    classService.get(apiEndpoint).then((response) => {
      this.setState({ sections: response.data });
    });
  };

  componentWillMount() {
    const establishmentId = this.props.userProfile.establishment_id;
    this.props.dispatch(getServiceV2(this.props.userProfile.establishment_id));
    this.props.dispatch(
      getAllocationServiceByEstablishment(
        this.props.userProfile.establishment_id
      )
    );
    this.props.dispatch(getLevelByEstablishmentId(establishmentId));
  }
  componentDidMount() {
    let apiEndpoint = "";
    const establishmentId = this.props.userProfile.establishment_id;
    apiEndpoint = `/students/fetchStudentByEstablishmentId/${this.props.userProfile.establishment_id}?access_token=${localStorage.token}`;
    classService.get(apiEndpoint).then((res) => {
      if (res) {
        this.setState({ studentList: res.data.students });
      }
    });

    apiEndpoint = `/school_years_v2?access_token=${localStorage.token}`;
    classService.get(apiEndpoint).then((res) => {
      if (res) {
        this.setState({ schoolYearsV2: res.data });
      }
    });

    this.props.dispatch(getClassesByEstablishmentId(establishmentId));
    this.props.dispatch(getSubjectsByEstablishmentId(establishmentId));
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.classes !== this.props.classes) {
      this.setState({
        classesList: this.props.classes,
      });
    }
    if (prevProps.allocationService !== this.props.allocationService) {
      this.setState({
        allocationServicesList: this.props.allocationService,
      });
    }

    if (prevProps.classLevels !== this.props.classLevels) {
      this.setState({
        sectionStatus: true,
      });
    }
  }

  openAddModal() {
    this.setState((previousState) => ({
      open: !previousState.open,
    }));
  }

  render() {
    let { services, classLevels } = this.props;
    let {
      classesList,
      sections,
      schoolYearsV2,
      allocationServicesList,
    } = this.state;
    let allserviceAllocated = [];
    allocationServicesList.map((data, index) =>
      allserviceAllocated.push(data.services)
    );
    allserviceAllocated = allserviceAllocated.flat();
    let serviceAllocated = _.intersectionWith(
      services,
      allserviceAllocated,
      (a, b) => a.id == b.fk_id_service
    );

    let detailCards = [
      {
        cardColor: "primary",
        imageIcon: require("../ServiceAllocation/Icone/project-icon.png"),
        title: services.length,
        subTitle: "Prestations",
      },
      {
        cardColor: "secondary",
        imageIcon: require("../ServiceAllocation/Icone/tasks-icon.png"),
        title: allocationServicesList.length,
        subTitle: "Affectations",
      },
      {
        cardColor: "info",
        imageIcon: require("../ServiceAllocation/Icone/teams-icon.png"),
        title: this.state.studentList.length,
        subTitle: "Inscrit",
      },
      {
        cardColor: "warning",
        imageIcon: require("../ServiceAllocation/Icone/files-icon.png"),
        title: serviceAllocated.length,
        subTitle: "Prestations affectées",
      },
    ];

    return (
      <div
        className="dashboard animated slideInUpTiny
      animation-duration-3"
        style={{
          marginLeft: "5%",
          marginRight: "4%",
        }}
      >
        <div className="row col-lg-12 col-md-12">
          {detailCards.map((data, index) => (
            <div
              key={index}
              className="col-xl-3 col-lg-3 col-md-6 col-sm-6 col-12"
            >
              <IconWithTextCard data={data} />
            </div>
          ))}
          {this.props.successStatus ? (
            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
              <UncontrolledAlert className="alert-addon-card bg-success bg-success text-white shadow-lg">
                <span className="icon-addon alert-addon">
                  <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                </span>
                <span className="d-inline-block"> {this.props.message} </span>
              </UncontrolledAlert>
            </div>
          ) : (
            ""
          )}
          {this.props.errorStatus ? (
            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
              <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
                <span className="icon-addon alert-addon">
                  <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                </span>
                <span className="d-inline-block"> {this.props.message} </span>
              </UncontrolledAlert>
            </div>
          ) : (
            ""
          )}
          <div
            className="row col-lg-12 col-md-12"
            style={{ display: "flex", alignItems: "center" }}
          >
            <div className="col-xl-1 col-lg-1 col-md-2 col-sm-2 col-12">
              <TextField
                id="classLevel"
                name="classLevel"
                select
                value={this.state.idClasse}
                onChange={this.handleChangeLevel("classLevel")}
                SelectProps={{}}
                label={<IntlMessages id={`components.note.niveau`} />}
                InputProps={{ disableUnderline: true }}
                margin="normal"
                fullWidth
              >
                {classLevels.map((option) => (
                  <MenuItem key={option.id} value={option.id}>
                    {option.name}
                  </MenuItem>
                ))}
              </TextField>
            </div>
            <div className="col-xl-1 col-lg-1 col-md-2 col-sm-2 col-12">
              <TextField
                id="idClasse"
                name="idClasse"
                select
                value={this.state.idClasse}
                onChange={this.handleChangeClass("idClasse")}
                SelectProps={{}}
                label={<IntlMessages id={`components.note.class`} />}
                InputProps={{ disableUnderline: true }}
                margin="normal"
                fullWidth
              >
                {classesList.map((option) => (
                  <MenuItem key={option.id} value={option.id}>
                    {option.name}
                  </MenuItem>
                ))}
              </TextField>
            </div>
            {this.state.sectionStatus &&
            this.props.classLevels[0].estab_type_id !== idHighSchool ? (
              <div className="col-xl-1 col-lg-1 col-md-2 col-sm-2 col-12">
                <TextField
                  id="idSection"
                  name="idSection"
                  select
                  value={this.state.idSection}
                  onChange={this.handleChangeSection("idSection")}
                  SelectProps={{}}
                  label={
                    <IntlMessages
                      id={`components.class.level.input.label.section`}
                    />
                  }
                  InputProps={{ disableUnderline: true }}
                  margin="normal"
                  fullWidth
                >
                  {sections.map((option) => (
                    <MenuItem key={option.id} value={option.id}>
                      {option.name_FR}
                    </MenuItem>
                  ))}
                </TextField>
              </div>
            ) : (
              ""
            )}
            <div className="col-xl-2 col-lg-2 col-md-6 col-sm-6 col-12">
              <TextField
                id="schoolYearsV2"
                name="schoolYearsV2"
                select
                value={this.state.idSchoolYearsV2}
                onChange={this.handleChange("idSchoolYearsV2")}
                SelectProps={{}}
                label={<IntlMessages id={`filter.school.years`} />}
                InputProps={{ disableUnderline: true }}
                margin="normal"
                fullWidth
              >
                {schoolYearsV2.map((option) => (
                  <MenuItem key={option.id} value={option.id}>
                    {option.name_school_year}
                  </MenuItem>
                ))}
              </TextField>
            </div>
            <div className="col-xl-6 col-lg-6 col-md-4 col-sm-4 col-12"></div>
            <div>
              <InputLabel htmlFor="newAllocation" style={{ color: "#0000FF" }}>
                Nouvelle Affectation
              </InputLabel>
            </div>
            &nbsp;&nbsp;&nbsp;
            <div>
              <Fab
                name="newAllocation"
                size="small"
                color="primary"
                aria-label="Add"
                onClick={this.openAddModal}
              >
                {this.state.open ? <RemoveSharpIcon /> : <AddIcon />}
              </Fab>
            </div>
          </div>

          {this.state.open ? (
            <AddAllocationService
              services={services}
              studentList={this.state.studentList}
              openAddModal={this.openAddModal}
              establishmentId={this.props.userProfile.establishment_id}
              allocationService={allocationServicesList}
            />
          ) : (
            ""
          )}
          <ListServiceAllocation allocationService={allocationServicesList} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
    services: state.service.servicesV2,
    classLevels: state.ClassLevels.remoteLevels,
    classes: state.classes,
    sections: state.classSections.remoteSections,
    allocationService: state.allocationService.allocationServiceList,
    successStatus: state.alert.success,
    errorStatus: state.alert.error,
    message: state.alert.message,
  };
};

export default connect(mapStateToProps)(ServiceAllocation);
