import axios from "axios";
import {
  HIDE_MESSAGE,
  INIT_URL,
  ON_HIDE_LOADER,
  ON_SHOW_LOADER,
  SHOW_MESSAGE,
  SIGNIN_FACEBOOK_USER,
  SIGNIN_FACEBOOK_USER_SUCCESS,
  SIGNIN_GITHUB_USER,
  SIGNIN_GITHUB_USER_SUCCESS,
  SIGNIN_GOOGLE_USER,
  SIGNIN_GOOGLE_USER_SUCCESS,
  SIGNIN_TWITTER_USER,
  SIGNIN_TWITTER_USER_SUCCESS,
  SIGNIN_USER_SUCCESS,
  SIGNOUT_USER,
  SIGNOUT_USER_SUCCESS,
  SIGNUP_USER,
  SIGNUP_USER_SUCCESS,
  GET_USER_PROFILE,
  THEME_COLOR,
} from "../constants/ActionTypes";
import cst from "../config/config";
import { getError } from "../Error/Error";
import { getThemeColor, getAppLanguage, initCalendar } from "./Setting";

export const userSignUp = (user) => {
  return {
    type: SIGNUP_USER,
    payload: user,
  };
};

export const userSignIn = (user) => {
  var email = user.email.toLowerCase().trim();
  var password = user.password;
  const User = { email, password };
  return (dispatch) => {
    axios
      .post(`${cst.baseUrl}/users/login`, User)
      .then((res) => {
        let userID = res.data.userId;
        let id = res.data.id;
        localStorage.setItem("token", res.data.id);
        localStorage.setItem("user_id", res.data.userId);
        axios
          .get(
            `${cst.baseUrl}/users/${res.data.userId}?access_token=${res.data.id}&filter[include][profiles]`
          )
          .then((res) => {
            let settingId = res.data.profiles[0].setting_id;
            axios
              .get(
                `${cst.baseUrl}/settings/${settingId}?access_token=${localStorage.token}`
              )
              .then((res) => {
                dispatch(getThemeColor(res.data.theme_color));
                dispatch(getAppLanguage(res.data.app_lang));
                dispatch(
                  initCalendar(
                    res.data.start_time_calendar,
                    res.data.end_time_calendar
                  )
                );
                localStorage.setItem("appLang", res.data.app_lang);
              });
          })
          .catch((err) => {
            console.log(err);
          });

        dispatch(getEstablishmentsModules(id));
        getprofile(localStorage.user_id);
        return axios.get(
          `${cst.baseUrl}/users/${res.data.userId}?access_token=${res.data.id}`
        );
      })
      .then((res) => {
        localStorage.setItem(
          "first_connexion",
          JSON.stringify(res.data.first_connexion)
        );
        localStorage.setItem("user", JSON.stringify(res.data));
        dispatch(getUserProfile(res.data.id));
        return dispatch({
          type: SIGNIN_USER_SUCCESS,
          payload: User,
        });
      })
      .catch((err) =>
        dispatch(
          showAuthMessage("Veuillez vérifier votre login et mot de passe")
        )
      );
  };
};

export const getEstablishmentsModules = (id) => {
  return function(dispatch) {
    axios
      .get(
        `${cst.baseUrl}/profiles/fetchModuleByUserID/` +
          localStorage.user_id +
          `?access_token=${localStorage.token}`
      )
      .then((res) => {
        dispatch({ type: "GET_ESTABLISHMENT_MODULE", payload: res.data });
      })
      .catch((error) => {
        console.log(error);
      });
  };
};

export const resetAccountPassword = (data) => {
  return function(dispatch) {
    var token = localStorage.getItem("token");
    axios
      .post(
        `${cst.baseUrl}/users/reset-initial-password?access_token=${token}`,
        data,
        {
          headers: {
            "content-type": "application/json",
          },
        }
      )
      .then((res) => {})
      .catch((error) => {
        console.log(error);
      });
  };
};

export function getUserProfile(userId) {
  return function(dispatch) {
    axios
      .get(
        `${cst.baseUrl}/profiles/findOne?access_token=${localStorage.token}&filter={"where":{"user_id":` +
          userId +
          `}}`
      )
      .then((res) => {
        dispatch({ type: GET_USER_PROFILE, payload: res.data });
        axios
          .get(
            `${cst.baseUrl}/settings/${res.data.setting_id}?access_token=${localStorage.token}`
          )
          .then((res) => {
            dispatch(getThemeColor(res.data.theme_color));
            dispatch(getAppLanguage(res.data.app_lang));
            dispatch(
              initCalendar(
                res.data.start_time_calendar,
                res.data.end_time_calendar
              )
            );
          });
      })
      .catch((err) => {
        console.log(err);
      });
  };
}

function getprofile(userId, dispatch) {
  axios
    .get(
      `${cst.baseUrl}/profiles/findOne?access_token=${localStorage.token}&filter={"where":{"user_id":` +
        userId +
        `}}`
    )
    .then((res) => {
      const profiles = res.data;
      localStorage.setItem("profileId", profiles.id);
      localStorage.setItem("roles_id", profiles.role_id);
      localStorage.setItem("establishment_id", profiles.establishment_id);
    })
    .catch((err) => {
      if (err.response.status === 401) {
        dispatch(userSignOut());
      }
    });
}

export const userSignOut = () => {
  axios.post(`${cst.baseUrl}/users/logout?access_token=${localStorage.token}`);
  localStorage.removeItem("token");
  localStorage.removeItem("profileId");
  localStorage.removeItem("roles_id");
  localStorage.removeItem("user_id");
  localStorage.removeItem("establishment_id");

  return {
    type: SIGNOUT_USER,
  };
};

export const userSignUpSuccess = (authUser) => {
  return {
    type: SIGNUP_USER_SUCCESS,
    payload: authUser,
  };
};

export const userSignInSuccess = (authUser) => {
  return {
    type: SIGNIN_USER_SUCCESS,
    payload: authUser,
  };
};
export const userSignOutSuccess = () => {
  return {
    type: SIGNOUT_USER_SUCCESS,
  };
};

export const showAuthMessage = (message) => {
  return {
    type: SHOW_MESSAGE,
    payload: message,
  };
};

export const userGoogleSignIn = () => {
  return {
    type: SIGNIN_GOOGLE_USER,
  };
};
export const userGoogleSignInSuccess = (authUser) => {
  return {
    type: SIGNIN_GOOGLE_USER_SUCCESS,
    payload: authUser,
  };
};
export const userFacebookSignIn = () => {
  return {
    type: SIGNIN_FACEBOOK_USER,
  };
};
export const userFacebookSignInSuccess = (authUser) => {
  return {
    type: SIGNIN_FACEBOOK_USER_SUCCESS,
    payload: authUser,
  };
};
export const setInitUrl = (url) => {
  return {
    type: INIT_URL,
    payload: url,
  };
};
export const userTwitterSignIn = () => {
  return {
    type: SIGNIN_TWITTER_USER,
  };
};
export const userTwitterSignInSuccess = (authUser) => {
  return {
    type: SIGNIN_TWITTER_USER_SUCCESS,
    payload: authUser,
  };
};
export const userGithubSignIn = () => {
  return {
    type: SIGNIN_GITHUB_USER,
  };
};
export const userGithubSignInSuccess = (authUser) => {
  return {
    type: SIGNIN_GITHUB_USER_SUCCESS,
    payload: authUser,
  };
};
export const showAuthLoader = () => {
  return {
    type: ON_SHOW_LOADER,
  };
};

export const hideMessage = () => {
  return {
    type: HIDE_MESSAGE,
  };
};
export const hideAuthLoader = () => {
  return {
    type: ON_HIDE_LOADER,
  };
};
