import React from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import IntlMessages from '../../../../../util/IntlMessages';
import EditUsers from './EditUsers';
import _ from 'lodash';
import UsersListItem from './UsersListItem';
import DeleteUsersItem from './DeleteUsers';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import Can from '../../../../../can';
import { RoleContext } from '../../../../../Context';
import {
  roleIdSuperAdmin,
  roleIdAdmin,
  roleIdProfessor,
  roleIdStudent,
  roleIdDirector,
  roleIdParent,
  roleIdSupervisor,
} from '../../../../../config/config';
import MenuItem from '@material-ui/core/MenuItem';

import ModalDetailsUser from './ModalDetailsUser';
const listRole = [
  // { id: 0, label: <IntlMessages id={`permission.role.all`} /> },
  { id: roleIdAdmin, label: <IntlMessages id={`role.admin`} /> },
  { id: roleIdDirector, label: <IntlMessages id={`component.etablishments.info.director`} /> },
