import React from "react";
import IntlMessages from "../../../../util/IntlMessages";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { connect } from "react-redux";
import DeleteQuestion from "./DeleteQuestion";
import ShowQuestion from "./ShowQuestion";
import QuestionListItems from "./QuestionListItems";
const roleIdSuperAdmin = 1;

class QuestionsTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      openDeleteQuestion: false,
      openShowQuestion: false,
      anchorEl: undefined,
      menuState: false,
      itemId: 0,
      questionItem: {},
      ItemToDelete: 0,
    };
    this.openDeleteQuestionModal = this.openDeleteQuestionModal.bind(this);
    this.openShowQuestionModal = this.openShowQuestionModal.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
  }

  handleDelete = () => {
    console.log("Lets delete it", this.state.ItemToDelete);
  };
  openShowQuestionModal = () => {
    this.setState({ openShowQuestion: true });
  };
  openDeleteQuestionModal = () => {
    this.setState({ openDeleteQuestion: true });
  };
  cancelModal = () => {
    this.setState({ openDeleteQuestion: false, openShowQuestion: false });
  };
  onOptionMenuSelect = (event) => {
    this.setState({
      menuState: true,
      anchorEl: event.currentTarget,
      itemId: event.currentTarget.value,
    });
  };
  handleRequestClose = () => {
    this.setState({ menuState: false });
  };

  render() {
    return (
      <div className="table-responsive-material">
        <Table className="default-table table-unbordered table table-sm table-hover">
          <TableHead className="th-border-b">
            <TableRow>
              <TableCell>{<IntlMessages id="question.topic" />}</TableCell>
              <TableCell>{<IntlMessages id="question.level" />}</TableCell>
              <TableCell>
                {<IntlMessages id="components.note.subject" />}
              </TableCell>
              <TableCell>
                {<IntlMessages id="question.affectation.number" />}
              </TableCell>
              <TableCell align="right">
                <IntlMessages id="stuppUser.action" />
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.props.questions.map((question) => (
              <QuestionListItems
                question={question}
                levels={this.props.levels}
                profSubjectList={this.props.profSubjectList}
                profLevels={this.props.profLevels}
                classLevels={this.props.classLevels}
              />
            ))}
          </TableBody>
        </Table>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
    settings: state.settings.locale,
    classLevels: state.ClassLevels.remoteLevels,
  };
};

export default connect(mapStateToProps)(QuestionsTable);
