import React from 'react';
import asyncComponent from '../../../util/asyncComponent';
import { Redirect, Route, Switch } from 'react-router-dom';
import Can from '../../../can';
import { RoleContext } from "../../../Context";
import Lesson from "./routes/Lesson/Lesson"
import Libraries from "./routes/Libraries/Libraries"
import VirtualClasses from "./routes/VirtualClasses/VirtualClasses"



const Learning = ({ match, estabModule }) =>(

  <div className="app-wrapper">
    <Switch>
      <Redirect exact from={`${match.url}/`} to={`${match.url}/lesson`} />
      <Route path={`${match.url}/lesson`} render={() =>
        <RoleContext.Consumer>
          {({ role }) => (
            < Can
              role={role}
              perform="module-nav-access"
              data={{
                mod: 'e-learning',
                moduleList: estabModule
              }}
              yes={() => (

                < Can
                  role={role}
                  perform={`module-nav-lesson`}
                  yes={() => (
                    < Lesson match={match} />
                  )}
                  no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                />
              )}
              no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
            />
          )}
        </RoleContext.Consumer>
      } />
      <Route path={`${match.url}/virtual_classes`} render={() =>
        <RoleContext.Consumer>
          {({ role }) => (
            < Can
              role={role}
              perform="module-nav-access"
              data={{
                mod: 'e-learning',
                moduleList: estabModule
              }}
              yes={() => (
                < Can
                  role={role}
                  perform={`module-nav-virtual_classes`}
                  yes={() => (
                    < VirtualClasses match={match} />
                  )}
                  no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                />
              )}
              no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
            />
          )}
        </RoleContext.Consumer>
      } />

      <Route path={`${match.url}/e-libraries`} render={() =>
        <RoleContext.Consumer>
          {({ role }) => (
            < Can
              role={role}
              perform="module-nav-access"
              data={{
                mod: 'e-learning',
                moduleList: estabModule
              }}
              yes={() => (
                < Can
                  role={role}
                  perform={`module-nav-e-libraries`}
                  yes={() => (
                    < Libraries match={match} />
                  )}
                  no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}

                />
              )}
              no={() => <Route component={asyncComponent(() => import('../../../components/Error404'))} />}
            />
          )}
        </RoleContext.Consumer>
      } />
    </Switch>
  </div>
)





export default Learning;
