import React from "react";
import IntlMessages from "../../../../../util/IntlMessages";
import CardBox from "../../../../../components/CardBox/index";
import Can from "../../../../../can";
import { RoleContext } from "../../../../../Context";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import TextField from "@material-ui/core/TextField";
import ImageGridList from "./ImageGridList";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import DateRangeComponent from "./DateRangeComponent";
import Button from "@material-ui/core/Button";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import { connect } from "react-redux";
import { addServiceV2 } from "../../../../../actions/ServiceAction";
import FormHelperText from "@material-ui/core/FormHelperText";
import { UncontrolledAlert } from "reactstrap";

const typeList = [
  {
    value: "obligatoire",
    name: <IntlMessages id="service.mandatory" />,
  },
  {
    value: "optionnel",
    name: <IntlMessages id="service.optional" />,
  },
];

class AddService extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      mensuel: false,
      trimestriel: false,
      hebdomadaire: false,
      annuel: false,
      frequencyVal: "",
      daily: false,
      nameFrService: "",
      typeService: "",
      pathImgService: "",
      currency: [],
      idCurrency: 0,
      vatService: "",
      otherVatService: "",
      frequency: [],
      comment: "",
      priceService: "",
      startDateService: "",
      endDateService: "",
      idFrequency: 0,
      minimumNights: 1,
      alerte: false,
      nameError: false,
    };
    this.openAddModal = this.openAddModal.bind(this);
    this.handleChangeRadio = this.handleChangeRadio.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleIcon = this.handleIcon.bind(this);
    this.setDate = this.setDate.bind(this);
    this.handleArchive = this.handleArchive.bind(this);
  }

  setDate(startDate, endDate) {
    this.setState({ startDateService: startDate, endDateService: endDate });
  }

  handleIcon(nameIcon) {
    this.setState({ pathImgService: nameIcon });
  }
  openAddModal() {
    this.setState((previousState) => ({
      open: !previousState.open,
    }));
  }

  handleSubmit(event) {
    event.preventDefault();

    if (this.state.pathImgService == "") {
      this.setState({ alerte: true });
      setTimeout(() => {
        this.setState({ alerte: false });
      }, 2000);
    } else {
      let dataPrestation = {
        name_fr_service: this.state.nameFrService,
        name_an_service: "string",
        name_ar_service: "string",
        comment: this.state.comment,
        path_img_service: this.state.pathImgService,
        price_service: parseInt(this.state.priceService),
        vat_service: parseInt(this.state.vatService),
        other_vat_service: this.state.otherVatService,
        start_date_service: this.state.startDateService,
        end_date_service: this.state.endDateService,
        type_service: this.state.typeService,
        status_service: true,
        fk_id_establishment: this.props.userProfile.establishment_id,
        fk_id_schol_year_v2: 1,
        fk_id_frequency: this.state.idFrequency,
        fk_id_currency: this.state.idCurrency,
      };
      this.props.dispatch(
        addServiceV2(dataPrestation, this.props.userProfile.establishment_id)
      );
      this.openAddModal();
      this.setState({
        vatService: "",
        otherVatService: "",
        comment: "",
        nameFrService: "",
        typeService: "",
        idCurrency: 0,
        priceService: "",
        idFrequency: 0,
        frequencyVal: "",
      });
    }
  }

  handleArchive(event) {
    event.preventDefault();
    if (this.state.pathImgService == "") {
      this.setState({ alerte: true });
      setTimeout(() => {
        this.setState({ alerte: false });
      }, 2000);
    } else {
      let dataPrestation = {
        name_fr_service: this.state.nameFrService,
        name_an_service: "string",
        name_ar_service: "string",
        comment: this.state.comment,
        path_img_service: this.state.pathImgService,
        price_service: parseInt(this.state.priceService),
        vat_service: parseInt(this.state.vatService),
        other_vat_service: this.state.otherVatService,
        start_date_service: this.state.startDateService,
        end_date_service: this.state.endDateService,
        type_service: this.state.typeService,
        status_service: false,
        fk_id_establishment: this.props.userProfile.establishment_id,
        fk_id_schol_year_v2: 3,
        fk_id_frequency: this.state.idFrequency,
        fk_id_currency: this.state.idCurrency,
      };
      this.props.dispatch(
        addServiceV2(dataPrestation, this.props.userProfile.establishment_id)
      );
      this.openAddModal();
      this.setState({
        vatService: "",
        otherVatService: "",
        comment: "",
        nameFrService: "",
        typeService: "",
        idCurrency: 0,
        priceService: "",
      });
    }
  }

  handleChange = (name) => (event) => {
    if (name == "nameFrService") {
      let nameError = false;
      nameError =
        this.props.services.filter(
          (element) => element.name_fr_service === event.target.value.trim()
        ).length > 0;

      this.setState({ [name]: event.target.value, nameError: nameError });
    } else {
      this.setState({ [name]: event.target.value });
    }
  };

  handleChangeRadio = (name) => (event) => {
    let item = this.props.frequency.filter(
      (element) => element.name_fr_frequency === event.target.value
    );
    this.setState({
      idFrequency: item[0].id,
      frequencyVal: event.target.value,
    });

    if (item[0].id === 1) {
      this.setState({
        minimumNights: 1,
      });
    } else if (item[0].id === 2) {
      this.setState({
        minimumNights: 6,
      });
    } else if (item[0].id === 3) {
      this.setState({
        minimumNights: 60,
      });
    } else if (item[0].id === 4) {
      this.setState({
        minimumNights: 29,
      });
    } else if (item[0].id === 5) {
      this.setState({
        minimumNights: 90,
      });
    } else if (item[0].id === 6) {
      this.setState({
        minimumNights: 180,
      });
    } else if (item[0].id === 7) {
      this.setState({
        minimumNights: 360,
      });
    }
  };

  render() {
    return (
      <div>
        <RoleContext.Consumer>
          {({ role }) => (
            <Can
              role={role}
              perform="add-service"
              yes={() => (
                <div>
                  <form autoComplete="off" onSubmit={this.handleSubmit}>
                    <div className="d-flex justify-content-start align-items-center">
                      <h1>
                        <b>
                          <IntlMessages id="new.service" />
                        </b>
                      </h1>
                      &nbsp;&nbsp;&nbsp;
                      <Fab
                        size="small"
                        color="primary"
                        aria-label="Add"
                        onClick={this.openAddModal}
                      >
                        {this.state.open ? <RemoveSharpIcon /> : <AddIcon />}
                      </Fab>
                    </div>
                    <br />
                    {this.state.open ? (
                      <>
                        {" "}
                        <CardBox styleName=" text-black  ">
                          <div className="d-flex flex-column  ">
                            <label
                              htmlFor="comment"
                              style={{ fontSize: "20px", color: "#0B4786" }}
                            >
                              <IntlMessages id="service.information" />
                            </label>
                            <div className="d-flex flex-wrap  align-items-center ">
                              <div className="p-2 col-md-4   ">
                                <TextField
                                  required
                                  id="nameFrService"
                                  error={this.state.nameError}
                                  label={
                                    <IntlMessages id="stuppUser.formadd.name" />
                                  }
                                  value={this.state.nameFrService}
                                  onChange={this.handleChange("nameFrService")}
                                  margin="normal"
                                  fullWidth
                                />
                                <FormHelperText error={this.state.nameError}>
                                  {this.state.nameError
                                    ? "Nom prestation dÃ©ja existe"
                                    : ""}
                                </FormHelperText>
                              </div>
                              <div className="p-2 col-md-3 px-md-2  ">
                                <TextField
                                  required
                                  select
                                  id="typeService"
                                  name="typeService"
                                  label={<IntlMessages id="service.type" />}
                                  value={this.state.typeService}
                                  onChange={this.handleChange("typeService")}
                                  margin="normal"
                                  fullWidth
                                  SelectProps={{
                                    native: true,
                                  }}
                                >
                                  <option disabled></option>
                                  {typeList.map((type, index) => (
                                    <option key={index} value={type.value}>
                                      {type.value}
                                    </option>
                                  ))}
                                </TextField>
                              </div>
                              <div className="ml-5 p-2 col-md-4 flex-wrap align-items-center ">
                                <label
                                  style={{ color: "#DCDCDC", margin: "20px" }}
                                >
                                  <IntlMessages id="service.icon" />
                                </label>

                                <ImageGridList handleIcon={this.handleIcon} />
                              </div>
                            </div>

                            <hr
                              style={{
                                width: "100%",
                                margin: "auto",
                                marginTop: "5%",
                                marginBottom: "5%",
                                border: "1px dashed #979A9A",
                                paddingLeft: "-100%",
                              }}
                            />

                            <label
                              htmlFor="comment"
                              style={{
                                fontSize: "20px",
                                color: "#0B4786",
                                marginTop: "10px",
                              }}
                            >
                              <IntlMessages id="service.currency" />
                            </label>
                            <div className="d-flex flex-wrap justify-content-start align-items-center  ">
                              <div className="p-2 col-md-3 ">
                                <TextField
                                  required
                                  select
                                  id="idCurrency"
                                  name="idCurrency"
                                  label={<IntlMessages id="service.currency" />}
                                  value={this.state.idCurrency}
                                  onChange={this.handleChange("idCurrency")}
                                  margin="normal"
                                  fullWidth
                                  SelectProps={{
                                    native: true,
                                  }}
                                >
                                  <option></option>
                                  {this.props.currency.map((option, index) => (
                                    <option key={index} value={option.id}>
                                      {option.sign_currency}
                                    </option>
                                  ))}
                                </TextField>
                              </div>

                              <div className="p-2 col-md-3">
                                <TextField
                                  required
                                  id="vatService"
                                  type="number"
                                  label={
                                    <IntlMessages id="etablishments.info.tva" />
                                  }
                                  value={this.state.vatService}
                                  onChange={this.handleChange("vatService")}
                                  error={this.state.vatService < 0}
                                  inputProps={{
                                    min: 0,
                                  }}
                                  margin="normal"
                                  fullWidth
                                />
                              </div>

                              <div className="p-2 col-md-3">
                                <TextField
                                  id="otherVatService"
                                  type="number"
                                  label={
                                    <IntlMessages id="service.other.tax" />
                                  }
                                  value={this.state.otherVatService}
                                  onChange={this.handleChange(
                                    "otherVatService"
                                  )}
                                  error={this.state.otherVatService < 0}
                                  inputProps={{
                                    min: 0,
                                  }}
                                  margin="normal"
                                  fullWidth
                                />
                              </div>

                              <div className="p-2 col-md-3"></div>
                            </div>

                            <hr
                              style={{
                                width: "100%",
                                margin: "auto",
                                marginTop: "5%",
                                marginBottom: "5%",
                                border: "1px dashed #979A9A",
                                paddingLeft: "-100%",
                              }}
                            />

                            <div className="d-flex flex-column ">
                              <label
                                htmlFor="comment"
                                style={{ fontSize: "20px", color: "#0B4786" }}
                              >
                                <IntlMessages id="sidebar.components.service" />
                              </label>
                              <div className="d-flex flex-column ">
                                <RadioGroup
                                  aria-label="anchorOriginVertical"
                                  name="anchorOriginVertical"
                                  value={this.state.name_fr_frequency}
                                  onChange={this.handleChangeRadio()}
                                >
                                  {this.props.frequency.map(
                                    (element, index) => (
                                      <div key={index}>
                                        <div
                                          className="d-flex flex-wrap  "
                                          key={index}
                                        >
                                          <FormControlLabel
                                            value={element.name_fr_frequency}
                                            control={<Radio required />}
                                            label={element.name_fr_frequency}
                                          />

                                          {this.state.frequencyVal ===
                                          element.name_fr_frequency ? (
                                            <>
                                              <div className="p-2 bd-highlight">
                                                <DateRangeComponent
                                                  setDate={this.setDate}
                                                  minimumNights={
                                                    this.state.minimumNights
                                                  }
                                                />
                                              </div>
                                              <div className="p-2 bd-highlight">
                                                <TextField
                                                  required
                                                  name="priceService"
                                                  id="priceService"
                                                  type="number"
                                                  value={
                                                    this.state.priceService
                                                  }
                                                  onChange={this.handleChange(
                                                    "priceService"
                                                  )}
                                                  error={
                                                    this.state.priceService < 0
                                                  }
                                                  inputProps={{
                                                    min: 0,
                                                  }}
                                                  label={
                                                    <IntlMessages id="service.price" />
                                                  }
                                                  variant="outlined"
                                                />
                                              </div>
                                            </>
                                          ) : (
                                            ""
                                          )}
                                        </div>
                                      </div>
                                    )
                                  )}
                                </RadioGroup>
                              </div>
                            </div>

                            <hr
                              style={{
                                width: "100%",
                                margin: "auto",
                                marginTop: "5%",
                                marginBottom: "5%",
                                border: "1px dashed #979A9A",
                                paddingLeft: "-100%",
                              }}
                            />

                            <div className="d-flex  flex-column justify-content-center">
                              <label
                                htmlFor="comment"
                                style={{ fontSize: "20px", color: "#0B4786" }}
                              >
                                <IntlMessages id="toDo.comments" />
                              </label>
                              <textarea
                                rows="3"
                                value={this.state.comment}
                                onChange={this.handleChange("comment")}
                                style={{
                                  borderRadius: "20px",
                                  marginTop: "10px",
                                }}
                              ></textarea>
                            </div>
                          </div>
                        </CardBox>
                        {this.state.alerte ? (
                          <UncontrolledAlert className="alert-addon-card bg-danger bg-danger text-white shadow-lg">
                            <span className="icon-addon alert-addon">
                              <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                            </span>
                            <span className="d-inline-block">
                              Veuillez choisir une photo
                            </span>
                          </UncontrolledAlert>
                        ) : (
                          ""
                        )}
                        <div className="d-flex flex-wrap justify-content-end ">
                          <Button
                            variant="contained"
                            onClick={this.openAddModal}
                            style={{
                              borderBottomLeftRadius: "16px",
                              borderBottomRightRadius: "16px",
                              borderTopLeftRadius: "16px",
                              borderTopRightRadius: "16px",
                              width: "10%",
                              height: "20%",
                            }}
                          >
                            {
                              <IntlMessages id="components.establishments.formadd.buttonCancel" />
                            }
                          </Button>
                          &nbsp;&nbsp;
                          <Button
                            variant="contained"
                            className="bg-grey text-white "
                            style={{
                              borderBottomLeftRadius: "16px",
                              borderBottomRightRadius: "16px",
                              borderTopLeftRadius: "16px",
                              borderTopRightRadius: "16px",
                              width: "10%",
                              height: "6%",
                            }}
                            onClick={this.handleArchive}
                          >
                            {<IntlMessages id="service.button.archive" />}
                          </Button>
                          &nbsp;&nbsp;
                          <Button
                            disabled={this.state.nameError}
                            variant="contained"
                            style={{
                              borderBottomLeftRadius: "16px",
                              borderBottomRightRadius: "16px",
                              borderTopLeftRadius: "16px",
                              borderTopRightRadius: "16px",
                              width: "10%",
                              height: "6%",
                            }}
                            className=" bg-indigo text-white "
                            type="submit"
                          >
                            <IntlMessages id="service.button.publish" />
                          </Button>
                        </div>{" "}
                      </>
                    ) : (
                      ""
                    )}
                  </form>
                </div>
              )}
            />
          )}
        </RoleContext.Consumer>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
  };
};

export default connect(mapStateToProps)(AddService);
