import React from 'react';
import ContainerHeader from "../../../../../components/ContainerHeader/index";
import IntlMessages from "../../../../../util/IntlMessages";
import { connect } from 'react-redux';
import { getLevels } from '../../../../../actions/classLevelAction';
import { getUserProfile } from '../../../../../actions/Auth';
import axios from 'axios';
import baseUrl from '../../../../../config/config';
import { getSections } from '../../../../../actions/sectionAction';
import { getClassesByEstablishmentId } from '../../../../../actions/classeAction';
import LessonList from './LessonList';
import { getLessons } from '../../../../../actions/LessonAction';


class Lesson extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      estab_type_id: 0,
    }

  }

  componentDidMount() {
    axios
      .get(
        `${baseUrl.baseUrl}/establishments/` +
        this.props.userProfile.establishment_id +
        `?access_token=${localStorage.token}`
      ).then(res => this.setState({
        estab_type_id: res.data.estab_type_id
      }))
    this.props.getLevels();
    this.props.getSections();
    this.props.getClassesByEstablishmentId(this.props.userProfile.establishment_id);
    this.props.getLessons();


  }


  render() {
    const { levels, sections, classes, lessons } = this.props;
    return (
      <div className="app-wrapper">
        <ContainerHeader
          match={this.props.match}
          title={<IntlMessages id="sidebar.components.e-learning" />}
        />

        <LessonList lessons={lessons} estab_type_id={this.state.estab_type_id} levels={levels} sections={sections} classes={classes} />
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    classes: state.classes,
    userProfile: state.auth.userProfile,
    levels: state.ClassLevels.remoteLevels,
    sections: state.classSections.remoteSections,
    lessons: state.lessons.remoteLessons

  };
}
export default connect(
  mapStateToProps,
  {
    getLevels,
    getUserProfile,
    getSections,
    getClassesByEstablishmentId,
    getLessons
  }
)(Lesson);
