import React from "react";
import moment from "moment";
import IconButton from "@material-ui/core/IconButton";
import _ from "lodash";
import { connect } from "react-redux";
import DeleteLesson from "./DeleteLesson";
import DownloadFiles from "./DownloadFiles";
import { deleteLesson } from "../../../actions/LessonAction";
import Can from "../../../can";
import { RoleContext } from "../../../Context";
import coursStudent from "./Assets/coursStudent.webp";
import matiereScolaire from "./Assets/matiereScolaire.webp";

class LessonGridItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      url: "",
      anchorEl: undefined,
      menuState: false,
      deleteIsopen: false,
      opendownload: false,
    };
    this.handleShowDeleteModal = this.handleShowDeleteModal.bind(this);
    this.cancelModal = this.cancelModal.bind(this);
    this.handleDeleteLesson = this.handleDeleteLesson.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleShowDownloadModal = this.handleShowDownloadModal.bind(this);
  }

  onOptionMenuSelect = (event) => {
    this.setState({
      menuState: true,
      anchorEl: event.currentTarget,
    });
  };
  handleRequestClose = () => {
    this.setState({ menuState: false });
  };
  handleDeleteLesson = () => {
    this.props.deleteLesson(this.props.lesson.id);
    this.setState({ deleteIsopen: false });
  };
  cancelModal() {
    this.setState({ deleteIsopen: false });
  }
  handleShowDeleteModal = () => {
    this.setState({ menuState: false, deleteIsopen: true });
  };
  handleShowDownloadModal = () => {
    this.setState({
      opendownload: true,
      menuState: false,
      anchorEl: undefined,
    });
  };

  handleCancel = () => {
    this.setState({
      opendownload: false,
    });
  };

  render() {
    var imageList = [coursStudent, matiereScolaire];
    var imageListItem = imageList[Math.floor(Math.random() * imageList.length)];
    const { name, details, creation_date } = this.props.lesson;
    const { anchorEl, menuState } = this.state;
    const { divEyeClassname, divEyeClassnameSpace } = this.props;

    return (
      <RoleContext.Consumer>
        {({ role }) => (
          <div className="col-xl-3 col-md-4 col-sm-6 col-12">
            <div
              className="card product-item  "
              style={{
                borderTopLeftRadius: "20px",
                borderTopRightRadius: "20px",
                borderBottomLeftRadius: "20px",
                borderBottomRightRadius: "20px",
              }}
            >
              <div
                className="card-header border-0 p-0"
                style={{
                  borderTopLeftRadius: "20px",
                  borderTopRightRadius: "20px",
                }}
              >
                <div className="card-image">
                  <div className="grid-thumb-equal">
                    <span className="grid-thumb-cover jr-link">
                      <img alt="Remy Sharp" src={imageListItem} />
                    </span>
                  </div>
                </div>
              </div>
              <div className="card-body">
                <div className="product-details">
                  <div className="row">
                    <div className="col-md-10 col-sm-10 col-10">
                      <h3 className="card-title fw-regular">
                        {"Sujet :" + name}{" "}
                      </h3>
                    </div>
                  </div>
                  <div className="d-flex ">
                    <h4 className="text-success">
                      {moment(creation_date).format("L")}{" "}
                    </h4>
                  </div>
                  <p>{"Descriptionâ€‰:" + details}</p>
                  <div className="d-flex flex-row  col-md-12 justify-content-between ">
                    <div className={divEyeClassnameSpace}></div>
                    <div className={divEyeClassname}>
                      <Can
                        role={role}
                        perform="lesson-filter-class:visit"
                        yes={() => (
                          <IconButton
                            size="small"
                            className="icon-btn"
                            onClick={(e) => {
                              this.setState({ menuState: false });
                              this.props.handleEdit(this.props.lesson);
                            }}
                          >
                            <i
                              className="zmdi zmdi-edit"
                              style={{ color: "#5422b9" }}
                            />
                          </IconButton>
                        )}
                      />
                      <Can
                        role={role}
                        perform="lesson-filter-class:visit"
                        yes={() => (
                          <IconButton
                            size="small"
                            className="icon-btn"
                            onClick={this.handleShowDeleteModal}
                          >
                            <i
                              className="zmdi zmdi-delete"
                              style={{ color: "#5422b9" }}
                            />
                          </IconButton>
                        )}
                      />
                      <IconButton
                        size="small"
                        className="icon-btn"
                        onClick={this.handleShowDownloadModal}
                      >
                        <i
                          className="zmdi zmdi-eye"
                          style={{ color: "#5422b9" }}
                        />
                      </IconButton>{" "}
                    </div>
                  </div>
                </div>
              </div>
            </div>
            {this.state.deleteIsopen ? (
              <DeleteLesson
                cancelModal={this.cancelModal}
                deleteIsopen={this.state.deleteIsopen}
                handleDeleteLesson={this.handleDeleteLesson}
              />
            ) : (
              ""
            )}

            {this.state.opendownload === true ? (
              <DownloadFiles
                opendownload={this.state.opendownload}
                handleCancel={this.handleCancel}
                lessonId={this.props.lesson.id}
              />
            ) : (
              ""
            )}
          </div>
        )}
      </RoleContext.Consumer>
    );
  }
}

function mapStateToProps(state) {
  return {
    classes: state.classes,
    userProfile: state.auth.userProfile,
    levels: state.ClassLevels.remoteLevels,
    sections: state.classSections.remoteSections,
    lessons: state.lessons.remoteLessons,
    successStatus: state.alert.success,
    errorStatus: state.alert.error,
    message: state.alert.message,
  };
}
export default connect(
  mapStateToProps,
  {
    deleteLesson,
  }
)(LessonGridItem);
