    composeReclam,
    folderReclamEnvoyées,
    folderReclamRecues,
    showMessage,
    alertMessage,
    alertMessageAR,
    alertMessageEN,
  } = complaint;
  const { locale } = settings;
  return {
    locale,
    showMessage,
    alertMessage,
    alertMessageAR,
    composeReclam,
    folderReclamEnvoyées,
    folderReclamRecues,
    alertMessageEN,
  };
};

