import React from 'react'
import TableCell from "@material-ui/core/TableCell";
import TableRow from "@material-ui/core/TableRow";
import IconButton from '@material-ui/core/IconButton';
import { getNameFromID } from '../../../../../actions/countriesAction';

class SubjectItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isOpenModalDelete: false
        };
        this.handleDelete = this.handleDelete.bind(this);
    }

    handleDelete= () => {
        this.setState({ isOpenModalDelete: true })
    }

    render() {
        let { subjectModules, subject } = this.props
        return (
            <>
                <TableRow key={subject.id} >
                    <TableCell align="left">{subject.name}</TableCell>
                    <TableCell align="left">{subject.wording}</TableCell>
                    <TableCell align="left">{getNameFromID(subjectModules, this.props.subject.fk_id_subjects_module)}</TableCell>
                    <TableCell align="center">
                        <IconButton
                            size="medium"
                            className="icon-btn"
                            onClick={(e)=>this.props.handleEdit(subject,e)}
                        >
                            <i
                                className="zmdi zmdi-edit "
                                style={{ color: 'text-grey' }}
                            />
                        </IconButton>
                        &nbsp; | &nbsp;
                        <IconButton
                            size="medium"
                            className="icon-btn"
                            onClick={(e)=>this.props.handleDelete(subject,e)}
                        >
                            <i
                                className="zmdi zmdi-delete"
                                style={{ color: 'text-grey' }}
                            />
                        </IconButton>
                        &nbsp; | &nbsp;
                        <IconButton
                            size="medium"
                            className="icon-btn"
                            onClick={(e)=>this.props.handleAssignment(subject,e)}
                        >
                            <i
                                className="zmdi zmdi-assignment"
                                style={{ color: 'text-grey' }}
                            />
                        </IconButton>
                    </TableCell>
                </TableRow>

            </>
        )
    }
}

export default SubjectItem;
