import IntlMessages from '../../../../../util/IntlMessages';
import React, { Component } from 'react';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button';
import InputLabel from '@material-ui/core/InputLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import moment from 'moment';
import { RoleContext } from '../../../../../Context';
import Can from '../../../../../can';
import { NavLink } from 'react-router-dom';
import {
  roleIdProfessor,
  roleIdAdmin,
  roleIdParent,
  roleIdStudent,
  roleIdSupervisor,
  roleIdDirector,
} from '../../../../../config/config';

class PermissionListItem extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  havePermission(idRole, idPermission) {
    let have = true;

    have = this.props.permission.roles.includes(idRole);
    let icone = have ? (
      <i
        className="zmdi zmdi-check"
        onClick={(e) => {
          this.props.deletePermission(idPermission, idRole);
        }}
      ></i>
    ) : (
      <i
        className="zmdi zmdi-block"
        onClick={(e) => {
          this.props.addPermission(idPermission, idRole);
        }}
      ></i>
    );
    return icone;
  }
  render() {
    const { permission } = this.props;
    return (
      <TableRow>
        {/* <TableCell>{permission.permissionFr}</TableCell> */}

        <TableCell>
          {this.props.settings.languageId == 'tunisia'
            ? permission.permissionAr
            : this.props.settings.languageId == 'french'
            ? permission.permissionFr
            : permission.permissionAng}
        </TableCell>

        <TableCell> {this.havePermission(roleIdAdmin, permission.id)} </TableCell>
        {/* <TableCell> {this.havePermission(roleIdDirector, permission.id)} </TableCell>
        <TableCell>{this.havePermission(roleIdSupervisor, permission.id)} </TableCell>
        <TableCell> {this.havePermission(roleIdProfessor, permission.id)} </TableCell>
        <TableCell>{this.havePermission(roleIdStudent, permission.id)} </TableCell>
        <TableCell> {this.havePermission(roleIdParent, permission.id)} </TableCell> */}
      </TableRow>
    );
  }
}

export default PermissionListItem;
