import React from "react";
import IntlMessages from "../../../util/IntlMessages";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import AddVirtualClass from "./AddVirtualClass";
import VirtualClassList from "./VirtualClassList";
import {
  getClassesVirtual,
  getClassVirtualByEstablishmentId,
  addClassVirtual,
} from "../../../actions/VirtualClassAction";

import { connect } from "react-redux";
import { getSectionFromLevel } from "../../../actions/sectionAction";
import axios from "axios";
import baseUrl from "../../../config/config";

import { getSections } from "../../../actions/sectionAction";
import {
  getLevels,
  getLevelsVirtualClass,
} from "../../../actions/classLevelAction";
import { getClassesByEstablishmentId } from "../../../actions/classeAction";
import { UncontrolledAlert } from "reactstrap";
import Can from "../../../can";
import { RoleContext } from "../../../Context";
import Input from "@material-ui/icons/Input";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/moment";
import { subjectsByLevelBySection } from "../../../actions/subjectAction";
import _ from "lodash";
import { roleIdSuperAdmin } from "../../../config/config";
import { roleIdAdmin } from "../../../config/config";
import { roleIdProfessor } from "../../../config/config";
import { roleIdParent } from "../../../config/config";
import { roleIdStudent } from "../../../config/config";
import { roleIdSupervisor } from "../../../config/config";
import { roleIdDirector } from "../../../config/config";
import Paper from "@material-ui/core/Paper";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import moment from "moment";

const format = "hh:mm:ss";

class VirtualClasses extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      alerte: false,
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: true,
      class_object: "",
      level_id: "",
      section_id: "",
      professor_id: "",
      class_id: "",
      subject_id: "",
      class_url: "",
      class_password: "",
      class_date: new Date(),
      selectedStartTime: new Date(),
      selectedEndTime: new Date(),
      startTime: new Date().getHours() + ":" + new Date().getMinutes(),
      endTime: new Date().getHours() + ":" + new Date().getMinutes(),
      studentsectionByLevels: [],
      adminsectionByLevels: [],
      Disable_studentsection: true,
      Disable_admin_section: true,
      levelsbyestablishment: [],
      studentClassesByLevelSectionID: [],
      adminClassesByLevelSectionID: [],
      subjectList: [],
      establishmentProfessor: [],
      professorIdFilter: "",
      professorClassesList: [],
      professor_class_id: 0,
      admin_level_id: "",
      admin_class_id: "",
      admin_section_id: "",
      statutId: 0,
      professorClassesAdd: [],
      profSubjects: [],
      selectedFilterDateProfessor: null,

      student_subject_id: "",
      selectedFilterDateStudent: null,
    };
    this.addClassShowModal = this.addClassShowModal.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleChangeDate = this.handleChangeDate.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleStartTimeChange = this.handleStartTimeChange.bind(this);
    this.handleEndTimeChange = this.handleEndTimeChange.bind(this);
    this.addVirtualClass = this.addVirtualClass.bind(this);
    this.handleChangestudentSection = this.handleChangestudentSection.bind(
      this
    );
    this.handleChangestudentLevel = this.handleChangestudentLevel.bind(this);
    this.handleChangeClass = this.handleChangeClass.bind(this);
    this.handleChangeSubject = this.handleChangeSubject.bind(this);
    this.handleChangeProfessor = this.handleChangeProfessor.bind(this);
    this.handleChangeDateProfessor = this.handleChangeDateProfessor.bind(this);
    this.handleChangeVirtualClassSubjectStudent = this.handleChangeVirtualClassSubjectStudent.bind(
      this
    );
    this.handleChangeDateStudent = this.handleChangeDateStudent.bind(this);
    this.handleChangeStatut = this.handleChangeStatut.bind(this);
  }

  addVirtualClass = () => {
    var data = {};
    data.object = this.state.class_object;
    data.class_id = this.state.class_id;
    data.subject_id = this.state.subject_id;
    data.class_url = this.state.class_url;
    data.password = this.state.class_password;
    data.date_virtual_class = this.state.class_date;
    data.start_time_class = this.state.startTime;
    data.end_time_class = this.state.endTime;
    data.professor_id = this.state.professor_id;
    data.status = true;
    data.establishment_id = this.props.userProfile.establishment_id;
    data.virtual_class_status = "programÃ©e";

    this.props.addClassVirtual(data);
    this.setState({
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: false,
      class_object: "",
      level_id: "",
      section_id: "",
      professor_id: "",
      class_id: "",
      subject_id: "",
      class_url: "",
      class_password: "",
      class_date: new Date(),
      selectedStartTime: new Date(),
      selectedEndTime: new Date(),
      startTime: "",
      endTime: "",
      studentsectionByLevels: [],
      Disable_studentsection: true,
      // levelsbyestablishment: [],
      studentClassesByLevelSectionID: [],
      // subjectList: [],
      // establishmentProfessor: [],
    });
  };
  handleChange = (name) => (event) => {
    this.setState({ [name]: event.target.value });
  };
  handleChangeProfessor = (name) => (event) => {
    this.setState({ professor_id: event.target.value });
  };

  handleChangeDate = (date) => {
    this.setState({ class_date: date });
  };
  handleStartTimeChange = (time) => {
    this.setState({ selectedStartTime: time, startTime: time.format("LT") });
  };

  handleEndTimeChange = (time) => {
    this.setState({ selectedEndTime: time, endTime: time.format("LT") });
  };

  addClassShowModal = () => {
    this.setState({ addIsopen: true });
  };

  editClassShowModal = () => {
    this.setState({ editIsopen: true });
  };
  handleCancel = () => {
    this.setState({
      addIsopen: false,
      editIsopen: false,
      deleteIsopen: false,
      showIsopen: false,
    });
  };

  handleChangeSubject = (name) => (event) => {
    this.setState({ [name]: event.target.value, professor_id: "" });
    let apiEndpoint =
      `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][and][0][subject_id]=` +
      event.target.value +
      `&filter[where][and][1][class_id]=` +
      this.state.class_id +
      `&filter[include][professor][profile][user]`;
    axios.get(apiEndpoint).then((res) => {
      var establishmentProfessor = res.data.filter((element) => element.status);
      this.setState({ establishmentProfessor });
    });
  };

  handleChangestudentSection = (name) => (event) => {
    if (name === "section_id") {
      this.setState({ [name]: event.target.value });
      const levelID = this.state.level_id;
      const sectionID = event.target.value;
      var studentfiltredClasses = this.props.classes.filter(
        (classe) =>
          classe.level_id === levelID &&
          classe.section_id === sectionID &&
          classe.status
      );
      var professorClassesAdd = this.state.professorClassesList.filter(
        (classe) =>
          classe.level_id === levelID &&
          classe.section_id === sectionID &&
          classe.status
      );
      this.setState({
        studentClassesByLevelSectionID: studentfiltredClasses,
        professorClassesAdd,
      });
    } else if (name === "admin_section_id") {
      this.setState({ [name]: event.target.value });
      const levelID = this.state.admin_level_id;
      const sectionID = event.target.value;
      var adminfiltredClasses = this.props.classes.filter(
        (classe) =>
          classe.level_id === levelID &&
          classe.section_id === sectionID &&
          classe.status
      );
      this.setState({ adminClassesByLevelSectionID: adminfiltredClasses });
    }
  };
  handleChangestudentLevel = (name) => (event) => {
    if (name === "level_id") {
      this.setState({
        section_id: 0,
        class_id: 0,
        studentClassesByLevelSectionID: [],
        professorClassesAdd: [],
        profSubjects: [],
        establishmentProfessor: [],
      });
      if (event.target.value > 10) {
        this.setState({ [name]: event.target.value });
        this.setState({ Disable_studentsection: false });
        var studentSectionbyLevel = getSectionFromLevel(
          this.props.classSections,
          event.target.value
        );
        this.setState({
          studentsectionByLevels: studentSectionbyLevel,
        });
      } else {
        this.setState({
          level_id: event.target.value,
          Disable_studentsection: true,
          section_id: 0,
        });
        const levelID = event.target.value;
        var studentfiltredClasses = this.props.classes.filter(
          (classe) => classe.level_id === levelID && classe.status
        );
        var professorClassesAdd = this.state.professorClassesList.filter(
          (classe) => classe.level_id === levelID && classe.status
        );
        this.setState({
          studentClassesByLevelSectionID: studentfiltredClasses,
          professorClassesAdd,
        });
      }
    } else if (name == "admin_level_id") {
      this.setState({ admin_section_id: 0, admin_class_id: 0, alerte: true });
      setTimeout(() => {
        this.setState({ alerte: false });
      }, 2000);
      if (event.target.value > 10) {
        this.setState({ admin_level_id: event.target.value });
        this.setState({ Disable_admin_section: false });
        var adminSectionbyLevel = getSectionFromLevel(
          this.props.classSections,
          event.target.value
        );
        this.setState({ adminsectionByLevels: adminSectionbyLevel });
      } else {
        this.setState({
          admin_level_id: event.target.value,
          Disable_admin_section: true,
          admin_section_id: 0,
        });
        const levelID = event.target.value;
        var adminfiltredClasses = this.props.classes.filter(
          (classe) => classe.level_id === levelID && classe.status
        );
        this.setState({
          adminClassesByLevelSectionID: adminfiltredClasses,
        });
      }
    }
  };
  handleChangeStatut = (name) => (event) => {
    this.setState({ [name]: event.target.value });
  };

  handleChangeClass = (name) => (event) => {
    if (name == "class_id") {
      this.setState({
        subject_id: "",
        profSubjects: [],
        establishmentProfessor: [],
        professor_id: "",
      });
      if (this.props.userProfile.role_id === roleIdAdmin) {
        this.setState({
          establishment_id: this.props.userProfile.establishment_id,
        });
      } else {
        let apiEndpoint1 =
          `${baseUrl.baseUrl}/classes_professors?access_token=${localStorage.token}&filter[where][and][0][class_id]=` +
          event.target.value +
          `&filter[where][and][1][professor_id]=` +
          this.state.professorIdFilter +
          `&filter[include][subject]`;
        axios.get(apiEndpoint1).then((res) => {
          let profSubjects = _.map(
            res.data.filter((element) => element.status),
            "subject"
          );
          this.setState({ profSubjects });
        });
      }
      this.setState({ [name]: event.target.value });
    } else if (name == "admin_class_id") {
      this.setState({ [name]: event.target.value });
    }
  };

  handleChangeVirtualClassProfessor = (name) => (event) => {
    if (event.target.value === "0") {
      this.setState({
        professor_class_id: 0,
        selectedFilterDateProfessor: null,
      });
    } else {
      this.setState({ [name]: event.target.value });
    }
  };

  handleChangeDateProfessor = (date) => {
    if (date === null) {
      this.setState({
        selectedFilterDateProfessor: null,
