import React from 'react';
import { Modal, ModalBody, ModalHeader } from 'reactstrap';
import IntlMessages from '../../../../../util/IntlMessages';
import TextField from '@material-ui/core/TextField';
import Auxiliary from '../../../../../util/Auxiliary';
import MenuItem from '@material-ui/core/MenuItem';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';
import RemoveSharpIcon from '@material-ui/icons/RemoveSharp';
import GetAppIcon from '@material-ui/icons/GetApp';
import ImportExportIcon from '@material-ui/icons/ImportExport';
import { connect } from 'react-redux';
import { KeyboardDatePicker, MuiPickersUtilsProvider } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/moment';
import InputLabel from '@material-ui/core/InputLabel';
import Select from 'react-select';
import AddBox from '@material-ui/icons/AddBox';
import Typography from '@material-ui/core/Typography';
import WcIcon from '@material-ui/icons/Wc';
import { Radio } from '@material-ui/core';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
import PrintIcon from '@material-ui/icons/Print';
import PictureAsPdfIcon from '@material-ui/icons/PictureAsPdf';
import RemoveIcon from '@material-ui/icons/Remove';
import MuiPhoneNumber from 'material-ui-phone-number';
import { isEmail } from '../../../../../constants/validationFunctions';
import moment from 'moment';
import PhotoIcon from '@material-ui/icons/Photo';

import {
  roleIdSuperAdmin,
  roleIdAdmin,
  roleIdProfessor,
  roleIdStudent,
  roleIdDirector,
  roleIdParent,
  roleIdSupervisor,
} from '../../../../../config/config';
const schoolSessionList = [
  {
    value: 'Trimestre 1',
    id: 1,
  },
  {
    value: 'Trimestre 2',
    id: 2,
  },
  {
    value: 'Trimestre 3',
    id: 3,
  },
  {
    value: 'Semestre 1',
    id: 4,
  },
  {
    value: 'Semestre 2',
    id: 5,
  },
];
const fonctionList = [
  { label: "Agent d'entretien", id: 1, value: 1 },
  { label: 'Infirmière', id: 2, value: 2 },
  { label: 'Cuisinier(e)', id: 3, value: 3 },
  { label: 'Gardien', id: 4, value: 4 },
  { label: 'surveillant', id: 5, value: 5 },
  { label: 'surveillant général', id: 6, value: 6 },
];
class EditUsers extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { values, usefulData } = this.props;
    console.log(usefulData, 'usefulData');
    console.log(values.roleItemEdit.id === roleIdStudent, 'values.roleItemEdit.id === roleIdStudent');


    return (
      <Auxiliary>
        <Modal isOpen={this.props.values.openEdit}>
          <ModalHeader
            toggle={this.props.handleToggle}
            className="modal-box-header bg-primary text-white"
          >
            Modifier un utilisateur
          </ModalHeader>
          <ModalBody>
            <div className="col-lg-12 col-md-12 col-sm-12 d-flex flex-wrap align-items-start">
              <form
                className="d-flex  flex-wrap col-lg-12 col-md-12 col-sm-12 p-4"
                autoComplete="off"
                // onSubmit={this.props.handleSubmit}
              >
                {this.props.values.openEdit ? (
                  <>
                    <div className=" d-flex col-lg-12 col-md-12 col-sm-12 flex-row flex-wrap justify-content-around align-items-center">
                      <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                        <InputLabel
                          htmlFor="nomSelect"
                          style={{
                            fontFamily: 'Roboto',
                            fontSize: '18px',
                          }}
                          required
                        >
                          {<IntlMessages id="role.user" />}
                        </InputLabel>
                        <Select
                          required
                          isDisabled
                          options={usefulData.listRoles}
                          // onChange={this.props.handleChangeRole}
                          value={values.roleItemEdit}
                          id="role"
                          name="role"
                          styles={{
                            control: (base) => ({
                              ...base,
                              '&:hover': { borderColor: 'gray' }, // border style on hover
                              border: '1px solid lightgray', // default border color
                              boxShadow: 'none', // no box-shadow
                              borderTopStyle: 'none',
                              borderRightStyle: 'none',
                              borderLeftStyle: 'none',
                              borderRadius: ' none',
                            }),
                          }}
                        />{' '}
                      </div>
                      <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                        <InputLabel
                          htmlFor="nomSelect"
                          style={{
                            fontFamily: 'Roboto',
                            fontSize: '18px',
                          }}
                          required
                        >
                          {<IntlMessages id="school.year.user" />}
                        </InputLabel>
                        <Select
                          options={usefulData.listSchoolYears}
                          // onChange={this.props.handleChangeSchoolYear}
                          value={values.schoolyearEdit}
                          id="role"
                          name="role"
                          styles={{
                            control: (base) => ({
                              ...base,
                              '&:hover': { borderColor: 'gray' }, // border style on hover
                              border: '1px solid lightgray', // default border color
                              boxShadow: 'none', // no box-shadow
                              borderTopStyle: 'none',
                              borderRightStyle: 'none',
                              borderLeftStyle: 'none',
                              borderRadius: ' none',
                            }),
                          }}
                        />{' '}
                      </div>
                      {this.props.userProfile.role_id === roleIdSuperAdmin ? (
                        <>
                          <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                            <InputLabel
                              htmlFor="nomSelect"
                              style={{
                                fontFamily: 'Roboto',
                                fontSize: '18px',
                              }}
                              required
                            >
                              {<IntlMessages id="user.establishment" />}
                            </InputLabel>
                            <Select
                              options={usefulData.establishmentsList}
                              // onChange={this.props.handleChangeEstablishments}
                              values={values.establishmentEdit}
                              id="role"
                              name="role"
                              styles={{
                                control: (base) => ({
                                  ...base,
                                  '&:hover': { borderColor: 'gray' }, // border style on hover
                                  border: '1px solid lightgray', // default border color
                                  boxShadow: 'none', // no box-shadow
                                  borderTopStyle: 'none',
                                  borderRightStyle: 'none',
                                  borderLeftStyle: 'none',
                                  borderRadius: ' none',
                                }),
                              }}
                            />{' '}
                          </div>
                        </>
                      ) : (
                        ''
                      )}
                      {this.props.values.roleId === roleIdStudent ? (
                        <>
                          <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                            <InputLabel
                              htmlFor="nomSelect"
                              style={{
                                fontFamily: 'Roboto',
                                fontSize: '18px',
                              }}
                            >
                              {<IntlMessages id="parent.couplage.user" />}
                            </InputLabel>
                            <Select
                              // options={this.props.parentsList}
                              // onChange={this.props.handleChangeParent}
                              id="role"
                              name="role"
                              styles={{
                                control: (base) => ({
                                  ...base,
                                  '&:hover': { borderColor: 'gray' }, // border style on hover
                                  border: '1px solid lightgray', // default border color
                                  boxShadow: 'none', // no box-shadow
                                  borderTopStyle: 'none',
                                  borderRightStyle: 'none',
                                  borderLeftStyle: 'none',
                                  borderRadius: ' none',
                                }),
                              }}
                            />{' '}
                          </div>
                          <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                            <InputLabel
                              htmlFor="nomSelect"
                              style={{
                                fontFamily: 'Roboto',
                                fontSize: '18px',
                              }}
                              required
                            >
                              {<IntlMessages id="classe.couplage.user" />}
                            </InputLabel>
                            <Select
                              // options={this.props.values.classForStudent}
                              // onChange={this.props.handleChangeStudentClass}
                              id="nomSelect"
                              styles={{
                                control: (base) => ({
                                  ...base,
                                  '&:hover': { borderColor: 'gray' }, // border style on hover
                                  border: '1px solid lightgray', // default border color
                                  boxShadow: 'none', // no box-shadow
                                  borderTopStyle: 'none',
                                  borderRightStyle: 'none',
                                  borderLeftStyle: 'none',
                                  borderRadius: ' none',
                                }),
                              }}
                            />{' '}
                          </div>
                          <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                            <InputLabel
                              htmlFor="group"
                              style={{
                                fontFamily: 'Roboto',
                                fontSize: '18px',
                              }}
                              required
                            >
                              {<IntlMessages id="assignment.student.group" />}
                            </InputLabel>
                            <Select
                              // options={this.props.values.listGroupClass}
                              // onChange={this.props.handleChangeGroupClassRoom}
                              id="group"
                              styles={{
                                control: (base) => ({
                                  ...base,
                                  '&:hover': { borderColor: 'gray' }, // border style on hover
                                  border: '1px solid lightgray', // default border color
                                  boxShadow: 'none', // no box-shadow
                                  borderTopStyle: 'none',
                                  borderRightStyle: 'none',
                                  borderLeftStyle: 'none',
                                  borderRadius: ' none',
                                }),
                              }}
                            />{' '}
                          </div>
                        </>
                      ) : (
                        ''
                      )}
                      {this.props.values.roleId === roleIdProfessor ? (
                        <>
                          <div className="col-md-6 col-lg-3 col-sm-12 p-0 "> </div>
                          <div className="col-md-6 col-lg-3 col-sm-12 p-0 "> </div>
                        </>
                      ) : (
                        ''
                      )}
                      {this.props.values.roleId === roleIdProfessor ? (
                        <>
                          {values.listOfSubjects.map((objSubject, index) => (
                            <div className="col-md-6 col-lg-12 col-sm-12 d-flex flex-row justify-content-between p-3">
                              <div className="col-md-6 col-lg-3 col-sm-12 p-0 ">
                                <InputLabel
                                  htmlFor="nomSelect"
                                  style={{
                                    fontFamily: 'Roboto',
                                    fontSize: '18px',
                                  }}
                                  required
                                >
                                  {<IntlMessages id="prof.couplage.class" />}
                                </InputLabel>
                                <Select
                                  // options={this.props.values.classRoomList}
                                  // onChange={(e) =>
                                  //   this.props.handleChangeClassRoom(e, 'classId', index)
                                  // }
                                  id="classId"
                                  name="classId"
                                  styles={{
                                    control: (base) => ({
                                      ...base,
                                      '&:hover': { borderColor: 'gray' }, // border style on hover
                                      border: '1px solid lightgray', // default border color
                                      boxShadow: 'none', // no box-shadow
                                      borderTopStyle: 'none',
                                      borderRightStyle: 'none',
                                      borderLeftStyle: 'none',
                                      borderRadius: ' none',
                                    }),
                                  }}
                                />{' '}
                              </div>
                              <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                                <InputLabel
                                  htmlFor="nomSelect"
                                  style={{
                                    fontFamily: 'Roboto',
                                    fontSize: '18px',
                                  }}
                                  required
                                >
                                  {<IntlMessages id="prof.couplage.subjects" />}
                                </InputLabel>
                                <Select
                                  // options={objSubject.subjects}
                                  // onChange={(e) =>
                                  //   this.props.handleChangeClassRoom(e, 'subjectId', index)
                                  // }
                                  // value={this.props.values.subjectItem}
                                  id="subjectId"
                                  name="subjectId"
                                  styles={{
                                    control: (base) => ({
                                      ...base,
                                      '&:hover': { borderColor: 'gray' }, // border style on hover
                                      border: '1px solid lightgray', // default border color
                                      boxShadow: 'none', // no box-shadow
                                      borderTopStyle: 'none',
                                      borderRightStyle: 'none',
                                      borderLeftStyle: 'none',
                                      borderRadius: ' none',
                                    }),
                                  }}
                                />{' '}
                              </div>
                              <div className="col-md-6 col-lg-2 col-sm-12 p-0">
                                <Fab
                                  size="small"
                                  value={`${index}`}
                                  color="primary"
                                  aria-label="Add"
                                  onClick={() => {
                                    if (objSubject.isAdded) {
                                      this.props.deleteChoice(index);
                                    } else {
                                      this.props.addNewSubject(index + 1);
                                    }
                                  }}
                                >
                                  {objSubject.isAdded ? <RemoveIcon /> : <AddIcon />}
                                </Fab>
                              </div>
                              <div className="col-md-6 col-lg-3 col-sm-12 p-0"></div>
                            </div>
                          ))}
                        </>
                      ) : (
                        ''
                      )}
                      {this.props.values.roleId === roleIdParent ? (
                        <>
                          <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                            <InputLabel
                              htmlFor="nomSelect"
                              style={{
                                fontFamily: 'Roboto',
                                fontSize: '18px',
                              }}
                            >
                              {<IntlMessages id="parent.couplage.student" />}
                            </InputLabel>
                            <Select
                              // options={this.props.studentsList}
                              // onChange={this.props.handleChangeStudent}
                              id="studentIdAssignement"
                              name="studentIdAssignement"
                              styles={{
                                control: (base) => ({
                                  ...base,
                                  '&:hover': { borderColor: 'gray' }, // border style on hover
                                  border: '1px solid lightgray', // default border color
                                  boxShadow: 'none', // no box-shadow
                                  borderTopStyle: 'none',
                                  borderRightStyle: 'none',
                                  borderLeftStyle: 'none',
                                  borderRadius: ' none',
                                }),
                              }}
                            />{' '}
                          </div>
                        </>
                      ) : (
                        ''
                      )}
                      {this.props.values.roleId === roleIdSupervisor ? (
                        <div className="col-md-6 col-lg-3 col-sm-12 p-0">
                          <InputLabel
                            htmlFor="nomSelect"
                            style={{
                              fontFamily: 'Roboto',
                              fontSize: '18px',
                            }}
                          >
                            {<IntlMessages id="vie.scolaire.fonction" />}
                          </InputLabel>
                          <Select
                            // options={fonctionList}
                            // onChange={this.props.handleChangeFunctions}
                            id="function"
                            name="function"
                            styles={{
                              control: (base) => ({
                                ...base,
                                '&:hover': { borderColor: 'gray' }, // border style on hover
                                border: '1px solid lightgray', // default border color
                                boxShadow: 'none', // no box-shadow
                                borderTopStyle: 'none',
                                borderRightStyle: 'none',
                                borderLeftStyle: 'none',
                                borderRadius: ' none',
                              }),
                            }}
                          />{' '}
                        </div>
                      ) : (
                        ''
                      )}
                      <hr
                        style={{
                          width: '100%',
                          margin: 'auto',
                          marginTop: '40px',
                          marginBottom: '10px',
                          border: '1px dashed #979A9A',
                          paddingLeft: '-100%',
                        }}
                      />
                    </div>

                    <div className="d-flex col-lg-12 col-md-12 col-sm-12 flex-row flex-wrap justify-content-around align-items-center pt-3">
                      <div className="col-md-6 col-lg-2 col-sm-12 p-1">
                        <InputLabel
                          style={{
                            fontFamily: 'Roboto',
                            fontSize: '18px',
                            marginTop: '-2%',
                          }}
                          required
                        >
                          {<IntlMessages id="user.name" />}
                        </InputLabel>
                        <TextField
                          required
                          id="userName"
                          name="userName"
                          value={values.userNameEdit || ''}
                          // onChange={this.props.handleChange('userName')}
                          style={{
                            marginTop: '3%',
                          }}
                          fullWidth
                          SelectProps={{
                            native: true,
                          }}
                        />
                        {/* <FormHelperText
                    error={values.nameError}
                    >
                      {values.nameError ? 'Nom de support de cours déja existe' : ''}
