import React, { Component } from "react";
import TableCell from "@material-ui/core/TableCell";
import TableRow from "@material-ui/core/TableRow";
import IconButton from "@material-ui/core/IconButton";
import _ from "lodash";
import moment from "moment";
import Avatar from "@material-ui/core/Avatar";
import defaultAvatar from "../../../../../assets/images/default-Avatar.png";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import IntlMessages from "../../../../../util/IntlMessages";
import { connect } from "react-redux";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

class UsersListItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      uri: "",
      menuState: false,
      anchorEl: undefined,
    };
  }
  render() {
    const { user } = this.props;
     return (
      <>
        <TableRow key={user.id}>
          <TableCell align="left">
            {/* <Avatar
              align="left"
              className="size-60"
              alt="..."
              src={defaultAvatar}
            /> */}
            {user.urlPhoto ? (
            <Avatar
              align="left"
              className="size-90"
              alt="..."
              src={defaultAvatar}
            />
          ) : (
              <Avatar
                align="left"
                className="size-90"
                alt="..."
                src={defaultAvatar}
              />
            )}
          </TableCell>
          <TableCell align="left">{user.cin}</TableCell>
          <TableCell align="left">{user.name}</TableCell>
          <TableCell align="left">{user.surname}</TableCell>
          <TableCell align="left">{user.roleId}</TableCell>
          <TableCell align="left">{user.email}</TableCell>
          <TableCell align="left">{user.phone}</TableCell>

          <TableCell>
            <IconButton
              size="meduim"
              className="icon-btn"
              // onClick={(e) => this.props.handleDelete(cours)}
            >
              <Typography
                variant="h6"
                style={{
                  color: "grey",
                  fontWeight: "normal",
                  fontSize: 10,
                }}
              >
                <IntlMessages id="user.new.password" />
              </Typography>
              <i className="zmdi zmdi-replay" style={{ color: "text-grey" }} />
            </IconButton>
            &nbsp; | &nbsp;
            <IconButton
              size="medium"
              className="icon-btn"
              // onClick={(e) => this.props.handleDelete(cours)}
            >
              <i className="zmdi zmdi-eye" style={{ color: "text-grey" }} />
            </IconButton>
            &nbsp; | &nbsp;
            <Button
              style={{
                backgroundColor: "white",
                color: "#7C7C7C",
                width: "50px",
                height: "20px",
              }}
              onClick={(e) => {
                // this.props.editShowModal(cours);
              }}
              target="_blank"
            >
              <span style={{ fontSize: "12px", color: "#7C7C7C" }}>
                <IntlMessages id="button.modify" />
              </span>
            </Button>
            &nbsp; | &nbsp;
            <IconButton
              size="medium"
              className="icon-btn"
              // onClick={(e) => this.props.handleDelete(cours)}
            >
              <i className="zmdi zmdi-delete" style={{ color: "text-grey" }} />
            </IconButton>
          </TableCell>
        </TableRow>

        {/* <hr
          style={{
            height: "100%",
            width: "100%",
            margin: "auto",
            border: "1px dashed #979A9A",
          }}
        /> */}
      </>
    );
  }
}
function mapStateToProps(state) {
  return {
    settings: state.settings.locale,
  };
}

export default connect(mapStateToProps, {})(UsersListItem);
