import React from "react";
import IntlMessages from "../../../../util/IntlMessages";
import CardBox from "../../../../components/CardBox/index";
import Can from "../../../../can";
import { RoleContext } from "../../../../Context";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import TextField from "@material-ui/core/TextField";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import { connect } from "react-redux";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import MenuItem from "@material-ui/core/MenuItem";
import _ from "lodash";
import QuestionsTable from "./QuestionsTable";
import { addQuestion } from "../../../../actions/QuestionActions";
import AddQuestion from "./AddQuestion";

class Questions extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      addQuestion: false,
      alerte: false,
      choices: [{ id: 1, add: false, content: "" }],
      addChoice: false,
      choiceChecked: 1,
      level_id: "",
      filter_level_id: "",
      subject_id: "",
      filter_subject_id: "",
      topic: "",
      question_description: "",
      questionFiltred: [],
    };
    this.openAddModal = this.openAddModal.bind(this);
    this.openQuestionModal = this.openQuestionModal.bind(this);
    this.addChoice = this.addChoice.bind(this);
    this.deleteChoice = this.deleteChoice.bind(this);
    this.handleChangeChoice = this.handleChangeChoice.bind(this);
    this.handleChangeRadio = this.handleChangeRadio.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentWillMount() {
    this.setState({ questionFiltred: this.props.questions });
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevProps.questions !== this.props.questions) {
      this.setState({ questionFiltred: this.props.questions });
    }
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const question_desc = this.state.question_description;
    const question_subject = this.state.topic;
    const subject_id = this.state.subject_id;
    const level_id = this.state.level_id;
    const id_profile_creator = this.props.userProfile.id;
    const status = true;

    const question = {
      question_desc,
      question_subject,
      subject_id,
      level_id,
      id_profile_creator,
      status,
    };
    let choices = this.state.choices.map((element) => ({
      proposal: element.content,
      propasal_status: element.id == this.state.choiceChecked,
      status: true,
    }));

    this.props.addQuestion(question, choices);
    this.setState({
      choiceChecked: 1,
      level_id: "",
      subject_id: "",
      topic: "",
      question_description: "",
      choices: [{ id: 1, add: false, content: "" }],
    });
    this.openQuestionModal();
  };
  handleChange = (name) => (event) => {
    this.setState({ [name]: event.target.value });
    if (name == "filter_level_id") {
      let questionFiltred = this.props.questions;
      if (event.target.value != 0) {
        questionFiltred = questionFiltred.filter(
          (element) => element.level_id == event.target.value
        );
      }
      this.setState({ filter_subject_id: "", questionFiltred });
    } else if (name == "level_id") {
      this.setState({ subject_id: "" });
    } else if (name == "filter_subject_id") {
      let questionFiltred = this.props.questions;

      if (event.target.value != 0) {
        questionFiltred = questionFiltred.filter(
          (element) =>
            element.subject_id == event.target.value &&
            element.level_id == this.state.filter_level_id
        );
      }

      this.setState({ questionFiltred });
    }
  };
  openAddModal() {
    this.setState((previousState) => ({
      open: !previousState.open,
    }));
  }
  openQuestionModal() {
    this.setState((previousState) => ({
      addQuestion: !previousState.addQuestion,
    }));
  }
  handleChangeRadio = (name) => (event) => {
    this.setState({
      choiceChecked: parseInt(event.target.value, 10),
    });
  };
  handleChangeChoice = (idChoice) => (event) => {
    let choices = this.state.choices;
    let index = _.findIndex(choices, { id: idChoice });
    _.fill(
      choices,
      {
        id: choices[index].id,

        content: event.target.value,
        add: choices[index].add,
      },
      index,
      index + 1
    );
    this.setState({ choices });
  };
  deleteChoice = (idChoice) => {
    let choiceLength = this.state.choices.length;
    let i = 1;
    let choices = [];
    this.state.choices.map((el) => {
      if (el.id !== idChoice && i !== choiceLength - 1) {
        choices.push({ id: i, add: true, content: el.content });
        i++;
      }
    });
    choices.push({
      id: choiceLength - 1,
      add: false,
      content: this.state.choices[choiceLength - 1].content,
    });
    var choiceChecked = this.state.choiceChecked;
    if (idChoice == choiceChecked) {
      this.setState({ choiceChecked: 0 });
    } else if (idChoice < choiceChecked) {
      this.setState({ choiceChecked: choiceChecked - 1 });
    }
    this.setState({ choices });
  };
  addChoice = () => {
    let choices = [];
    this.state.choices.map((el) => {
      choices.push({ id: el.id, add: true, content: el.content });
    });
    choices.push({
      id: choices[choices.length - 1].id + 1,
      add: false,
      content: "",
    });

    this.setState({ choices });
  };

  render() {
    return (
      <div className="col-lg-12">
        <RoleContext.Consumer>
          {({ role }) => (
            <Can
              role={role}
              perform="add-test-level"
              yes={() => (
                <div>
                  <div className="d-flex justify-content-start align-items-center">
                    <h1>
                      <IntlMessages id="level.test.question" />
                    </h1>
                    &nbsp;&nbsp;&nbsp;
                    <Fab
                      size="small"
                      color="primary"
                      aria-label="Add"
                      onClick={this.openAddModal}
                    >
                      {this.state.open ? (
                        <ArrowUpwardIcon />
                      ) : (
                        <ArrowDownwardIcon />
                      )}
                    </Fab>
                  </div>
                  <br />
                  {this.state.open ? (
                    <>
                      {""}
                      <CardBox styleName="col-lg-12 text-primary">
                        <div className="row">
                          <div className="d-flex justify-content-start align-items-center col-md-12">
                            <h2>
                              <IntlMessages id="new.level.test.add.question" />
                            </h2>
                            &nbsp;&nbsp;&nbsp;
                            <Fab
                              size="small"
                              color="primary"
                              aria-label="Add"
                              onClick={this.openQuestionModal}
                            >
                              {this.state.addQuestion ? (
                                <RemoveSharpIcon />
                              ) : (
                                <AddIcon />
                              )}
                            </Fab>
                          </div>
                          {this.state.addQuestion ? (
                            <AddQuestion
                              openQuestionModal={this.openQuestionModal}
                              values={this.state}
                              handleSubmit={this.handleSubmit}
                              handleChangeRadio={this.handleChangeRadio}
                              handleChange={this.handleChange}
                              profSubjectList={this.props.profSubjectList}
                              profLevels={this.props.profLevels}
                              classLevels={this.props.classLevels}
                              level_id={this.props.level_id}
                              deleteChoice={this.deleteChoice}
                              addChoice={this.addChoice}
                              handleChangeChoice={this.handleChangeChoice}
                            />
                          ) : (
                            ""
                          )}
                          <div className="col-md-12  d-flex justify-content-center">
                            <div className="col-md-3 text-left ">
                              <TextField
                                required
                                select
                                name="entree"
                                id="entree"
                                label={
                                  <IntlMessages id="components.note.niveau" />
                                }
                                onChange={this.handleChange("filter_level_id")}
                                value={this.state.filter_level_id}
                                margin="normal"
                                fullWidth
                              >
                                <MenuItem key="0" value="0">
                                  <IntlMessages id="all.label" />
                                </MenuItem>
                                {this.props.classLevels.map((level) => {
                                  if (
                                    this.props.profLevels.indexOf(level.id) >= 0
                                  ) {
                                    return (
                                      <MenuItem key={level.id} value={level.id}>
                                        {level.name}
                                      </MenuItem>
                                    );
                                  }
                                })}
                              </TextField>
                            </div>

                            <div className="col-md-3 text-left ">
                              <TextField
                                required
                                select
                                name="suite"
                                id="filter_subject_id"
                                label={
                                  <IntlMessages id="components.note.subject" />
                                }
                                onChange={this.handleChange(
                                  "filter_subject_id"
                                )}
                                value={this.state.filter_subject_id}
                                margin="normal"
                                fullWidth
                              >
                                <MenuItem key="0" value="0">
                                  Toutes les Mati√©res
                                </MenuItem>
                                {this.props.profSubjectList.map((subject) => {
                                  if (
                                    subject.level_id ==
                                    this.state.filter_level_id
                                  ) {
                                    return (
                                      <MenuItem
                                        key={subject.id}
                                        value={subject.id}
                                      >
                                        {this.props.settings.languageId ==
                                        "tunisia"
                                          ? subject.name_AR
                                          : this.props.settings.languageId ==
                                            "french"
                                          ? subject.name_FR
                                          : subject.name_EN}
                                      </MenuItem>
                                    );
                                  }
                                })}
                              </TextField>
                            </div>
                          </div>

                          <div className="col-md-12 p-4">
                            <QuestionsTable
                              questions={this.state.questionFiltred}
                              levels={this.props.classLevels}
                              profSubjectList={this.props.profSubjectList}
                              profLevels={this.props.profLevels}
                              classLevels={this.props.classLevels}
                            ></QuestionsTable>
                          </div>
                        </div>
                      </CardBox>
                    </>
                  ) : (
                    ""
                  )}
                </div>
              )}
            />
          )}
        </RoleContext.Consumer>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
    settings: state.settings.locale,
    classLevels: state.ClassLevels.remoteLevels,
  };
};

export default connect(
  mapStateToProps,
  {
    addQuestion,
  }
)(Questions);
