                            <div className="p-2" style={{ fontFamily: 'Roboto', fontSize: '17px' }}>
                              <IntlMessages id="icon.archives" />
                            </div>
                          </div>
                        </CardBox>

                        {this.state.isOpenAddModal && (
                          <CardBox styleName="col-lg-12">
                            <AddHomework
                              values={this.state}
