                              onChange={this.handleChangeFilterStatus('statusHomework')}
                              SelectProps={{}}
                              label={<IntlMessages id={`components.todo.etat`} />}
                              InputProps={{ disableUnderline: true }}
                              margin="normal"
                              fullWidth
                            >
                              <MenuItem key={0} value="all">
