import React from "react";
import Auxiliary from "../../../../util/Auxiliary";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import { connect } from "react-redux";
import IntlMessages from "../../../../util/IntlMessages";
import CardBox from "../../../../components/CardBox/index";
import moment from "moment";
import Button from "@material-ui/core/Button";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";

class LevelTestShow extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {

    let test = this.props.test;
    let listQuestion = this.props.listQuestion;

    return (
      <Auxiliary>
        <Modal isOpen={this.props.openTestShow}>
          <ModalHeader
            toggle={this.props.closeTestShowModal}
            className="modal-box-header bg-primary text-white"
          >
            {test.test_name}
          </ModalHeader>
          <br />
          <ModalBody>
            <div className="row">
              <CardBox styleName="col-lg-12 text-primary">
                <div className="d-flex flex-column justify-content-start align-items-center">
                  <h2>
                    <label>
                      <IntlMessages id="components.note.subject" /> :{" "}
                      {test.subjects.name_FR}
                    </label>
                  </h2>
    <h2><IntlMessages id="level.test.date" />:{" "}
                    {moment(test.limit_date).format("DD/MM/YYYY")}</h2>
                  <h2>
                    <IntlMessages id="level.test.duration" /> :
                    {test.test_duration} <IntlMessages id="level.test.minutes" />
                  </h2>
                </div>
              </CardBox>
              <CardBox styleName="col-lg-12 text-primary">
                <>
                  {test.test_desc}
                  <br />
                  {listQuestion.map((element, index) => {
                    return (
                      <div
                        key={index}
                        style={({ color: "black" }, { paddingTop: "5%" })}
                      >
                        Question {index + 1} :
                        <div style={({ color: "black" }, { padding: "1em" })}>
                          {element.test_questions.question_desc}
                        </div>
                        {element.test_questions.question_proposals.map(
                          (choiceItem, i) => {
                            let checkedChoice = element.test_questions.question_proposals.filter(
                              (item) => item.propasal_status
                            )[0].id;

                            return (
                              <div key={i}>
                                <div
                                  style={
                                    ({ color: "black" }, { paddingLeft: "5%" })
                                  }
                                >
                                  <div className="d-flex flex-row  col-md-12">
                                    <RadioGroup
                                      aria-label="anchorOriginVertical"
                                      name="anchorOriginVertical"
                                      value={checkedChoice}
                                    >
                                      <FormControlLabel
                                        value={choiceItem.id}
                                        control={<Radio required />}
                                        style={{
                                          marginTop: "4%",
                                        }}
                                      />
                                    </RadioGroup>
                                    <div
                                      className="col-md-9"
                                      style={
                                        ({ color: "black" },
                                        { paddingTop: "2.2%" })
                                      }
                                    >
                                      {choiceItem.proposal}
                                    </div>
                                  </div>
                                </div>
                              </div>
                            );
                          }
                        )}
                      </div>
                    );
                  })}
                </>
              </CardBox>
            </div>
          </ModalBody>
        </Modal>
      </Auxiliary>
    );
  }
}

function mapStateToProps(state) {
  return {
    userProfile: state.auth.userProfile,
    settings: state.settings.locale,
  };
}

export default connect(
  mapStateToProps,
  {}
)(LevelTestShow);
