import React from "react";
import CardBox from "../../../../../components/CardBox/index";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "react-select";
import IntlMessages from "../../../../../util/IntlMessages";
import AddBox from "@material-ui/icons/AddBox";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider,
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/moment";
import { TimePicker } from "@material-ui/pickers";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Button from "@material-ui/core/Button";

class HomeworkList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div className="row ">
        <div className="d-flex flex-row justify-content-start align-items-center p-4 col-lg-12 col-md-12 col-sm-12">
          <div className="p-2  col-lg-4">
            <InputLabel
              htmlFor="nomSelect"
              style={{
                paddingLeft: "10px",
                fontFamily: "Roboto",
                fontSize: "13px",
              }}
            >
              {/* {<IntlMessages id="material.course.join.moocs" />} */}
              Nom du fichier*
            </InputLabel>
            <Select
              defaultValue={[]}
              isMulti
              name="nameMoocs"
              //   options={values.optionsListMoocs}
              onChange={this.props.handleChangeMoocsIds}
              className="basic-multi-select"
              classNamePrefix="select"
              styles={{
                control: (base) => ({
                  ...base,
                  "&:hover": { borderColor: "gray" }, // border style on hover
                  border: "1px solid lightgray", // default border color
                  boxShadow: "none", // no box-shadow
                  borderTopStyle: "none",
                  borderRightStyle: "none",
                  borderLeftStyle: "none",
                  borderRadius: " none",
                }),
              }}
            />{" "}
          </div>
          <div className="pl-4 p-2 col-lg-4">
            <InputLabel
              htmlFor="nomSelect"
              style={{
                paddingLeft: "10px",
                fontFamily: "Roboto",
                fontSize: "13px",
              }}
            >
              {/* {<IntlMessages id="material.course.join.virtual.class" />} */}
              Nom de la matière
            </InputLabel>
            <Select
              defaultValue={[]}
              isMulti
              name="nameMoocs"
              //   options={values.optionsListVirtualClasses}
              className="basic-multi-select"
              classNamePrefix="select"
              onChange={this.props.handleChangeVirtualClassIds}
              styles={{
                control: (base) => ({
                  ...base,
                  "&:hover": { borderColor: "gray" }, // border style on hover
                  border: "1px solid lightgray", // default border color
                  boxShadow: "none", // no box-shadow
                  borderTopStyle: "none",
                  borderRightStyle: "none",
                  borderLeftStyle: "none",
                  borderRadius: " none",
                }),
              }}
            />{" "}
          </div>
        </div>

        <div className="d-flex flex-row flex-wrap justify-content-start align-items-start p-4 col-lg-12 col-md-12 col-sm-12">
          <div className=" p-2 col-lg-4">
            <div className="d-flex flex-column">
              <div>
                <RadioGroup
                  row
                  aria-label="eventChecked"
                  name="eventChecked"
                  // value={this.props.eventChecked}
                  // onChange={this.props.handleChangeEvent('eventChecked')}
                >
                  <FormControlLabel
                    value="thisEvent"
                    defaultChecked
                    control={<Radio color="primary" />}
                    label="Tous les classes"
                  />
                  <FormControlLabel
                    value="thisEventAndAllSubsequent"
                    control={<Radio color="primary" />}
                    label="Par classe"
                  />
                </RadioGroup>
              </div>
              <div style={{ marginLeft: "50%" }}>
                <Select
                  defaultValue={[]}
                  isMulti
                  name="nameMoocs"
                  //   options={values.optionsListMoocs}
                  onChange={this.props.handleChangeMoocsIds}
                  className="basic-multi-select"
                  classNamePrefix="select"
                  styles={{
                    control: (base) => ({
                      ...base,
                      "&:hover": { borderColor: "gray" }, // border style on hover
                      border: "1px solid lightgray", // default border color
                      boxShadow: "none", // no box-shadow
                      borderTopStyle: "none",
