import { callService } from '../_services/call.service';
import _ from 'lodash';
import { GET_PERMISSION_SETTING } from '../constants/ActionTypes';
var listPermissions = [
  {
    id: 1,
    Action_Fr: 'Ajouter un admin',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 2,
    role: [1, 2, 3, 5],
  },
  {
    id: 2,
    Action_Fr: 'Modifier un admin ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 2,
    role: [1, 3, 5],
  },
  {
    id: 3,
    Action_Fr: 'Supprimer un admin  ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 2,
    role: [1, 3, 5, 6],
  },
  {
    id: 4,
    Action_Fr: 'Consulter des admin',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 2,
    role: [1, 2, 3, 7],
  },
  {
    id: 5,
    Action_Fr: 'Ajouter un membre de direction ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 2,
    role: [1, 2, 3, 4],
  },
  {
    id: 6,
    Action_Fr: 'Ajouter un devoir de maison  ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 9,
    role: [1, 2, 3, 5, 6],
  },
  {
    id: 7,
    Action_Fr: 'Modifier un devoir de maison ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 9,
    role: [1, 2, 3, 5],
  },
  {
    id: 8,
    Action_Fr: 'Supprimer un devoir de maison ',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 9,
    role: [1, 2, 3, 5],
  },
  {
    id: 9,
    Action_Fr: 'Consulter les devoirs de maisons',
    Action_Ag: 'Add admin',
    Action_Ar: 'أضف المسؤول',
    moduleId: 9,
    role: [1, 2, 3, 5],
  },
];
export const getPermissionSetting = (estabId) => {
  let id = parseInt(estabId, 10);
  return (dispatch) => {
    let apiEndpoint = `/establishments/${id}/permission?access_token=${localStorage.token}`;

    callService.get(apiEndpoint).then((response) => {
      if (response) {
        // let listPermissionsuniq = _.uniqBy(response.data, 'fk_id_permission');
        dispatch({ type: GET_PERMISSION_SETTING, payload: response.data.permissionList });

        console.log(response.data.permissionList, 'response response response');
      }
    });
  };
};
export const applyPermissionSetting = (data) => {
  let permissionList = { permissionRoleEtab: data };
  return (dispatch) => {
    let apiEndpoint = `/permissions/attributePermission?access_token=${localStorage.token}`;
    console.log(data, 'data aplyyyyyyy');

    callService.put(apiEndpoint, data).then((response) => {
      if (response) {
        // let listPermissionsuniq = _.uniqBy(response.data, 'fk_id_permission');
        dispatch({ type: GET_PERMISSION_SETTING, payload: response.data.permissionList });

        console.log(response.data, 'response applyPermissionSetting response');
      }
    });
  };
};
