export class PermissionSetting extends Component {
  constructor(props) {
    super(props);
    this.state = {
      moduleId: '',
      subModuleList:[]
    };
    this.handleChange = this.handleChange.bind(this);
  }
  componentDidMount() {
    this.props.dispatch(getPermissionSetting(4));
  }
  handleChange = (name) => (event) => {
